(49, 8, 15, 'Pari, dispari o nullo', '1629643001_1629488397_img_c.webp', '&lt;p&gt;&lt;strong&gt;PROBLEMA&lt;/strong&gt;&lt;/p&gt;\r\n\r\n&lt;p&gt;Scrivere un programma che legge 10 numeri da tastiera e dice quanti sono positivi, quanti negativi e quanti nulli&lt;/p&gt;\r\n\r\n&lt;hr /&gt;\r\n&lt;p&gt;&lt;strong&gt;CODICE SORGENTE&lt;/strong&gt;&lt;/p&gt;\r\n\r\n&lt;p&gt;&lt;code&gt;#include &amp;lt;stdio.h&amp;gt;&lt;br /&gt;\r\n#include &amp;lt;stdlib.h&amp;gt;&amp;nbsp;&lt;br /&gt;\r\n#define NUMERI_TOTALI 10&lt;/code&gt;&lt;/p&gt;\r\n\r\n&lt;p&gt;&lt;br /&gt;\r\n&lt;code&gt;int main(int argc, char *argv[]) { //corpo del programma&lt;br /&gt;\r\n&amp;nbsp;&lt;br /&gt;\r\n&amp;nbsp;int i=0, positivi=0, nulli=0, negativi=0; //dichiarazione delle variabili&lt;br /&gt;\r\n&amp;nbsp;float numero; //dechiarazione del numero da inserire&lt;br /&gt;\r\n&amp;nbsp;&lt;br /&gt;\r\n&amp;nbsp;for (i=1; i&amp;lt;=NUMERI_TOTALI; i++) { //for per far ripetere l&amp;#39;immissione per 10 volte (define)&lt;br /&gt;\r\n&amp;nbsp; printf(&amp;quot;Dammi il numero: &amp;quot;);&lt;br /&gt;\r\n&amp;nbsp; scanf(&amp;quot;%f&amp;quot;, &amp;amp;numero); //immissione numero&lt;br /&gt;\r\n&amp;nbsp; if (numero&amp;gt;0) //se &amp;egrave; maggiore di 0&lt;br /&gt;\r\n&amp;nbsp; {&lt;br /&gt;\r\n&amp;nbsp; &amp;nbsp;positivi++; //incremento +1 positivi&lt;br /&gt;\r\n&amp;nbsp; }&lt;br /&gt;\r\n&amp;nbsp; else if (numero==0) //se &amp;egrave; uguale a 0&lt;br /&gt;\r\n&amp;nbsp; {&lt;br /&gt;\r\n&amp;nbsp; &amp;nbsp;nulli++; //incremento +1 nulli&amp;nbsp;&lt;br /&gt;\r\n&amp;nbsp; }&lt;br /&gt;\r\n&amp;nbsp; else //se &amp;egrave; minore di 0&lt;br /&gt;\r\n&amp;nbsp; {&lt;br /&gt;\r\n&amp;nbsp; &amp;nbsp;negativi++; //incremento +1 negativi&lt;br /&gt;\r\n&amp;nbsp; }&lt;br /&gt;\r\n&amp;nbsp;}&lt;br /&gt;\r\n&amp;nbsp;printf(&amp;quot;positivi %i\\n&amp;quot;, positivi); //numero dei positivi a video&lt;br /&gt;\r\n&amp;nbsp;printf(&amp;quot;nulli %i\\n&amp;quot;, nulli); //numeor dei positivi a video&lt;br /&gt;\r\n&amp;nbsp;printf(&amp;quot;negativi %i\\n&amp;quot;, negativi); //numero dei negativi a video&lt;br /&gt;\r\n&amp;nbsp;&lt;br /&gt;\r\n&amp;nbsp;system(&amp;quot;pause&amp;quot;);&lt;br /&gt;\r\n&amp;nbsp;return 0;&lt;br /&gt;\r\n}&lt;/code&gt;&lt;/p&gt;\r\n', 1, '2021-08-22 16:36:41'),
(50, 8, 15, 'Da 1 a X', '1629644534_16294883297_img_c.webp', '&lt;p&gt;&lt;strong&gt;PROBLEMA&lt;/strong&gt;&lt;/p&gt;\r\n\r\n&lt;p&gt;Scrivere I numeri naturali compresi tra 1 ed un numero ricevuto da tastiera.&lt;/p&gt;\r\n\r\n&lt;hr /&gt;\r\n&lt;p&gt;&lt;strong&gt;CODICE SORGENTE&lt;/strong&gt;&lt;/p&gt;\r\n\r\n&lt;p&gt;&lt;code&gt;#include &amp;lt;stdio.h&amp;gt;&lt;br /&gt;\r\n#include &amp;lt;stdlib.h&amp;gt;&lt;br /&gt;\r\nint main(int argc, char *argv[]) { //corpo del programma&lt;br /&gt;\r\n&amp;nbsp;int x=0, numero=1, i=0; //dichiarazione variabili&lt;br /&gt;\r\n&amp;nbsp;printf(&amp;quot;Dammi il numero: &amp;quot;);&lt;br /&gt;\r\n&amp;nbsp;do {&lt;br /&gt;\r\n&amp;nbsp; scanf(&amp;quot;%i&amp;quot;, &amp;amp;x); //immissione valore massimo a cui arrivare&lt;br /&gt;\r\n&amp;nbsp;}while(x&amp;lt;0);&lt;br /&gt;\r\n&amp;nbsp;system(&amp;quot;cls&amp;quot;); //pulire l&amp;#39;input&lt;br /&gt;\r\n&amp;nbsp;for (i=1; i&amp;lt;=x; i++) { //ciclio for per stampare tutti i numeri fino alla X cio&amp;egrave; il valore inserito&lt;br /&gt;\r\n&amp;nbsp; printf(&amp;quot;%i\\n&amp;quot;, numero++); //stampa a video partendo da 1 per poi aggiungere 1 alla variabi&amp;ograve;e&amp;nbsp;&lt;br /&gt;\r\n&amp;nbsp;}&lt;br /&gt;\r\n&amp;nbsp;system(&amp;quot;pause&amp;quot;);&lt;br /&gt;\r\n&amp;nbsp;return 0;&lt;br /&gt;\r\n}&lt;/code&gt;&lt;/p&gt;\r\n', 1, '2021-08-22 17:02:14'),
(51, 8, 15, 'Numeri maggiori di X', '1629644784_1629488397_img_c.webp', '&lt;p&gt;&lt;strong&gt;PROBLEMA&lt;/strong&gt;&lt;/p&gt;\r\n\r\n&lt;p&gt;Inserire due numeri interi da tastiera: n, val. Il programma deve chiedere all&amp;rsquo;utente di inserire n valori interi e verificare quanti di questi sono maggiori di val e quanti sono minori di val.&lt;/p&gt;\r\n\r\n&lt;hr /&gt;\r\n&lt;p&gt;&lt;strong&gt;CODICE SORGENTE&lt;/strong&gt;&lt;/p&gt;\r\n\r\n&lt;p&gt;&lt;code&gt;#include &amp;lt;stdio.h&amp;gt; //libreria&lt;br /&gt;\r\n#include &amp;lt;stdlib.h&amp;gt; //libreia&lt;/code&gt;&lt;/p&gt;\r\n\r\n&lt;p&gt;&lt;code&gt;int main(int argc, char *argv[]) { //corpo programma&lt;br /&gt;\r\n&amp;nbsp;&lt;br /&gt;\r\n&amp;nbsp;int i=0, n=0, val=0, n_val=0, minori=0, maggiore=0; //dichiarazione variabili&lt;/code&gt;&lt;/p&gt;\r\n\r\n&lt;p&gt;&lt;code&gt;&amp;nbsp;do { //inizio ciclo per n numero di volte&lt;br /&gt;\r\n&amp;nbsp; system(&amp;quot;cls&amp;quot;); //pulizia shell&lt;br /&gt;\r\n&amp;nbsp; if (i==0)&lt;br /&gt;\r\n&amp;nbsp; {&lt;br /&gt;\r\n&amp;nbsp; &amp;nbsp;printf(&amp;quot;inserire val: &amp;quot;); //testo a video&lt;br /&gt;\r\n&amp;nbsp; &amp;nbsp;scanf(&amp;quot;%i&amp;quot;, &amp;amp;val); //input valore&lt;br /&gt;\r\n&amp;nbsp; &amp;nbsp;printf(&amp;quot;inserire n: &amp;quot;); //testo a video&lt;br /&gt;\r\n&amp;nbsp; &amp;nbsp;scanf(&amp;quot;%i&amp;quot;, &amp;amp;n); //input valore&lt;br /&gt;\r\n&amp;nbsp; }&lt;br /&gt;\r\n&amp;nbsp; system(&amp;quot;cls&amp;quot;); //pulizia shell&lt;br /&gt;\r\n&amp;nbsp; printf(&amp;quot;Inserisci valore: &amp;quot;); // testo a video&lt;br /&gt;\r\n&amp;nbsp; scanf(&amp;quot;%i&amp;quot;, &amp;amp;n_val); //input valore&lt;br /&gt;\r\n&amp;nbsp; if (n_val&amp;gt;val) //se &amp;egrave; maggiore&lt;br /&gt;\r\n&amp;nbsp; {&lt;br /&gt;\r\n&amp;nbsp; &amp;nbsp;maggiore++; //incremente di 1&lt;br /&gt;\r\n&amp;nbsp; }&lt;br /&gt;\r\n&amp;nbsp; else if (n_val&amp;lt;val) //se &amp;egrave; minore&lt;br /&gt;\r\n&amp;nbsp; {&lt;br /&gt;\r\n&amp;nbsp; &amp;nbsp;minori++; // incremento di 1&lt;br /&gt;\r\n&amp;nbsp; }&lt;br /&gt;\r\n&amp;nbsp; else //altro&lt;br /&gt;\r\n&amp;nbsp; {&amp;nbsp;&lt;br /&gt;\r\n&amp;nbsp; }&lt;br /&gt;\r\n&amp;nbsp; i++; //incremento conteggio&lt;br /&gt;\r\n&amp;nbsp;}while(n&amp;gt;i); //fine ciclio per n numero di volte&lt;br /&gt;\r\n&amp;nbsp;system(&amp;quot;cls&amp;quot;); //pulizia shell&amp;nbsp;&lt;br /&gt;\r\n&amp;nbsp;printf(&amp;quot;\\nmaggiori di val: %i&amp;quot;, maggiore); //mostra a video variabile&lt;br /&gt;\r\n&amp;nbsp;printf(&amp;quot;\\nminori di val: %i\\n&amp;quot;, minori); //mostra a video variabile&lt;br /&gt;\r\n&amp;nbsp;system(&amp;quot;pause&amp;quot;);&lt;br /&gt;\r\n&amp;nbsp;return 0;&lt;br /&gt;\r\n}&lt;/code&gt;&lt;/p&gt;\r\n', 1, '2021-08-22 17:06:24'),
(52, 8, 15, 'Numeri compresi pari', '1629645160_16294883297_img_c.webp', '&lt;p&gt;&lt;strong&gt;PROBLEMA&lt;/strong&gt;&lt;/p&gt;\r\n\r\n&lt;p&gt;Scrivere un programma che stampi a video tutti i numeri pari compresi tra due estremi a e b letti da tastiera. Il programma deve dire anche quanti sono.&lt;/p&gt;\r\n\r\n&lt;hr /&gt;\r\n&lt;p&gt;&lt;strong&gt;CODICE SORGENTE&lt;/strong&gt;&lt;/p&gt;\r\n\r\n&lt;p&gt;&lt;code&gt;#include &amp;lt;stdio.h&amp;gt; //libreria&lt;br /&gt;\r\n#include &amp;lt;stdlib.h&amp;gt; //libreria&lt;/code&gt;&lt;/p&gt;\r\n\r\n&lt;p&gt;&lt;code&gt;int main(int argc, char *argv[]) { //corpo del programma&lt;br /&gt;\r\n&amp;nbsp;int n_inizio=0, n_fine=0, i=0, conta=0; //dichiarazione variabili&lt;br /&gt;\r\n&amp;nbsp;printf(&amp;quot;Inserire primo valore:&amp;quot;); //testo a video&lt;br /&gt;\r\n&amp;nbsp;scanf(&amp;quot;%i&amp;quot;, &amp;amp;n_inizio); //input valore&lt;br /&gt;\r\n&amp;nbsp;printf(&amp;quot;Inserire secondo valore:&amp;quot;); //testo a video&lt;br /&gt;\r\n&amp;nbsp;scanf(&amp;quot;%i&amp;quot;, &amp;amp;n_fine); //input valore&lt;br /&gt;\r\n&amp;nbsp;system(&amp;quot;cls&amp;quot;); //pulizia shell&lt;br /&gt;\r\n&amp;nbsp;do { //inizio del ciclo&amp;nbsp;&lt;br /&gt;\r\n&amp;nbsp; n_inizio++; //incremento variabile +1&lt;br /&gt;\r\n&amp;nbsp; if (n_inizio%2==0) //se &amp;egrave; pari&lt;br /&gt;\r\n&amp;nbsp; {&lt;br /&gt;\r\n&amp;nbsp; &amp;nbsp;if (n_inizio!=n_fine) //per non comprendere l&amp;#39;ultimo&lt;br /&gt;\r\n&amp;nbsp; &amp;nbsp;{&lt;br /&gt;\r\n&amp;nbsp; &amp;nbsp; printf(&amp;quot;\\n%i&amp;quot;, n_inizio); //testo a video + variabile&lt;br /&gt;\r\n&amp;nbsp; &amp;nbsp; conta++; //conteggio per quanti numeri sono&lt;br /&gt;\r\n&amp;nbsp; &amp;nbsp;}&lt;br /&gt;\r\n&amp;nbsp; }&lt;br /&gt;\r\n&amp;nbsp;}while(n_inizio!=n_fine); //fino a che non raggiunge la variabile fine&lt;br /&gt;\r\n&amp;nbsp;printf(&amp;quot;\\nI numeri pari sono: %i\\n&amp;quot;, conta); //testo a video&lt;br /&gt;\r\n&amp;nbsp;system(&amp;quot;pause&amp;quot;); //pause per vedere l&amp;#39;output&lt;br /&gt;\r\n&amp;nbsp;return 0; //tutto ok&lt;br /&gt;\r\n}&lt;/code&gt;&lt;/p&gt;\r\n', 1, '2021-08-22 17:12:40'),
(53, 8, 15, 'Somma di un range di numeri', '1629645308_1629488397_img_c.webp', '&lt;p&gt;&lt;strong&gt;PROBLEMA&lt;/strong&gt;&lt;/p&gt;\r\n\r\n&lt;p&gt;Calcolare la somma di tutti i numeri interi compresi fra due valori positivi dati in input a e b.&lt;/p&gt;\r\n\r\n&lt;hr /&gt;\r\n&lt;p&gt;&lt;strong&gt;CODICE SORGENTE&lt;/strong&gt;&lt;/p&gt;\r\n\r\n&lt;p&gt;&lt;code&gt;#include &amp;lt;stdio.h&amp;gt; //libreria&lt;br /&gt;\r\n#include &amp;lt;stdlib.h&amp;gt; //libreria&lt;/code&gt;&lt;/p&gt;\r\n\r\n&lt;p&gt;&lt;code&gt;int main(int argc, char *argv[]) {&lt;br /&gt;\r\n&amp;nbsp;&lt;br /&gt;\r\n&amp;nbsp;int a=0, b=0, i=0;&lt;br /&gt;\r\n&amp;nbsp;&lt;br /&gt;\r\n&amp;nbsp;scanf(&amp;quot;%i&amp;quot;, &amp;amp;a);&lt;br /&gt;\r\n&amp;nbsp;scanf(&amp;quot;%i&amp;quot;, &amp;amp;b);&lt;br /&gt;\r\n&amp;nbsp;do {&lt;br /&gt;\r\n&amp;nbsp; if (i==0)&lt;br /&gt;\r\n&amp;nbsp; {&lt;br /&gt;\r\n&amp;nbsp; &amp;nbsp;i+=a;&lt;br /&gt;\r\n&amp;nbsp; }&lt;br /&gt;\r\n&amp;nbsp; a++;&lt;br /&gt;\r\n&amp;nbsp; i+=a;&lt;br /&gt;\r\n&amp;nbsp;}while(a!=b);&lt;br /&gt;\r\n&amp;nbsp;printf(&amp;quot;%i&amp;quot;, i);&lt;br /&gt;\r\n&amp;nbsp;return 0;&lt;br /&gt;\r\n}&lt;/code&gt;&lt;/p&gt;\r\n', 1, '2021-08-22 17:15:08'),
(54, 8, 15, 'Una classe di 10 alunni e i voti', '1629645559_16294883297_img_c.webp', '&lt;p&gt;&lt;strong&gt;PROBLEMA&lt;/strong&gt;&lt;/p&gt;\r\n\r\n&lt;p&gt;Scrivere un programma che per una classe con 10 alunni per ogni alunno:&lt;/p&gt;\r\n\r\n&lt;ol&gt;\r\n	&lt;li&gt;chiede quanti voti ha;&lt;/li&gt;\r\n	&lt;li&gt;l&amp;igrave; fa inserire;&lt;/li&gt;\r\n	&lt;li&gt;ne calcola per ogni alunno la media;&lt;/li&gt;\r\n	&lt;li&gt;dice quante insufficienze ha quell&amp;rsquo;alunno.&lt;/li&gt;\r\n&lt;/ol&gt;\r\n\r\n&lt;p&gt;Dopo che sono stati inseriti i voti per tutti gli alunni, il programma dice quanti voti insufficienti in tutto ci sono stati e quanti alunni hanno una media inferiore al 6.&lt;/p&gt;\r\n\r\n&lt;hr /&gt;\r\n&lt;p&gt;&lt;strong&gt;CODICE SORGENTE&lt;/strong&gt;&lt;/p&gt;\r\n\r\n&lt;p&gt;&lt;code&gt;#include &amp;lt;stdio.h&amp;gt; //library&lt;br /&gt;\r\n#include &amp;lt;stdlib.h&amp;gt; //library&lt;br /&gt;\r\n#define NUMERO_ALUNNI 10 //constante&lt;/code&gt;&lt;/p&gt;\r\n\r\n&lt;p&gt;&lt;code&gt;int main(int argc, char *argv[]) { //corpo del programma&lt;br /&gt;\r\n&amp;nbsp;int a=0, b=0, numero_di_voti_da_inserire=0, insufficienze_alunno=0, alunni_con_media_insufficiente=0, insufficienze_totale=0; //dichiarazione variabili&lt;br /&gt;\r\n&amp;nbsp;float voto=0, media=0; //dichiarazione variabile&lt;br /&gt;\r\n&amp;nbsp;for(a=0; a&amp;lt;NUMERO_ALUNNI; a++) { //ciclo per ripeterlo 10 volte&lt;br /&gt;\r\n&amp;nbsp; do { //controllo input&lt;br /&gt;\r\n&amp;nbsp; &amp;nbsp;printf(&amp;quot;\\nInserire numero di valutazioni:&amp;quot;); //testo a video&lt;br /&gt;\r\n&amp;nbsp; &amp;nbsp;scanf(&amp;quot;%i&amp;quot;, &amp;amp;numero_di_voti_da_inserire); //input&lt;br /&gt;\r\n&amp;nbsp; }while(numero_di_voti_da_inserire&amp;lt;0); //fine controllo input rig.17&lt;br /&gt;\r\n&amp;nbsp; media=0; //assegnazione&lt;br /&gt;\r\n&amp;nbsp; for(b=0; b&amp;lt;numero_di_voti_da_inserire; b++) { //ripetere per il numero insertito&lt;br /&gt;\r\n&amp;nbsp; &amp;nbsp;do { //controllo input&lt;br /&gt;\r\n&amp;nbsp; &amp;nbsp; printf(&amp;quot;Inserire valutazione n.%i: &amp;quot;, ++b); //messaggio a video&lt;br /&gt;\r\n&amp;nbsp; &amp;nbsp; b--; //decremento&lt;br /&gt;\r\n&amp;nbsp; &amp;nbsp; scanf(&amp;quot;%f&amp;quot;, &amp;amp;voto); //input&lt;br /&gt;\r\n&amp;nbsp; &amp;nbsp;}while(voto&amp;lt;0); //fine controllo input rig.23&lt;br /&gt;\r\n&amp;nbsp; &amp;nbsp;media+=voto; //media=media+voto&lt;br /&gt;\r\n&amp;nbsp; &amp;nbsp;if (voto&amp;lt;6) //condizione&lt;br /&gt;\r\n&amp;nbsp; &amp;nbsp;{&lt;br /&gt;\r\n&amp;nbsp; &amp;nbsp; insufficienze_alunno++; //incremento&lt;br /&gt;\r\n&amp;nbsp; &amp;nbsp;}&lt;br /&gt;\r\n&amp;nbsp; } //fine ciclo rig.22&lt;br /&gt;\r\n&amp;nbsp; media=media/numero_di_voti_da_inserire; //calcolo media&lt;br /&gt;\r\n&amp;nbsp; printf(&amp;quot;Insufficienze alunno: %i&amp;quot;, insufficienze_alunno); //messaggio a video&lt;br /&gt;\r\n&amp;nbsp; printf(&amp;quot;\\nMedia alunno: %.2f\\n&amp;quot;, media); //messaggio a video&lt;br /&gt;\r\n&amp;nbsp; if (media&amp;lt;6) //condizione&lt;br /&gt;\r\n&amp;nbsp; {&lt;br /&gt;\r\n&amp;nbsp; &amp;nbsp;alunni_con_media_insufficiente++; //incremento&lt;br /&gt;\r\n&amp;nbsp; }&lt;br /&gt;\r\n&amp;nbsp; insufficienze_totale=+insufficienze_alunno; //insufficienze_tatole=insufficienze_totale+insufficienze_alunno&lt;br /&gt;\r\n&amp;nbsp;}&lt;br /&gt;\r\n&amp;nbsp;printf(&amp;quot;\\nVoti insufficienti totale: %i&amp;quot;, insufficienze_totale); //messaggio a video&lt;br /&gt;\r\n&amp;nbsp;printf(&amp;quot;\\nAlunni con media inferiore al 6: %i\\n&amp;quot;, alunni_con_media_insufficiente); //messaggio a video&lt;br /&gt;\r\n&amp;nbsp;system(&amp;quot;pause&amp;quot;);&lt;br /&gt;\r\n&amp;nbsp;return 0;&lt;br /&gt;\r\n}&lt;/code&gt;&lt;/p&gt;\r\n', 1, '2021-08-22 17:19:19'),
(55, 8, 15, 'Sequenza di numeri', '1629645763_1629488397_img_c.webp', '&lt;p&gt;&lt;strong&gt;PROBLEMA&lt;/strong&gt;&lt;/p&gt;\r\n\r\n&lt;p&gt;Si scriva un programma per poter analizzare una sequenza di numeri. Dati N numeri interi letti da tastiera si vogliono calcolare e stampare su schermo diversi risultati:&lt;/p&gt;\r\n\r\n&lt;ol&gt;\r\n	&lt;li&gt;quanti sono i numeri positivi, nulli e negativi&lt;/li&gt;\r\n	&lt;li&gt;quanti sono i numeri pari e dispari&lt;/li&gt;\r\n	&lt;li&gt;se la sequenza dei numeri inseriti &amp;egrave; crescente, decrescente oppure n&amp;eacute; crescente n&amp;eacute; decrescente.&lt;/li&gt;\r\n&lt;/ol&gt;\r\n\r\n&lt;hr /&gt;\r\n&lt;p&gt;&lt;strong&gt;CODICE SORGENTE&lt;/strong&gt;&lt;/p&gt;\r\n\r\n&lt;p&gt;&lt;code&gt;#include &amp;lt;stdio.h&amp;gt;&lt;br /&gt;\r\n#include &amp;lt;stdlib.h&amp;gt;&lt;br /&gt;\r\nint main(int argc, char *argv[]) {&lt;br /&gt;\r\n&amp;nbsp;int i=0, valore=0, valore_corrente=0, numeri_da_inserire=0, crescente=0, decrescente=0, positivi=0, negativi=0, neutri=0, pari=0, dispari=0; //dischiarazione variabili&lt;br /&gt;\r\n&amp;nbsp;printf(&amp;quot;Inserisci il numero di elementi da inserire nella sequenza: &amp;quot;); //testo a video&lt;br /&gt;\r\n&amp;nbsp;scanf(&amp;quot;%i&amp;quot;, &amp;amp;numeri_da_inserire); //input&lt;br /&gt;\r\n&amp;nbsp;for(i=0; i&amp;lt;numeri_da_inserire; i++) { //ciclo for per ritere numero_da_inserire&lt;br /&gt;\r\n&amp;nbsp; printf(&amp;quot;Inserisci il numero: &amp;quot;); //testo a video&lt;br /&gt;\r\n&amp;nbsp; scanf(&amp;quot;%i&amp;quot;, &amp;amp;valore); //input&lt;br /&gt;\r\n&amp;nbsp; if (i==0) //se &amp;egrave; uguale a 0&lt;br /&gt;\r\n&amp;nbsp; {&lt;br /&gt;\r\n&amp;nbsp; &amp;nbsp;valore_corrente=valore; //assegnazione&lt;br /&gt;\r\n&amp;nbsp; }&lt;br /&gt;\r\n&amp;nbsp; else //afferrmazione falsa&lt;br /&gt;\r\n&amp;nbsp; {&lt;br /&gt;\r\n&amp;nbsp; &amp;nbsp;if (valore&amp;gt;valore_corrente) //condizione&lt;br /&gt;\r\n&amp;nbsp; &amp;nbsp;{&lt;br /&gt;\r\n&amp;nbsp; &amp;nbsp; crescente++; //incremento&lt;br /&gt;\r\n&amp;nbsp; &amp;nbsp; valore_corrente=valore; //assegnazione&lt;br /&gt;\r\n&amp;nbsp; &amp;nbsp;}&lt;br /&gt;\r\n&amp;nbsp; &amp;nbsp;else if (valore&amp;lt;valore_corrente) //condizione&lt;br /&gt;\r\n&amp;nbsp; &amp;nbsp;{&lt;br /&gt;\r\n&amp;nbsp; &amp;nbsp; decrescente++; //incremento&lt;br /&gt;\r\n&amp;nbsp; &amp;nbsp; valore_corrente=valore; //assegnazione&lt;br /&gt;\r\n&amp;nbsp; &amp;nbsp;}&lt;br /&gt;\r\n&amp;nbsp; &amp;nbsp;else&lt;br /&gt;\r\n&amp;nbsp; &amp;nbsp;{&lt;br /&gt;\r\n&amp;nbsp; &amp;nbsp; crescente++; //incremento&lt;br /&gt;\r\n&amp;nbsp; &amp;nbsp; decrescente++; //incremnto&lt;br /&gt;\r\n&amp;nbsp; &amp;nbsp;}&lt;br /&gt;\r\n&amp;nbsp; }&lt;br /&gt;\r\n&amp;nbsp; if (valore&amp;gt;0) //condizione&lt;br /&gt;\r\n&amp;nbsp; {&lt;br /&gt;\r\n&amp;nbsp; &amp;nbsp;positivi++; //incremento&lt;br /&gt;\r\n&amp;nbsp; }&lt;br /&gt;\r\n&amp;nbsp; else if (valore==0) //condizione&lt;br /&gt;\r\n&amp;nbsp; {&lt;br /&gt;\r\n&amp;nbsp; &amp;nbsp;neutri++; //incremento&lt;br /&gt;\r\n&amp;nbsp; }&lt;br /&gt;\r\n&amp;nbsp; else&lt;br /&gt;\r\n&amp;nbsp; {&lt;br /&gt;\r\n&amp;nbsp; &amp;nbsp;negativi++; //incremento&lt;br /&gt;\r\n&amp;nbsp; }&lt;br /&gt;\r\n&amp;nbsp; if (valore%2==0) //condizione&lt;br /&gt;\r\n&amp;nbsp; {&lt;br /&gt;\r\n&amp;nbsp; &amp;nbsp;pari++; //incremento&lt;br /&gt;\r\n&amp;nbsp; }&lt;br /&gt;\r\n&amp;nbsp; else&lt;br /&gt;\r\n&amp;nbsp; {&lt;br /&gt;\r\n&amp;nbsp; &amp;nbsp;dispari++; //incremnto&lt;br /&gt;\r\n&amp;nbsp; }&lt;br /&gt;\r\n&amp;nbsp;}&lt;br /&gt;\r\n&amp;nbsp;printf(&amp;quot;Positivi: %i\\nNegativi: %i\\nNeutri: %i\\nPari: %i\\nDispari: %i\\n&amp;quot;, positivi, negativi, neutri, pari, dispari); //testo a video&lt;br /&gt;\r\n&amp;nbsp;if (decrescente==0) //condizione&lt;br /&gt;\r\n&amp;nbsp;{&lt;br /&gt;\r\n&amp;nbsp; printf(&amp;quot;La sequenza e&amp;#39;: Crescente\\n&amp;quot;); //testo a video&lt;br /&gt;\r\n&amp;nbsp;}&lt;br /&gt;\r\n&amp;nbsp;else if (crescente==0) //condizione&lt;br /&gt;\r\n&amp;nbsp;{&lt;br /&gt;\r\n&amp;nbsp; printf(&amp;quot;La sequenza e&amp;#39;: Decrescente\\n&amp;quot;); //testo a video&lt;br /&gt;\r\n&amp;nbsp;}&lt;br /&gt;\r\n&amp;nbsp;else&amp;nbsp;&lt;br /&gt;\r\n&amp;nbsp;{&lt;br /&gt;\r\n&amp;nbsp; printf(&amp;quot;La sequenza non e&amp;#39; ne&amp;#39; crescente ne&amp;#39; decrescente\\n&amp;quot;); //testo a video&lt;br /&gt;\r\n&amp;nbsp;}&lt;br /&gt;\r\n&amp;nbsp;system(&amp;quot;pause&amp;quot;);&lt;br /&gt;\r\n&amp;nbsp;return 0;&lt;br /&gt;\r\n}&lt;/code&gt;&lt;/p&gt;\r\n', 1, '2021-08-22 17:22:43'),
(56, 8, 15, 'Somma fino a 100', '1629646639_16294883297_img_c.webp', '&lt;p&gt;&lt;strong&gt;PROBLEMA&lt;/strong&gt;&lt;/p&gt;\r\n\r\n&lt;p&gt;Leggere e sommare una quantit&amp;agrave; indefinita di numeri reali finch&amp;eacute;&amp;sbquo; la loro somma diventa uguale a 100.&lt;/p&gt;\r\n\r\n&lt;hr /&gt;\r\n&lt;p&gt;&lt;strong&gt;CODICE SORGENTE&lt;/strong&gt;&lt;/p&gt;\r\n\r\n&lt;p&gt;&lt;code&gt;#include &amp;lt;stdio.h&amp;gt; //libreria&lt;br /&gt;\r\n#include &amp;lt;stdlib.h&amp;gt; //libreria&lt;br /&gt;\r\n#define NUMERO_MULTIPLI 10 //costante&lt;/code&gt;&lt;/p&gt;\r\n\r\n&lt;p&gt;&lt;code&gt;int main(int argc, char *argv[]) { //corpo del programma&lt;br /&gt;\r\n&amp;nbsp; &amp;nbsp; int n=0, s=0; //dichiarazione variabile&lt;br /&gt;\r\n&amp;nbsp; &amp;nbsp; do { //inizio ciclo&lt;br /&gt;\r\n&amp;nbsp; &amp;nbsp; &amp;nbsp;printf(&amp;quot;inserisci il numero: &amp;quot;);&lt;br /&gt;\r\n&amp;nbsp; &amp;nbsp; &amp;nbsp;scanf (&amp;quot;%i&amp;quot;, &amp;amp;n); //input&lt;br /&gt;\r\n&amp;nbsp; &amp;nbsp; &amp;nbsp;s+=n; //s=s+n&lt;br /&gt;\r\n&amp;nbsp;}while (s&amp;lt;100); //condizione per uscire dal ciclo&lt;br /&gt;\r\n&amp;nbsp;printf(&amp;quot;Somma: %i\\n&amp;quot;, s); //testo a video&lt;br /&gt;\r\n&amp;nbsp;system(&amp;quot;pause&amp;quot;); //pause&lt;br /&gt;\r\n&amp;nbsp; &amp;nbsp; return 0; //tutto ok&lt;br /&gt;\r\n} //fine corpo del programma&amp;nbsp;&lt;/code&gt;&lt;/p&gt;\r\n', 1, '2021-08-22 17:37:19'),
(57, 8, 15, 'Serie di numeri ', '1629647301_1629488397_img_c.webp', '&lt;p&gt;&lt;strong&gt;PROBLEMA&lt;/strong&gt;&lt;/p&gt;\r\n\r\n&lt;p&gt;Inserire una serie di numeri interi e sommarli. Interrompere l&amp;rsquo;immissione al sesto valore pari e visualizzare quindi in output la somma ottenuta.&lt;/p&gt;\r\n\r\n&lt;hr /&gt;\r\n&lt;p&gt;&lt;strong&gt;CODICE SORGENTE&lt;/strong&gt;&lt;/p&gt;\r\n\r\n&lt;p&gt;&lt;code&gt;#include &amp;lt;stdio.h&amp;gt;&amp;nbsp;&amp;nbsp; &amp;nbsp;//libreria&lt;br /&gt;\r\n#include &amp;lt;stdlib.h&amp;gt;&amp;nbsp;&amp;nbsp; &amp;nbsp;//libreria&lt;br /&gt;\r\nint main(int argc, char *argv[]) {&amp;nbsp;&amp;nbsp; &amp;nbsp;//corpo del programma&lt;br /&gt;\r\n&amp;nbsp;int n=0, s=0, numeri_pari_inseriti=0;&amp;nbsp;&amp;nbsp; &amp;nbsp;//dichiarazione variabile&lt;br /&gt;\r\n&amp;nbsp;do {&amp;nbsp;&amp;nbsp; &amp;nbsp;//inizio ciclo&amp;nbsp;&lt;br /&gt;\r\n&amp;nbsp; printf(&amp;quot;Inserisci un numero: &amp;quot;);&amp;nbsp;&amp;nbsp; &amp;nbsp;//testo a video&amp;nbsp;&lt;br /&gt;\r\n&amp;nbsp; scanf(&amp;quot;%i&amp;quot;, &amp;amp;n);&amp;nbsp;&amp;nbsp; &amp;nbsp;//input&lt;br /&gt;\r\n&amp;nbsp; if (n%2==0)&amp;nbsp;&amp;nbsp; &amp;nbsp;//controllo se &amp;egrave; pari&lt;br /&gt;\r\n&amp;nbsp; {&lt;br /&gt;\r\n&amp;nbsp; &amp;nbsp;numeri_pari_inseriti++;&amp;nbsp;&amp;nbsp; &amp;nbsp;//incremento variabile&lt;br /&gt;\r\n&amp;nbsp; }&lt;br /&gt;\r\n&amp;nbsp; s+=n;&amp;nbsp;&amp;nbsp; &amp;nbsp;//s=s+n&lt;br /&gt;\r\n&amp;nbsp;} while(numeri_pari_inseriti!=6);&amp;nbsp;&amp;nbsp; &amp;nbsp;//se numeri_pari_inseriti=6 esci dal ciclio&lt;br /&gt;\r\n&amp;nbsp;printf(&amp;quot;%i&amp;quot;, s);&amp;nbsp;&amp;nbsp; &amp;nbsp;//etsto + variabile a video&lt;br /&gt;\r\n&amp;nbsp;return 0;&amp;nbsp;&amp;nbsp; &amp;nbsp;//tutto ok&lt;br /&gt;\r\n}&amp;nbsp;&amp;nbsp; &amp;nbsp;//fine corpo del programma&lt;/code&gt;&lt;/p&gt;\r\n', 1, '2021-08-22 17:48:21'),
(58, 8, 15, 'I primi 11 multipli', '1629647642_16294883297_img_c.webp', '&lt;p&gt;&lt;strong&gt;PROBLEMA&lt;/strong&gt;&lt;/p&gt;\r\n\r\n&lt;p&gt;Scrivere i primi 11 multipli di un numero inserito da tastiera.&lt;/p&gt;\r\n\r\n&lt;hr /&gt;\r\n&lt;p&gt;&lt;strong&gt;CODICE SORGENTE&lt;/strong&gt;&lt;/p&gt;\r\n\r\n&lt;p&gt;&lt;code&gt;#include &amp;lt;stdio.h&amp;gt;&amp;nbsp;&amp;nbsp; &amp;nbsp;//libreria&lt;br /&gt;\r\n#include &amp;lt;stdlib.h&amp;gt;&amp;nbsp;&amp;nbsp; &amp;nbsp;//libreria&lt;br /&gt;\r\n#define NUMERO_DI_MULTIPLI 11&lt;br /&gt;\r\nint main(int argc, char *argv[]) {&amp;nbsp;&amp;nbsp; &amp;nbsp;//corpo del programma&lt;br /&gt;\r\n&amp;nbsp;int n=0, s=0, i=0;&amp;nbsp;&amp;nbsp; &amp;nbsp;//dichiarazione variabile&lt;br /&gt;\r\n&amp;nbsp;printf(&amp;quot;Gli %i multipli del tuo numero\\nInserisci il numero:&amp;quot;, NUMERO_DI_MULTIPLI);&lt;br /&gt;\r\n&amp;nbsp;scanf(&amp;quot;%i&amp;quot;, &amp;amp;n);&amp;nbsp;&amp;nbsp; &amp;nbsp;//input&lt;br /&gt;\r\n&amp;nbsp;system(&amp;quot;cls&amp;quot;);&amp;nbsp;&amp;nbsp; &amp;nbsp;//clear&lt;br /&gt;\r\n&amp;nbsp;for (i=0; i&amp;lt;NUMERO_DI_MULTIPLI; i++) {&amp;nbsp;&amp;nbsp; &amp;nbsp;//inizio ciclio&lt;br /&gt;\r\n&amp;nbsp; i++;&amp;nbsp;&amp;nbsp; &amp;nbsp;//incremento&lt;br /&gt;\r\n&amp;nbsp; s=n*i;&amp;nbsp;&amp;nbsp; &amp;nbsp;//calcolo&lt;br /&gt;\r\n&amp;nbsp; i--;&amp;nbsp;&amp;nbsp; &amp;nbsp;//decremento&lt;br /&gt;\r\n&amp;nbsp; printf(&amp;quot;%i\\n&amp;quot;, s);&amp;nbsp;&amp;nbsp; &amp;nbsp;//testo a video&lt;br /&gt;\r\n&amp;nbsp;}&amp;nbsp;&amp;nbsp; &amp;nbsp;//fine ciclo for&lt;br /&gt;\r\n&amp;nbsp;return 0;&amp;nbsp;&amp;nbsp; &amp;nbsp;//tutto ok&lt;br /&gt;\r\n}&amp;nbsp;&amp;nbsp; &amp;nbsp;//fine corpo del programma&lt;/code&gt;&lt;/p&gt;\r\n', 1, '2021-08-22 17:54:02'),
(59, 8, 15, 'Serie non crescente', '1629650036_1629488397_img_c.webp', '&lt;p&gt;&lt;strong&gt;PROBLEMA&lt;/strong&gt;&lt;/p&gt;\r\n\r\n&lt;p&gt;Leggere una serie di numeri interi passati dall&amp;rsquo;utente, fermandosi al primo numero che rende la serie non crescente e restituendo quanti numeri erano stati inseriti.&lt;/p&gt;\r\n\r\n&lt;hr /&gt;\r\n&lt;p&gt;&lt;strong&gt;RAPPRESENTAZIONE TECNICO-ILLUSTRTIVA&lt;/strong&gt;&lt;/p&gt;\r\n\r\n&lt;p&gt;Si richiede un numero da tastiera e se il conteggio &amp;egrave; pari a 0 lo si associa a un numero base e si incrementa di 1 il conteggio, se invece il conteggio &amp;egrave; diverso da 0, se il numero base &amp;egrave; minore del numero inserito il numero di base diventa il numero inserito e si incrementa il conteggio di 1 altrimenti si mostra il conteggio e si assegna 0 al conteggio. Ripetere il tutto fin quando il conteggio non &amp;egrave; 0.&lt;/p&gt;\r\n\r\n&lt;hr /&gt;\r\n&lt;p&gt;&lt;strong&gt;TABELLA DATI&lt;/strong&gt;&lt;/p&gt;\r\n\r\n&lt;table align=&quot;left&quot; border=&quot;1&quot; cellpadding=&quot;10&quot; cellspacing=&quot;1&quot; style=&quot;width:100%&quot;&gt;\r\n	&lt;thead&gt;\r\n		&lt;tr&gt;\r\n			&lt;th scope=&quot;col&quot;&gt;IDENTIFICATORE&lt;/th&gt;\r\n			&lt;th scope=&quot;col&quot;&gt;DESCRIZIONE&lt;/th&gt;\r\n		&lt;/tr&gt;\r\n	&lt;/thead&gt;\r\n	&lt;tbody&gt;\r\n		&lt;tr&gt;\r\n			&lt;td&gt;Numero_inserito&lt;/td&gt;\r\n			&lt;td&gt;Numero immesso di tipo intero variabile&lt;/td&gt;\r\n		&lt;/tr&gt;\r\n		&lt;tr&gt;\r\n			&lt;td&gt;Numera_base&lt;/td&gt;\r\n			&lt;td&gt;Numero precedente della sequenza di tipo intero variaible&lt;/td&gt;\r\n		&lt;/tr&gt;\r\n		&lt;tr&gt;\r\n			&lt;td&gt;i&lt;/td&gt;\r\n			&lt;td&gt;variabile conteggio di tipo intero variabile&lt;/td&gt;\r\n		&lt;/tr&gt;\r\n	&lt;/tbody&gt;\r\n&lt;/table&gt;\r\n\r\n&lt;p&gt;&amp;nbsp;&lt;/p&gt;\r\n\r\n&lt;hr /&gt;\r\n&lt;p&gt;&lt;strong&gt;DIAGRAMMA A BLOCCHI&lt;/strong&gt;&lt;/p&gt;\r\n\r\n&lt;p&gt;Clicca qui per visualizzare il&amp;nbsp;&lt;a href=&quot;../assets/images/posts/body/4840867.svg&quot; target=&quot;_blank&quot;&gt;Diagramma a blocchi&lt;/a&gt;.&lt;/p&gt;\r\n\r\n&lt;hr /&gt;\r\n&lt;p&gt;&lt;strong&gt;CODICE SORGENTE&lt;/strong&gt;&lt;/p&gt;\r\n\r\n&lt;p&gt;&lt;code&gt;#include //libreria&lt;br /&gt;\r\n#include //libreria&lt;br /&gt;\r\nint main(int argc, char *argv[]) {&amp;nbsp;&lt;br /&gt;\r\n&amp;nbsp;int numero_inserito=0, numero_base=0, i=0; //dichiarazione variabilio&lt;br /&gt;\r\n&amp;nbsp;printf(&amp;quot;\\1 Inserire una serie di numeri, il programma si fermer\\240 al primo numero che interromper\\240 la serie crescente&amp;quot;); //testo a video \\1 server per una faccia felice, \\240 server per la a accentata&lt;br /&gt;\r\n&amp;nbsp;sleep(4); //attesa di 4 secondi&lt;br /&gt;\r\n&amp;nbsp;do { //inizio ciclo do-while&lt;br /&gt;\r\n&amp;nbsp; system(&amp;quot;cls&amp;quot;); //cancellazione messaggi precedenti&lt;br /&gt;\r\n&amp;nbsp; printf(&amp;quot;inserire il %i\\247 numero:&amp;quot;, i+1); //testo a video \\247 serve per il simbolo speciale&lt;br /&gt;\r\n&amp;nbsp; scanf(&amp;quot;%i&amp;quot;, &amp;amp;numero_inserito); //in-put da tastiera&lt;br /&gt;\r\n&amp;nbsp; if (i==0) //condizione&lt;br /&gt;\r\n&amp;nbsp; {&lt;br /&gt;\r\n&amp;nbsp; &amp;nbsp;numero_base=numero_inserito; //assegnazione&lt;br /&gt;\r\n&amp;nbsp; &amp;nbsp;i++; //incremento di 1&lt;br /&gt;\r\n&amp;nbsp; }&lt;br /&gt;\r\n&amp;nbsp; else if (numero_base &amp;nbsp; {&lt;br /&gt;\r\n&amp;nbsp; &amp;nbsp;numero_base=numero_inserito; //assegnazione&lt;br /&gt;\r\n&amp;nbsp; &amp;nbsp;i++; //incremento di 1&lt;br /&gt;\r\n&amp;nbsp; }&lt;br /&gt;\r\n&amp;nbsp; else&lt;br /&gt;\r\n&amp;nbsp; {&lt;br /&gt;\r\n&amp;nbsp; &amp;nbsp;system(&amp;quot;cls&amp;quot;); //cancellazione messaggi precedenti&lt;br /&gt;\r\n&amp;nbsp; &amp;nbsp;printf(&amp;quot;Numeri totali inseriti:%i\\n&amp;quot;, i); //messaggio a video&lt;br /&gt;\r\n&amp;nbsp; &amp;nbsp;i=0; //assegnazione&lt;br /&gt;\r\n&amp;nbsp; }&lt;br /&gt;\r\n&amp;nbsp;} while(i!=0); //fine ciclo do while rig.10&lt;br /&gt;\r\n&amp;nbsp;system(&amp;quot;PAUSE&amp;quot;); //premi un tasto per continuare&lt;br /&gt;\r\n&amp;nbsp;return 0; //tutto OK&lt;br /&gt;\r\n}&lt;/code&gt;&lt;/p&gt;\r\n', 1, '2021-08-22 18:33:56'),
(60, 8, 15, 'Somma solo dei pari', '1629650724_16294883297_img_c.webp', '&lt;p&gt;&lt;strong&gt;PROBLEMA&lt;/strong&gt;&lt;/p&gt;\r\n\r\n&lt;p&gt;Programma&amp;nbsp;che letta da tastiera una sequenza di numeri interi restituisce a video la somma ed il numero dei pari, la sequenza si arresta inserendo lo 0.&lt;/p&gt;\r\n\r\n&lt;hr /&gt;\r\n&lt;p&gt;&lt;strong&gt;CODICE SORGENTE&lt;/strong&gt;&lt;/p&gt;\r\n\r\n&lt;p&gt;&lt;code&gt;#include &amp;lt;stdio.h&amp;gt; //libreria&lt;br /&gt;\r\n#include &amp;lt;stdlib.h&amp;gt; //libreria&lt;br /&gt;\r\nint main(int argc, char *argv[]) { /body&lt;br /&gt;\r\n&amp;nbsp;int numero=0, somma=0, i=0; //dichiarazione&lt;br /&gt;\r\n&amp;nbsp;do { //inizio ciclo&lt;br /&gt;\r\n&amp;nbsp; printf(&amp;quot;Inserire il numero: &amp;quot;); //testo a video&lt;br /&gt;\r\n&amp;nbsp; scanf(&amp;quot;%i&amp;quot;, &amp;amp;numero); //input&lt;br /&gt;\r\n&amp;nbsp; if (numero&amp;gt;0 &amp;amp;&amp;amp; numero%2==0) //condizione&lt;br /&gt;\r\n&amp;nbsp; {&lt;br /&gt;\r\n&amp;nbsp; &amp;nbsp;i++; //incremento&lt;br /&gt;\r\n&amp;nbsp; &amp;nbsp;somma=+numero; //somma=somma+numero&lt;br /&gt;\r\n&amp;nbsp; }&lt;br /&gt;\r\n&amp;nbsp;} while (numero!=0); //fine ciclio do rig. 8&lt;br /&gt;\r\n&amp;nbsp;printf(&amp;quot;\\nQuantita&amp;#39;: %i&amp;quot;, i); //testo a video&lt;br /&gt;\r\n&amp;nbsp;printf(&amp;quot;\\nSomma: %i&amp;quot;, somma); //testo a video&lt;br /&gt;\r\n&amp;nbsp;system(&amp;quot;PAUSE&amp;quot;); //pausa&lt;br /&gt;\r\n&amp;nbsp;return 0; //tutto ok&lt;br /&gt;\r\n} //end body&lt;/code&gt;&lt;/p&gt;\r\n', 1, '2021-08-22 18:45:24'),
(61, 8, 15, 'Numeri con la virgola', '1629652053_1629488397_img_c.webp', '&lt;p&gt;&lt;strong&gt;PROBLEMA&lt;/strong&gt;&lt;/p&gt;\r\n\r\n&lt;p&gt;Scrivere un programma che legga da tastiera una sequenza di numeri decimali con due cifre dopo la virgola restituisce a video la somma dei numeri positivi, la somma dei numeri negativi, il numero dei positivi, il numero dei negativi. la sequenza si arresta inserendo lo 0&lt;/p&gt;\r\n\r\n&lt;hr /&gt;\r\n&lt;p&gt;&lt;strong&gt;CODICE SORGENTE&lt;/strong&gt;&lt;/p&gt;\r\n\r\n&lt;p&gt;&lt;code&gt;#include &amp;lt;stdio.h&amp;gt; //libreria&lt;br /&gt;\r\n#include &amp;lt;stdlib.h&amp;gt; //libreria&lt;br /&gt;\r\nint main(int argc, char *argv[]) { //body&lt;br /&gt;\r\n&amp;nbsp;float numero=0, somma_positivi=0, somma_negativi=0; //dichiarazione&lt;br /&gt;\r\n&amp;nbsp;int positivi=0, negativi=0; //dichiarazione&lt;br /&gt;\r\n&amp;nbsp;do { //inizio ciclo&amp;nbsp;&lt;br /&gt;\r\n&amp;nbsp; &amp;nbsp;printf(&amp;quot;Inserire il numero: &amp;quot;); //testo &amp;nbsp;video&lt;br /&gt;\r\n&amp;nbsp; &amp;nbsp;scanf(&amp;quot;%f&amp;quot;, &amp;amp;numero); //input&lt;br /&gt;\r\n&amp;nbsp; &amp;nbsp;if (numero&amp;gt;0) //condizione&lt;br /&gt;\r\n&amp;nbsp; &amp;nbsp;{&lt;br /&gt;\r\n&amp;nbsp; &amp;nbsp; positivi++; //incremento&amp;nbsp;&lt;br /&gt;\r\n&amp;nbsp; &amp;nbsp; somma_positivi=+numero; //somma_positivi=somma_positivi+numero&lt;br /&gt;\r\n&amp;nbsp; &amp;nbsp;}&lt;br /&gt;\r\n&amp;nbsp; &amp;nbsp;else if (numero&amp;lt;0) //condizione&lt;br /&gt;\r\n&amp;nbsp; &amp;nbsp;{&lt;br /&gt;\r\n&amp;nbsp; &amp;nbsp; negativi++; //incremento&lt;br /&gt;\r\n&amp;nbsp; &amp;nbsp; somma_negativi=+numero; //somma_negativi=somma_negativi+numero&lt;br /&gt;\r\n&amp;nbsp; &amp;nbsp;}&lt;br /&gt;\r\n&amp;nbsp;} while (numero!=0); //condizione per uiscire dal ciclo di rig. 9&lt;br /&gt;\r\n&amp;nbsp;printf(&amp;quot;\\nQuantita di numeri positivi: %i&amp;quot;, positivi); //testo a video&lt;br /&gt;\r\n&amp;nbsp;printf(&amp;quot;\\nQuantita di numeri negativi: %i&amp;quot;, negativi); //testo a video&lt;br /&gt;\r\n&amp;nbsp;printf(&amp;quot;\\nSomma numeri positivi: %.2f&amp;quot;, somma_positivi); //testo a video&lt;br /&gt;\r\n&amp;nbsp;printf(&amp;quot;\\nSomma numeri negativi: %.2f\\n&amp;quot;, somma_negativi); //testo a video&lt;br /&gt;\r\n&amp;nbsp;system(&amp;quot;PAUSE&amp;quot;); //system pause&lt;br /&gt;\r\n&amp;nbsp;return 0; //tutto ok&lt;br /&gt;\r\n} //end body&lt;/code&gt;&lt;/p&gt;\r\n', 1, '2021-08-22 19:07:33'),
(62, 8, 15, 'Una media al momento', '1629652548_16294883297_img_c.webp', '&lt;p&gt;&lt;strong&gt;PROBLEMA&lt;/strong&gt;&lt;/p&gt;\r\n\r\n&lt;p&gt;Scrivere un programma&amp;nbsp;che legga da tastiera una sequenza di lunghezza ignota a priori di numeri interi positivi. Il programma, a partire dal primo numero introdotto, stampa ogni volta la media di tutti i numeri introdotti. Terminare quando il numero inserito &amp;egrave; negativo.&lt;/p&gt;\r\n\r\n&lt;hr /&gt;\r\n&lt;p&gt;&lt;strong&gt;CODICE SORGENTE&lt;/strong&gt;&lt;/p&gt;\r\n\r\n&lt;p&gt;&lt;code&gt;#include &amp;lt;stdio.h&amp;gt; //libreria&lt;br /&gt;\r\n#include &amp;lt;stdlib.h&amp;gt; //libreria&lt;br /&gt;\r\nint main(int argc, char *argv[]) { //body&lt;br /&gt;\r\n&amp;nbsp;int i=0, somma=0, numero=0; //dichiarazione&lt;br /&gt;\r\n&amp;nbsp;float media=0; //dichiarazione&lt;br /&gt;\r\n&amp;nbsp;do { //inizio ciclo&amp;nbsp;&lt;br /&gt;\r\n&amp;nbsp; &amp;nbsp;printf(&amp;quot;\\nInserire il numero: &amp;quot;); //testo &amp;nbsp;video&lt;br /&gt;\r\n&amp;nbsp; &amp;nbsp;scanf(&amp;quot;%i&amp;quot;, &amp;amp;numero); //input&lt;br /&gt;\r\n&amp;nbsp; &amp;nbsp;if (numero&amp;gt;0) //condizione&lt;br /&gt;\r\n&amp;nbsp; &amp;nbsp;{&lt;br /&gt;\r\n&amp;nbsp; &amp;nbsp; i++; //incremento&lt;br /&gt;\r\n&amp;nbsp; &amp;nbsp; somma=somma+numero; //calcolo&lt;br /&gt;\r\n&amp;nbsp; &amp;nbsp; media=(float)somma/i; //calcolo&lt;br /&gt;\r\n&amp;nbsp; &amp;nbsp;}&lt;br /&gt;\r\n&amp;nbsp; &amp;nbsp;printf(&amp;quot;La media e: %.2f&amp;quot;, media); //testo a video&lt;br /&gt;\r\n&amp;nbsp;} while (numero&amp;gt;=0); //condizione per uiscire dal ciclo di rig. 9&lt;br /&gt;\r\n&amp;nbsp;system(&amp;quot;PAUSE&amp;quot;); //system pause&lt;br /&gt;\r\n&amp;nbsp;return 0; //tutto ok&lt;br /&gt;\r\n} //end body&lt;/code&gt;&lt;/p&gt;\r\n', 1, '2021-08-22 19:15:48'),
(63, 8, 15, 'Somma dei primi 100 numeri pari', '1629655038_1629488397_img_c.webp', '&lt;p&gt;&lt;strong&gt;PROBLEMA&lt;/strong&gt;&lt;/p&gt;\r\n\r\n&lt;p&gt;Scrivere un programma che letti i primi 100 numeri pari restituisce in out-put la loro somma.&lt;/p&gt;\r\n\r\n&lt;hr /&gt;\r\n&lt;p&gt;&lt;strong&gt;CODICE SORGENTE&lt;/strong&gt;&lt;/p&gt;\r\n\r\n&lt;p&gt;&lt;code&gt;#include &amp;lt;stdio.h&amp;gt; //libreria&lt;br /&gt;\r\n#include &amp;lt;stdlib.h&amp;gt; //libreria&lt;br /&gt;\r\n#define NUMERI 200&lt;br /&gt;\r\nint main(int argc, char *argv[]) { //body&lt;br /&gt;\r\n&amp;nbsp;int i=0, somma=0; //dichiarazione&lt;br /&gt;\r\n&amp;nbsp;for (i=0; i&amp;lt;=NUMERI; i++) { //inizio ciclo for&lt;br /&gt;\r\n&amp;nbsp; if (i%2==0) //condizione&lt;br /&gt;\r\n&amp;nbsp; {&lt;br /&gt;\r\n&amp;nbsp; &amp;nbsp;somma=somma+i; //calcolo&lt;br /&gt;\r\n&amp;nbsp; }&amp;nbsp;&lt;br /&gt;\r\n&amp;nbsp;} //fine ciclo rig. 9&lt;br /&gt;\r\n&amp;nbsp;printf(&amp;quot;la somma e: %i\\n&amp;quot;, somma); //testo a video&lt;br /&gt;\r\n&amp;nbsp;system(&amp;quot;PAUSE&amp;quot;); //system pause&lt;br /&gt;\r\n&amp;nbsp;return 0; //tutto ok&lt;br /&gt;\r\n} //end body&lt;/code&gt;&lt;/p&gt;\r\n', 1, '2021-08-22 19:57:18'),
(64, 8, 15, '1000 Numeri da tastiera', '1629655235_16294883297_img_c.webp', '&lt;p&gt;&lt;strong&gt;PROBLEMA&lt;/strong&gt;&lt;/p&gt;\r\n\r\n&lt;p&gt;Scrivere un programma in C, che letti da tastiera 1000 numeri interi restituisce a video:&lt;/p&gt;\r\n\r\n&lt;ul&gt;\r\n	&lt;li&gt;il numero dei numeri pari&lt;/li&gt;\r\n	&lt;li&gt;il numero dei numeri dispari&lt;/li&gt;\r\n	&lt;li&gt;la somma dei numeri pari&lt;/li&gt;\r\n	&lt;li&gt;la somma dei numeri dispari&lt;/li&gt;\r\n	&lt;li&gt;la media dei numeri pari&lt;/li&gt;\r\n	&lt;li&gt;la media dei numeri dispari&lt;/li&gt;\r\n&lt;/ul&gt;\r\n\r\n&lt;hr /&gt;\r\n&lt;p&gt;&lt;strong&gt;CODICE SORGENTE&lt;/strong&gt;&lt;/p&gt;\r\n\r\n&lt;p&gt;&lt;code&gt;#include &amp;lt;stdio.h&amp;gt; //libreria&lt;br /&gt;\r\n#include &amp;lt;stdlib.h&amp;gt; //libreria&lt;br /&gt;\r\n#define NUMERI 1000 //costante&lt;br /&gt;\r\nint main(int argc, char *argv[]) { //body&lt;br /&gt;\r\n&amp;nbsp;int numero=0, q_pari=0, q_dispari=0, somma_pari=0, somma_dispari=0, i=0; //dichiarazione variabili&lt;br /&gt;\r\n&amp;nbsp;float media_pari=0, media_dispari=0; //dichiarazione variabili&lt;br /&gt;\r\n&amp;nbsp;for (i=0; i&amp;lt;NUMERI; i++) { //inizio ciclo for per NUMERI volte&lt;br /&gt;\r\n&amp;nbsp; printf(&amp;quot;Inserisci il valore: &amp;quot;); //testo a video&lt;br /&gt;\r\n&amp;nbsp; scanf(&amp;quot;%i&amp;quot;, &amp;amp;numero); //input&lt;br /&gt;\r\n&amp;nbsp; if (numero%2==0) { //condizione&lt;br /&gt;\r\n&amp;nbsp; &amp;nbsp;q_pari++; //incremento&lt;br /&gt;\r\n&amp;nbsp; &amp;nbsp;somma_pari=somma_pari+numero; //calcolo&lt;br /&gt;\r\n&amp;nbsp; }&lt;br /&gt;\r\n&amp;nbsp; else { //condizione rig.19 non verificata&lt;br /&gt;\r\n&amp;nbsp; &amp;nbsp;q_dispari++; //incremento&lt;br /&gt;\r\n&amp;nbsp; &amp;nbsp;somma_dispari=somma_dispari+numero; //calcolo&lt;br /&gt;\r\n&amp;nbsp; }&lt;br /&gt;\r\n&amp;nbsp;}&lt;br /&gt;\r\n&amp;nbsp;media_pari=(float)somma_pari/NUMERI; //calcolo&lt;br /&gt;\r\n&amp;nbsp;media_dispari=(fl