(66, 8, 15, 'Vettore Topolino', '1629657586_16294883297_img_c.webp', '&lt;p&gt;&lt;strong&gt;PROBLEMA&lt;/strong&gt;&lt;/p&gt;\r\n\r\n&lt;p&gt;Scrivere un programma&amp;nbsp;che letti da tastiera dieci elementi di un array di tipo intero dal nome topolino stampa a video gli elementi stessi&lt;/p&gt;\r\n\r\n&lt;hr /&gt;\r\n&lt;p&gt;&lt;strong&gt;CODICE SORGENTE&lt;/strong&gt;&lt;/p&gt;\r\n\r\n&lt;p&gt;&lt;code&gt;#include &amp;lt;stdio.h&amp;gt; //libreria&lt;br /&gt;\r\n#include &amp;lt;stdlib.h&amp;gt; //libreria&lt;br /&gt;\r\n#define NUMERI 10 //costante&lt;br /&gt;\r\nint main(int argc, char *argv[]) { //start body&lt;br /&gt;\r\n&amp;nbsp;int topolino[NUMERI]; //dichiarazione array&lt;br /&gt;\r\n&amp;nbsp;int i=0; //dichiarazione&lt;br /&gt;\r\n&amp;nbsp;for(i=0;i&amp;lt;NUMERI;i++) { //inizio ciclo for&lt;br /&gt;\r\n&amp;nbsp; printf(&amp;quot;Inserisci il numero %i:&amp;quot;, i+1); //testo a video&lt;br /&gt;\r\n&amp;nbsp; scanf(&amp;quot;%d&amp;quot;, &amp;amp;topolino[i]); //input&lt;br /&gt;\r\n&amp;nbsp;} //fine ciclo for rig.10&lt;br /&gt;\r\n&amp;nbsp;printf(&amp;quot;\\n-----------------------------\\nInterno del vettore topolino:\\n-----------------------------\\n&amp;quot;); //testo a video&lt;br /&gt;\r\n&amp;nbsp;for(i=0;i&amp;lt;NUMERI;i++) { //inizio ciclo for&lt;br /&gt;\r\n&amp;nbsp; printf(&amp;quot;Array numero %i: %i\\n&amp;quot;, i+1, topolino[i]); //testo a video&lt;br /&gt;\r\n&amp;nbsp;} //fine ciclo for rig.15&lt;br /&gt;\r\n&amp;nbsp;return 0;&lt;br /&gt;\r\n}&lt;/code&gt;&lt;/p&gt;\r\n', 1, '2021-08-22 20:39:46'),
(67, 8, 15, 'Somma di un vettore', '1629657699_1629488397_img_c.webp', '&lt;p&gt;&lt;strong&gt;PROBLEMA&lt;/strong&gt;&lt;/p&gt;\r\n\r\n&lt;p&gt;Scrivere un programma&amp;nbsp;che legga da tastiera un array di 15 elementi restituisce a video gli elemnti stessi e la loro somma.&lt;/p&gt;\r\n\r\n&lt;hr /&gt;\r\n&lt;p&gt;&lt;strong&gt;CODICE SORGENTE&lt;/strong&gt;&lt;/p&gt;\r\n\r\n&lt;p&gt;&lt;code&gt;#include &amp;lt;stdio.h&amp;gt; //libreria&lt;br /&gt;\r\n#include &amp;lt;stdlib.h&amp;gt; //libreria&lt;br /&gt;\r\n#define NUMERI 15 //costante&lt;br /&gt;\r\nint main(int argc, char *argv[]) { //start body&lt;br /&gt;\r\n&amp;nbsp;int topolino[NUMERI]; //dichiarazione array&lt;br /&gt;\r\n&amp;nbsp;int i=0, somma=0; //dichiarazione&lt;br /&gt;\r\n&amp;nbsp;for(i=0;i&amp;lt;NUMERI;i++) { //inizio ciclo for&lt;br /&gt;\r\n&amp;nbsp; printf(&amp;quot;Inserisci il numero %i:&amp;quot;, i+1); //testo a video&lt;br /&gt;\r\n&amp;nbsp; scanf(&amp;quot;%d&amp;quot;, &amp;amp;topolino[i]); //input&lt;br /&gt;\r\n&amp;nbsp;} //fine ciclo for rig.10&lt;br /&gt;\r\n&amp;nbsp;system(&amp;quot;cls&amp;quot;);&lt;br /&gt;\r\n&amp;nbsp;printf(&amp;quot;-----------------------------\\nInterno del vettore topolino:\\n-----------------------------\\n&amp;quot;); //testo video&lt;br /&gt;\r\n&amp;nbsp;for(i=0;i&amp;lt;NUMERI;i++) { //inizio ciclo for&lt;br /&gt;\r\n&amp;nbsp; printf(&amp;quot;Arrey numero %i: %i\\n&amp;quot;, i+1, topolino[i]); //testo a video&lt;br /&gt;\r\n&amp;nbsp;} //fine ciclo for rig.14&lt;br /&gt;\r\n&amp;nbsp;for(i=0;i&amp;lt;NUMERI;i++) { //inizio ciclo for&lt;br /&gt;\r\n&amp;nbsp; somma=somma+topolino[i]; //calcolo&lt;br /&gt;\r\n&amp;nbsp;} //fine ciclo for rig.18&lt;br /&gt;\r\n&amp;nbsp;printf(&amp;quot;\\nSomma di tutti vettori: %d&amp;quot;, somma); //testo&lt;br /&gt;\r\n&amp;nbsp;return 0;&lt;br /&gt;\r\n}&lt;/code&gt;&lt;/p&gt;\r\n', 1, '2021-08-22 20:41:39'),
(68, 8, 15, 'Posizione elemento nel vettore', '1629658013_16294883297_img_c.webp', '&lt;p&gt;&lt;strong&gt;PROBLEMA&lt;/strong&gt;&lt;/p&gt;\r\n\r\n&lt;p&gt;Scrivere un programma che letto da tastiera un vettore di 10 elementi stama a video la somma degli elementi, la somma degli elmenti in posizione pari, la somma degli elemnti in posizione dispari, mostrare quelli pari.&lt;/p&gt;\r\n\r\n&lt;hr /&gt;\r\n&lt;p&gt;&lt;strong&gt;CODICE SORGENTE&lt;/strong&gt;&lt;/p&gt;\r\n\r\n&lt;p&gt;&lt;code&gt;#include &amp;lt;stdio.h&amp;gt;&lt;br /&gt;\r\n#include &amp;lt;stdlib.h&amp;gt;&lt;br /&gt;\r\n#define NUMERI 10&lt;br /&gt;\r\nint main(int argc, char *argv[]) {&lt;br /&gt;\r\n&amp;nbsp;int topolino[NUMERI];&lt;br /&gt;\r\n&amp;nbsp;int i=0, somma_pari=0, somma_dispari=0, somma_totale=0;&lt;br /&gt;\r\n&amp;nbsp;for(i=0;i&amp;lt;NUMERI;i++) { //inizio ciclo for&lt;br /&gt;\r\n&amp;nbsp; printf(&amp;quot;Inserisci il numero %i:&amp;quot;, i+1); //testo a video&lt;br /&gt;\r\n&amp;nbsp; scanf(&amp;quot;%d&amp;quot;, &amp;amp;topolino[i]); //input&lt;br /&gt;\r\n&amp;nbsp;} //fine ciclo for rig.8&lt;br /&gt;\r\n&amp;nbsp;system(&amp;quot;cls&amp;quot;); //pulisci schermo&lt;br /&gt;\r\n&amp;nbsp;printf(&amp;quot;-----------------------------\\nInterno del vettore topolino:\\n-----------------------------\\n&amp;quot;); //testo a video&lt;br /&gt;\r\n&amp;nbsp;for(i=0;i&amp;lt;NUMERI;i++) { //inizio ciclo for&lt;br /&gt;\r\n&amp;nbsp; if (topolino[i]%2==0) { //se &amp;egrave; parr&lt;br /&gt;\r\n&amp;nbsp; &amp;nbsp;somma_pari=somma_pari+topolino[i]; //calcolo&lt;br /&gt;\r\n&amp;nbsp; }&lt;br /&gt;\r\n&amp;nbsp; else { //se &amp;egrave; dispari&lt;br /&gt;\r\n&amp;nbsp; &amp;nbsp;somma_dispari=somma_dispari+topolino[i]; //calcolo&lt;br /&gt;\r\n&amp;nbsp; }&lt;br /&gt;\r\n&amp;nbsp;} //fine ciclo for rig.16&lt;br /&gt;\r\n&amp;nbsp;somma_totale=somma_pari+somma_dispari; //calcolo&lt;br /&gt;\r\n&amp;nbsp;printf(&amp;quot;\\nSomma pari: %i\\nSomma dispari: %i\\nSomma totale: %i\\n&amp;quot;, somma_pari, somma_dispari, somma_totale); //testo a video&lt;br /&gt;\r\n&amp;nbsp;for(i=0;i&amp;lt;NUMERI;i++) { //inizio ciclo for&lt;br /&gt;\r\n&amp;nbsp; if (topolino[i]%2==0) { //se &amp;egrave; pari&lt;br /&gt;\r\n&amp;nbsp; &amp;nbsp;printf(&amp;quot;\\nVettore %i e&amp;#39; pari: %d&amp;quot;,i+1, topolino[i]); //testo a video&lt;br /&gt;\r\n&amp;nbsp; }&lt;br /&gt;\r\n&amp;nbsp;} //fine ciclo for rig.26&lt;br /&gt;\r\n&amp;nbsp;return 0;&lt;br /&gt;\r\n}&lt;/code&gt;&lt;/p&gt;\r\n', 1, '2021-08-22 20:46:53'),
(69, 8, 15, 'Media, pari e dispari  di un vettore', '1629658340_1629488397_img_c.webp', '&lt;p&gt;&lt;strong&gt;PROBLEMA&lt;/strong&gt;&lt;/p&gt;\r\n\r\n&lt;p&gt;Scrivere un programma&amp;nbsp;che letto da tastiera un array di 10 elementi stampa a video la media degli elementi, il numero dei pari e il numero dei dispari&lt;/p&gt;\r\n\r\n&lt;hr /&gt;\r\n&lt;p&gt;&lt;strong&gt;CODICE SORGENTE&lt;/strong&gt;&lt;/p&gt;\r\n\r\n&lt;p&gt;&lt;code&gt;#include &amp;lt;stdio.h&amp;gt; //libreria&lt;br /&gt;\r\n#include &amp;lt;stdlib.h&amp;gt; //libreria&lt;br /&gt;\r\n#define NUMERI 10 //costante&lt;br /&gt;\r\nint main(int argc, char *argv[]) { //start body&lt;br /&gt;\r\n&amp;nbsp;int topolino[NUMERI]; //dichiarazione array&lt;br /&gt;\r\n&amp;nbsp;int i=0, pari=0, dispari=0; //dichiarazione&lt;br /&gt;\r\n&amp;nbsp;float somma=0;&lt;br /&gt;\r\n&amp;nbsp;for(i=0;i&amp;lt;NUMERI;i++) { //inizio ciclo for&lt;br /&gt;\r\n&amp;nbsp; printf(&amp;quot;Inserisci il numero %i:&amp;quot;, i+1); //testo a video&lt;br /&gt;\r\n&amp;nbsp; scanf(&amp;quot;%d&amp;quot;, &amp;amp;topolino[i]); //input&lt;br /&gt;\r\n&amp;nbsp;}&lt;br /&gt;\r\n&amp;nbsp;system(&amp;quot;cls&amp;quot;); //pulisci shell&lt;br /&gt;\r\n&amp;nbsp;for(i=0;i&amp;lt;NUMERI;i++) { //ciclo for&lt;br /&gt;\r\n&amp;nbsp; if (topolino[i]%2==0) { //se &amp;egrave; pari&lt;br /&gt;\r\n&amp;nbsp; &amp;nbsp;pari++; //incremento&lt;br /&gt;\r\n&amp;nbsp; }&lt;br /&gt;\r\n&amp;nbsp;}&lt;br /&gt;\r\n&amp;nbsp;for(i=0;i&amp;lt;NUMERI;i++) { //ciclo for&lt;br /&gt;\r\n&amp;nbsp; if (topolino[i]%2!=0) { //se &amp;egrave; pari&lt;br /&gt;\r\n&amp;nbsp; &amp;nbsp;dispari++; //incremento&lt;br /&gt;\r\n&amp;nbsp; }&lt;br /&gt;\r\n&amp;nbsp;}&lt;br /&gt;\r\n&amp;nbsp;for(i=0;i&amp;lt;NUMERI;i++) { //ciclo for&lt;br /&gt;\r\n&amp;nbsp; somma=(float)somma+topolino[i]; //calcolo&lt;br /&gt;\r\n&amp;nbsp;}&lt;br /&gt;\r\n&amp;nbsp;printf(&amp;quot;\\n-----------------------------\\nInterno del vettore topolino:\\n--------------------