(70, 8, 15, 'Posizione del numero nel vettore', '1629658903_16294883297_img_c.webp', '&lt;p&gt;&lt;strong&gt;PROBLEMA&lt;/strong&gt;&lt;/p&gt;\r\n\r\n&lt;p&gt;Scrivere un programma&amp;nbsp;che acquisito da tastiera un array di 10 elementi di tipo intero restituisce a video il numero dei pari, il numero dei dispari, la posizione di ciascun pari e la posizione di ciascun dispari.&lt;/p&gt;\r\n\r\n&lt;hr /&gt;\r\n&lt;p&gt;&lt;strong&gt;CODICE SORGENTE&lt;/strong&gt;&lt;/p&gt;\r\n\r\n&lt;p&gt;&lt;code&gt;//Scrivere un programma in C che acquisito da tastiera un array di 10 elementi di tipo intero restituisce a video il numero dei pari, il numero dei dispari, la posizione di ciascun pari e la posizione di ciascun dispari&lt;br /&gt;\r\n//author: Alessandro Masone&lt;br /&gt;\r\n//Date: 2021/02/10&lt;br /&gt;\r\n#include &amp;lt;stdio.h&amp;gt; //libreria&lt;br /&gt;\r\n#include &amp;lt;stdlib.h&amp;gt; //libreria&lt;br /&gt;\r\n#define NUMERI 10 //costante&lt;br /&gt;\r\n&lt;br /&gt;\r\nint main(int argc, char *argv[]) { //start body&lt;br /&gt;\r\n&amp;nbsp;int numero[NUMERI]; //dichiarazione array&lt;br /&gt;\r\n&amp;nbsp;int i=0, pari=0, dispari=0; //dichiarazione&lt;br /&gt;\r\n&amp;nbsp;printf(&amp;quot;Scrivere un programma in C che acquisito da tastiera un array di 10 elementi di tipo intero restituisce a video il numero dei pari, il numero dei dispari, la posizione di ciascun pari e la posizione di ciascun dispari&amp;quot;);&lt;br /&gt;\r\n&amp;nbsp;sleep(5); //pausa 5 secondi&lt;br /&gt;\r\n&amp;nbsp;system(&amp;quot;cls&amp;quot;); //cls&lt;br /&gt;\r\n&amp;nbsp;for(i=0;i&amp;lt;NUMERI;i++) { //inizio ciclo for&lt;br /&gt;\r\n&amp;nbsp; printf(&amp;quot;Inserisci il numero %i:&amp;quot;, i+1); //testo a video&lt;br /&gt;\r\n&amp;nbsp; scanf(&amp;quot;%d&amp;quot;, &amp;amp;numero[i]); //input&lt;br /&gt;\r\n&amp;nbsp;} //fine ciclo for rig.10&lt;br /&gt;\r\n&amp;nbsp;for (i=0;i&amp;lt;NUMERI;i++) { //inizio for&lt;br /&gt;\r\n&amp;nbsp; if (numero[i]%2==0) { //se &amp;egrave; pari&lt;br /&gt;\r\n&amp;nbsp; &amp;nbsp;pari++; //incremnto&lt;br /&gt;\r\n&amp;nbsp; }&lt;br /&gt;\r\n&amp;nbsp; else { //se &amp;egrave; dispari&lt;br /&gt;\r\n&amp;nbsp; &amp;nbsp;dispari++; //incremento&lt;br /&gt;\r\n&amp;nbsp; }&lt;br /&gt;\r\n&amp;nbsp;} //fine for rig.17&lt;br /&gt;\r\n&amp;nbsp;system(&amp;quot;cls&amp;quot;);&lt;br /&gt;\r\n&amp;nbsp;printf(&amp;quot;-----------------------------\\nInterno del vettore numero:\\n-----------------------------\\n&amp;quot;); //testo a video&lt;br /&gt;\r\n&amp;nbsp;printf(&amp;quot;Numero di vettori pari: %i\\n&amp;quot;, pari); //testo a video&lt;br /&gt;\r\n&amp;nbsp;printf(&amp;quot;Numero di vettori dispari: %i\\n&amp;quot;, dispari); //testo a video&lt;br /&gt;\r\n&amp;nbsp;for (i=0;i&amp;lt;NUMERI;i++) { //inizio for&lt;br /&gt;\r\n&amp;nbsp; if (numero[i]%2==0) { //se &amp;egrave; pari&lt;br /&gt;\r\n&amp;nbsp; &amp;nbsp;printf(&amp;quot;\\nIl vettore numero %i e&amp;#39; pari&amp;quot;, i+1); //testo a video&lt;br /&gt;\r\n&amp;nbsp; }&lt;br /&gt;\r\n&amp;nbsp; else { //se &amp;egrave; dispari&lt;br /&gt;\r\n&amp;nbsp; &amp;nbsp;printf(&amp;quot;\\nIl vettore numero %i e&amp;#39; dispari&amp;quot;, i+1); //testo a video&lt;br /&gt;\r\n&amp;nbsp; } &amp;nbsp;&lt;br /&gt;\r\n&amp;nbsp;} //fine for rig.29&lt;br /&gt;\r\n&amp;nbsp;printf(&amp;quot;\\n\\n\\n\\nClicca un tasto per continuare...&amp;quot;); //testo a video&lt;br /&gt;\r\n&amp;nbsp;getch(); //pausa&lt;br /&gt;\r\n&lt;br /&gt;\r\n&amp;nbsp;system(&amp;quot;PAUSE&amp;quot;);&lt;br /&gt;\r\n&amp;nbsp;return 0;&lt;br /&gt;\r\n}&lt;/code&gt;&lt;/p&gt;\r\n', 1, '2021-08-22 21:01:43'),
(71, 8, 15, 'Vettore a rovescio', '1629659027_1629488397_img_c.webp', '&lt;p&gt;&lt;strong&gt;PROBLEMA&lt;/strong&gt;&lt;/p&gt;\r\n\r\n&lt;p&gt;Scrivere un programma&amp;nbsp;che legge un array di 20 elementi di tipo float, e ne visualizza a video il contenuto a rovescio.&lt;/p&gt;\r\n\r\n&lt;hr /&gt;\r\n&lt;p&gt;&lt;strong&gt;CODICE SORGENTE&lt;/strong&gt;&lt;/p&gt;\r\n\r\n&lt;p&gt;&lt;code&gt;#include &amp;lt;stdio.h&amp;gt; //libreria&lt;br /&gt;\r\n#include &amp;lt;stdlib.h&amp;gt; //libreria&lt;br /&gt;\r\n#define NUMERI 20 //costante&lt;br /&gt;\r\n&lt;br /&gt;\r\nint main(int argc, char *argv[]) { //start body&lt;br /&gt;\r\n&amp;nbsp;float numero[NUMERI]; //dichiarazione array&lt;br /&gt;\r\n&amp;nbsp;int i=0, pari=0, dispari=0; //dichiarazione&lt;br /&gt;\r\n&amp;nbsp;printf(&amp;quot;Scrivere un programma in C che legge un array di 20 elementi di tipo float, e ne visualizza a video il contenuto a rovescio.&amp;quot;); //testo a video&lt;br /&gt;\r\n&amp;nbsp;sleep(5); //pausa 5 secondi&lt;br /&gt;\r\n&amp;nbsp;system(&amp;quot;cls&amp;quot;); //cls&lt;br /&gt;\r\n&amp;nbsp;for(i=0;i&amp;lt;NUMERI;i++) { //inizio ciclo for&lt;br /&gt;\r\n&amp;nbsp; printf(&amp;quot;Inserisci il numero %i:&amp;quot;, i+1); //testo a video&lt;br /&gt;\r\n&amp;nbsp; scanf(&amp;quot;%f&amp;quot;, &amp;amp;numero[i]); //input&lt;br /&gt;\r\n&amp;nbsp;} //fine ciclo for rig.10&lt;br /&gt;\r\n&amp;nbsp;system(&amp;quot;cls&amp;quot;);&lt;br /&gt;\r\n&amp;nbsp;printf(&amp;quot;-----------------------------\\nInterno del vettore numero:\\n-----------------------------\\n&amp;quot;); //testo a video&lt;br /&gt;\r\n&amp;nbsp;for (i=NUMERI; i&amp;gt;0; i--) { //inizio ciclo for&lt;br /&gt;\r\n&amp;nbsp; printf(&amp;quot;Il vettore %i contiene \\t%f\\n&amp;quot;, i, numero[i-1]);&lt;br /&gt;\r\n&amp;nbsp;} //fine ciclo for rig.19&amp;nbsp;&lt;br /&gt;\r\n&amp;nbsp;printf(&amp;quot;\\n\\n\\n\\nClicca un tasto per continuare...&amp;quot;); //testo a video&lt;br /&gt;\r\n&lt;br /&gt;\r\n&amp;nbsp;system(&amp;quot;PAUSE&amp;quot;);&lt;br /&gt;\r\n&amp;nbsp;return 0;&lt;br /&gt;\r\n}&lt;/code&gt;&lt;/p&gt;\r\n', 1, '2021-08-22 21:03:47'),
(72, 8, 15, 'Lavorare con i vettori', '1629659997_16294883297_img_c.webp', '&lt;p&gt;&lt;strong&gt;PROBLEMA&lt;/strong&gt;&lt;/p&gt;\r\n\r\n&lt;p&gt;Scrivere un programma che legge da tastiera un array di numeri reali diversi da 0. La lettura termina quando la somma dei numeri &amp;egrave; maggiore di 50 e comunque non si possono immettere pi&amp;ugrave; di 100 numeri. Stampare in output la somma dei numeri dispari.&lt;/p&gt;\r\n\r\n&lt;hr /&gt;\r\n&lt;p&gt;&lt;strong&gt;CODICE SORGENTE&lt;/strong&gt;&lt;/p&gt;\r\n\r\n&lt;p&gt;&lt;code&gt;#include &amp;lt;stdio.h&amp;gt; //libreria&lt;br /&gt;\r\n#include &amp;lt;stdlib.h&amp;gt; //libreria&lt;br /&gt;\r\n#define NUMERI 100 //costante&lt;br /&gt;\r\n#define SOMMA 50 //costante&lt;/code&gt;&lt;/p&gt;\r\n\r\n&lt;p&gt;&lt;code&gt;int main(int argc, char *argv[]) { //start body&lt;br /&gt;\r\n&amp;nbsp;float numero[NUMERI]; //dichiarazione array&lt;br /&gt;\r\n&amp;nbsp;int i=0, numero_dispari=0;&lt;br /&gt;\r\n&amp;nbsp;float somma=0, somma_dispari=0; //dichiarazione&lt;br /&gt;\r\n&amp;nbsp;printf(&amp;quot;legge da tastiera un array di numeri reali diversi da 0.\\nLa lettura termina quando la somma dei numeri e&amp;#39; maggiore di 50 e comunque non si possono immettere piu&amp;#39; di 100 numero.\\nStampare in output la somma dei numeri dispari.\\n&amp;quot;);&lt;br /&gt;\r\n&amp;nbsp;sleep(5); //pausa 5 secondi&lt;br /&gt;\r\n&amp;nbsp;system(&amp;quot;cls&amp;quot;); //clear&lt;br /&gt;\r\n&amp;nbsp;do { //inizio ciclo do-while&lt;br /&gt;\r\n&amp;nbsp; do { //inizio ciclo do-while&lt;br /&gt;\r\n&amp;nbsp; &amp;nbsp;printf(&amp;quot;Inserisci il numero %i:&amp;quot;, i+1); //testo a video&lt;br /&gt;\r\n&amp;nbsp; &amp;nbsp;scanf(&amp;quot;%f&amp;quot;, &amp;amp;numero[i]); //input&lt;br /&gt;\r\n&amp;nbsp; } while (numero[i]==0); //fine do-while rig.16&lt;br /&gt;\r\n&amp;nbsp;somma=somma+numero[i]; //calcolo&lt;br /&gt;\r\n&amp;nbsp;numero_dispari=numero[i]; //casting variabile&lt;br /&gt;\r\n&amp;nbsp;if (numero_dispari%2!=0) { //se &amp;egrave; dispari&lt;br /&gt;\r\n&amp;nbsp; somma_dispari=somma_dispari+numero[i]; //calcolo&lt;br /&gt;\r\n&amp;nbsp;}&lt;br /&gt;\r\n&amp;nbsp;i++; //incremento&lt;br /&gt;\r\n&amp;nbsp;} while(i&amp;lt;NUMERI &amp;amp;&amp;amp; somma&amp;lt;=SOMMA); //fine ciclo do-while rig.15&amp;nbsp;&lt;br /&gt;\r\n&amp;nbsp;system(&amp;quot;cls&amp;quot;); //clear&lt;br /&gt;\r\n&amp;nbsp;printf(&amp;quot;Somma dei numeri dispari inseriti fin ora e&amp;#39;: %f\\n\\n\\n\\nClicca un tasto per continuare...&amp;quot;, somma_dispari); //testo a video&lt;/code&gt;&lt;/p&gt;\r\n\r\n&lt;p&gt;&lt;code&gt;&amp;nbsp;system(&amp;quot;PAUSE&amp;quot;);&lt;br /&gt;\r\n&amp;nbsp;return 0;&lt;br /&gt;\r\n}&lt;/code&gt;&lt;/p&gt;\r\n', 1, '2021-08-22 21:19:57'),
(73, 8, 15, 'Analizzare una sequenza di numeri con i vettori', '1629660417_1629488397_img_c.webp', '&lt;p&gt;&lt;strong&gt;PROBLEMA&lt;/strong&gt;&lt;/p&gt;\r\n\r\n&lt;p&gt;Si scriva un programma&amp;nbsp;per poter analizzare una sequenza di numeri. Dati N numeri interi letti da tastiera si vogliono calcolare e stampare su schermo diversi risultati:&amp;nbsp;&lt;/p&gt;\r\n\r\n&lt;ul&gt;\r\n	&lt;li&gt;quanti sono i numeri positivi, nulli e negativi&amp;nbsp;&lt;/li&gt;\r\n	&lt;li&gt;quanti sono i numeri pari e dispari&amp;nbsp;&lt;/li&gt;\r\n	&lt;li&gt;e la sequenza dei numeri inseriti e crescente, decrescente oppure n&amp;eacute; crescente n&amp;eacute; decrescente.&amp;nbsp;&lt;/li&gt;\r\n&lt;/ul&gt;\r\n\r\n&lt;hr /&gt;\r\n&lt;p&gt;&lt;strong&gt;CODICE SORGENTE&lt;/strong&gt;&lt;/p&gt;\r\n\r\n&lt;p&gt;&lt;code&gt;#include &amp;lt;stdio.h&amp;gt; //libreria&lt;br /&gt;\r\n#include &amp;lt;stdlib.h&amp;gt;&lt;br /&gt;\r\nvoid banner (int numeri_da_inserire) { //funzione&lt;br /&gt;\r\n&amp;nbsp;printf(&amp;quot;-------------------------------\\nAnalisi sequenza di %i numeri\\n-------------------------------\\n&amp;quot;, numeri_da_inserire); //testo a video&lt;br /&gt;\r\n} //fine funzione rig.11&lt;br /&gt;\r\nint main(int argc, char *argv[]) { //inizio body del programma&lt;br /&gt;\r\n&amp;nbsp;int numeri_da_inserire=0, i=0, numero=0, positivi=0, negativi=0, neutri=0, pari=0, dispari=0, numero_base=0, decrescente=0, crescente=0; //dichiarazione variabili&lt;br /&gt;\r\n&amp;nbsp;printf(&amp;quot;Inserire la quantita&amp;#39; di numeri da analizzare:&amp;quot;); //testo a video&lt;br /&gt;\r\n&amp;nbsp;scanf(&amp;quot;%i&amp;quot;, &amp;amp;numeri_da_inserire); //input&lt;br /&gt;\r\n&amp;nbsp;system(&amp;quot;cls&amp;quot;); //pulire il terminale&lt;br /&gt;\r\n&amp;nbsp;for (i=0; i&amp;lt;numeri_da_inserire; i++) { //ciclo for&lt;br /&gt;\r\n&amp;nbsp; printf(&amp;quot;Inserire il %i\\370 numero:&amp;quot;,i+1); //testo a video&lt;br /&gt;\r\n&amp;nbsp; scanf(&amp;quot;%i&amp;quot;, &amp;amp;numero); //input&lt;br /&gt;\r\n&amp;nbsp; if (numero&amp;gt;0) { //se &amp;egrave; positivo&lt;br /&gt;\r\n&amp;nbsp; &amp;nbsp;positivi++;&lt;br /&gt;\r\n&amp;nbsp; }&lt;br /&gt;\r\n&amp;nbsp; else if (numero&amp;lt;0) { //se &amp;egrave; negativo&lt;br /&gt;\r\n&amp;nbsp; &amp;nbsp;negativi++;&lt;br /&gt;\r\n&amp;nbsp; }&lt;br /&gt;\r\n&amp;nbsp; else { //se &amp;egrave; neutro&lt;br /&gt;\r\n&amp;nbsp; &amp;nbsp;neutri++;&lt;br /&gt;\r\n&amp;nbsp; }&lt;br /&gt;\r\n&amp;nbsp; if(numero%2==0) { //se &amp;egrave; pari&lt;br /&gt;\r\n&amp;nbsp; &amp;nbsp;pari++;&lt;br /&gt;\r\n&amp;nbsp; }&lt;br /&gt;\r\n&amp;nbsp; else { //se &amp;egrave; dispari&lt;br /&gt;\r\n&amp;nbsp; &amp;nbsp;dispari++;&lt;br /&gt;\r\n&amp;nbsp; }&lt;br /&gt;\r\n&amp;nbsp; if (i==0) { //assegna il numero base uguale al primo numero&lt;br /&gt;\r\n&amp;nbsp; &amp;nbsp;numero_base=numero;&lt;br /&gt;\r\n&amp;nbsp; }&lt;br /&gt;\r\n&amp;nbsp; else if (numero_base&amp;lt;numero) { //se la sequenza &amp;egrave; crescente&lt;br /&gt;\r\n&amp;nbsp; &amp;nbsp;crescente=1;&lt;br /&gt;\r\n&amp;nbsp; &amp;nbsp;numero_base=numero;&lt;br /&gt;\r\n&amp;nbsp; }&lt;br /&gt;\r\n&amp;nbsp; else if (numero_base&amp;gt;numero) { //se la sequenza &amp;egrave; decrescente&lt;br /&gt;\r\n&amp;nbsp; &amp;nbsp;decrescente=1;&lt;br /&gt;\r\n&amp;nbsp; &amp;nbsp;numero_base=numero;&lt;br /&gt;\r\n&amp;nbsp; }&lt;br /&gt;\r\n&amp;nbsp; else if (numero_base==numero) { //se la sequenza non &amp;egrave; nulla&lt;br /&gt;\r\n&amp;nbsp; &amp;nbsp;crescente=1;&lt;br /&gt;\r\n&amp;nbsp; &amp;nbsp;decrescente=1;&lt;br /&gt;\r\n&amp;nbsp; }&lt;br /&gt;\r\n&amp;nbsp;}&lt;br /&gt;\r\n&amp;nbsp;system(&amp;quot;cls&amp;quot;); //pulire il terminale&lt;br /&gt;\r\n&amp;nbsp;banner(numeri_da_inserire); //richiamo funzione&lt;br /&gt;\r\n&amp;nbsp;printf(&amp;quot;Positivi: %i\\nNegativi: %i\\nNeutri: %i\\nPari: %i\\nDispari: %i\\nLa sequenza e&amp;#39; &amp;quot;,positivi, negativi, neutri, pari, dispari); //testo a video&lt;br /&gt;\r\n&amp;nbsp;if (decrescente==1 &amp;amp;&amp;amp; crescente==0) { //se &amp;egrave; decrescente&lt;br /&gt;\r\n&amp;nbsp; printf(&amp;quot;decrescente\\n&amp;quot;);&lt;br /&gt;\r\n&amp;nbsp;}&lt;br /&gt;\r\n&amp;nbsp;else if (crescente==1 &amp;amp;&amp;amp; decrescente==0) { //se &amp;egrave; crescente&lt;br /&gt;\r\n&amp;nbsp; printf(&amp;quot;crescente\\n&amp;quot;);&lt;br /&gt;\r\n&amp;nbsp;}&lt;br /&gt;\r\n&amp;nbsp;else {&lt;br /&gt;\r\n&amp;nbsp; printf(&amp;quot;ne decrescente ne crescente\\n&amp;quot;); //se &amp;egrave; nulla&lt;br /&gt;\r\n&amp;nbsp;}&lt;br /&gt;\r\n&amp;nbsp;system(&amp;quot;PAUSE&amp;quot;);&lt;br /&gt;\r\n&amp;nbsp;return 0;&lt;br /&gt;\r\n}&lt;/code&gt;&lt;/p&gt;\r\n', 1, '2021-08-22 21:26:57'),
(74, 8, 15, 'Massimo e minimo in un vettore', '1629661581_16294883297_img_c.webp', '&lt;p&gt;&lt;strong&gt;PROBLEMA&lt;/strong&gt;&lt;/p&gt;\r\n\r\n&lt;p&gt;Si scriva un programma per calcolare il valore massimo e minimo di un insieme di N numeri inseriti da tastiera. Il programma deve leggere il valore di N, ed in seguito deve leggere una sequenza di N numeri. A questo punto il programma deve stampare il massimo ed il minimo tra i numeri inseriti.&lt;/p&gt;\r\n\r\n&lt;hr /&gt;\r\n&lt;p&gt;&lt;strong&gt;CODICE SORGENTE&lt;/strong&gt;&lt;/p&gt;\r\n\r\n&lt;p&gt;&lt;code&gt;#include &amp;lt;stdio.h&amp;gt; //libreria&lt;br /&gt;\r\n#include &amp;lt;stdlib.h&amp;gt;&lt;br /&gt;\r\nvoid banner (int numeri_da_inserire) { //funzione&lt;br /&gt;\r\n&amp;nbsp;printf(&amp;quot;-------------------------------\\nAnalisi sequenza di %i numeri\\n-------------------------------\\n&amp;quot;, numeri_da_inserire); //testo a video&lt;br /&gt;\r\n} //fine funzione rig.11&lt;br /&gt;\r\nint main(int argc, char *argv[]) { //inizio body del programma&lt;br /&gt;\r\n&amp;nbsp;int numeri_da_inserire=0, i=0, numero=0, numero_minore=0, numero_maggiore=0; //dichiarazione variabili&lt;br /&gt;\r\n&amp;nbsp;printf(&amp;quot;Inserire la quantita&amp;#39; di numeri da analizzare:&amp;quot;); //testo a video&lt;br /&gt;\r\n&amp;nbsp;scanf(&amp;quot;%i&amp;quot;, &amp;amp;numeri_da_inserire); //input&lt;br /&gt;\r\n&amp;nbsp;system(&amp;quot;cls&amp;quot;); //pulire il terminale&lt;br /&gt;\r\n&amp;nbsp;for (i=0; i&amp;lt;numeri_da_inserire; i++) { //ciclo for&lt;br /&gt;\r\n&amp;nbsp; printf(&amp;quot;Inserire il %i\\370 numero:&amp;quot;,i+1); //testo a video&lt;br /&gt;\r\n&amp;nbsp; scanf(&amp;quot;%i&amp;quot;, &amp;amp;numero); //input&lt;br /&gt;\r\n&amp;nbsp; if (i==0) { //per assegnare i primi numeri come numeri base&lt;br /&gt;\r\n&amp;nbsp; &amp;nbsp;numero_minore=numero;&lt;br /&gt;\r\n&amp;nbsp; &amp;nbsp;numero_maggiore=numero;&lt;br /&gt;\r\n&amp;nbsp; }&lt;br /&gt;\r\n&amp;nbsp; else { //se i &amp;egrave; maggiore di 0&lt;br /&gt;\r\n&amp;nbsp; &amp;nbsp;if (numero&amp;lt;numero_minore) { //se &amp;egrave; minore&lt;br /&gt;\r\n&amp;nbsp; &amp;nbsp; numero_minore=numero;&lt;br /&gt;\r\n&amp;nbsp; &amp;nbsp;}&lt;br /&gt;\r\n&amp;nbsp; &amp;nbsp;else if (numero&amp;gt;numero_maggiore) { //se &amp;egrave; maggiore&lt;br /&gt;\r\n&amp;nbsp; &amp;nbsp; numero_maggiore=numero;&lt;br /&gt;\r\n&amp;nbsp; &amp;nbsp;}&lt;br /&gt;\r\n&amp;nbsp; }&lt;br /&gt;\r\n&amp;nbsp;}&lt;br /&gt;\r\n&amp;nbsp;system(&amp;quot;cls&amp;quot;); //pulire il terminale&lt;br /&gt;\r\n&amp;nbsp;banner(numeri_da_inserire); //richiamo funzione&lt;br /&gt;\r\n&amp;nbsp;printf(&amp;quot;Il minore e&amp;#39;: %i\\nIl maggiore e&amp;#39;: %i\\n&amp;quot;, numero_minore, numero_maggiore); //testo a video&lt;br /&gt;\r\n&amp;nbsp;system(&amp;quot;PAUSE&amp;quot;);&lt;br /&gt;\r\n&amp;nbsp;return 0;&lt;br /&gt;\r\n}&lt;/code&gt;&lt;/p&gt;\r\n', 1, '2021-08-22 21:46:21'),
(75, 8, 15, 'Vettore con contenuto casuale', '1629661802_1629488397_img_c.webp', '&lt;p&gt;&lt;strong&gt;PROBLEMA&lt;/strong&gt;&lt;/p&gt;\r\n\r\n&lt;p&gt;Dato un vettore di N interi, inizializzati casualmente, si determini il valore massimo tra quelli memorizzati nel vettore e lo si stampi a video.&lt;/p&gt;\r\n\r\n&lt;hr /&gt;\r\n&lt;p&gt;&lt;strong&gt;CODICE SORGENTE&lt;/strong&gt;&lt;/p&gt;\r\n\r\n&lt;p&gt;&lt;code&gt;#include &amp;lt;stdio.h&amp;gt;&lt;br /&gt;\r\n#include &amp;lt;stdlib.h&amp;gt;&lt;br /&gt;\r\n#include &amp;lt;time.h&amp;gt;&lt;/code&gt;&lt;/p&gt;\r\n\r\n&lt;p&gt;&lt;br /&gt;\r\n&lt;code&gt;int main(int argc, char *argv[]) {&lt;br /&gt;\r\n&amp;nbsp;&lt;br /&gt;\r\n&amp;nbsp;//sezione dichiarativa&lt;br /&gt;\r\n&amp;nbsp;int i, a, max, min, posizione_max=0, posizione_min=0;&lt;br /&gt;\r\n&amp;nbsp;srand(time(NULL));&lt;br /&gt;\r\n&amp;nbsp;&lt;br /&gt;\r\n&amp;nbsp;//sezione spiegazione programma&lt;br /&gt;\r\n&amp;nbsp;printf(&amp;quot;Dato un vettore di N interi, inizializzati casualmente.\\nSi determini il valore massimo tra quelli memorizzati nel vettore e lo si stampi a video.&amp;quot;);&lt;br /&gt;\r\n&amp;nbsp;sleep(5);&lt;br /&gt;\r\n&amp;nbsp;&lt;br /&gt;\r\n&amp;nbsp;//sezione di input&lt;br /&gt;\r\n&amp;nbsp;do {&lt;br /&gt;\r\n&amp;nbsp; system(&amp;quot;cls&amp;quot;);&lt;br /&gt;\r\n&amp;nbsp; printf(&amp;quot;Inserire il numero di Vettori da creare:&amp;quot;);&lt;br /&gt;\r\n&amp;nbsp; scanf(&amp;quot;%i&amp;quot;, &amp;amp;i);&lt;br /&gt;\r\n&amp;nbsp; if (i==0 || i==1) { //valore inserito minore di 2&lt;br /&gt;\r\n&amp;nbsp; &amp;nbsp;for (a=5;a&amp;gt;0;a--){&lt;br /&gt;\r\n&amp;nbsp; &amp;nbsp; system(&amp;quot;cls&amp;quot;);&lt;br /&gt;\r\n&amp;nbsp; &amp;nbsp; printf(&amp;quot;[!] Il valore immesso non e&amp;#39; valido potrai reinserire il valore tra %i secondi&amp;quot;, a);&lt;br /&gt;\r\n&amp;nbsp; &amp;nbsp; sleep(1);&lt;br /&gt;\r\n&amp;nbsp; &amp;nbsp;}&lt;br /&gt;\r\n&amp;nbsp; }&lt;br /&gt;\r\n&amp;nbsp;} while (i==0 || i==1);&lt;br /&gt;\r\n&amp;nbsp;&lt;br /&gt;\r\n&amp;nbsp;//sezione dichiarativa&amp;nbsp;&lt;br /&gt;\r\n&amp;nbsp;int array[i];&lt;br /&gt;\r\n&amp;nbsp;&lt;br /&gt;\r\n&amp;nbsp;//assegnazione numero casuale a ogni singolo elemento&lt;br /&gt;\r\n&amp;nbsp;for (a=0; a&amp;lt;i; a++) {&lt;br /&gt;\r\n&amp;nbsp; array[a]=rand();&lt;br /&gt;\r\n&amp;nbsp;}&lt;br /&gt;\r\n&amp;nbsp;&lt;br /&gt;\r\n&amp;nbsp;//assegnazione punto di riferimento minimo e maggiore&lt;br /&gt;\r\n&amp;nbsp;max=array[0];&lt;br /&gt;\r\n&amp;nbsp;min=array[0];&lt;br /&gt;\r\n&amp;nbsp;&lt;br /&gt;\r\n&amp;nbsp;//ricerca del minore e del maggiore&lt;br /&gt;\r\n&amp;nbsp;for (a=0; a&amp;lt;i; a++) {&lt;br /&gt;\r\n&amp;nbsp; if (array[a]&amp;gt;max) { //se &amp;egrave; maggiore del massimo lo stistuisce&lt;br /&gt;\r\n&amp;nbsp; &amp;nbsp;max=array[a];&lt;br /&gt;\r\n&amp;nbsp; &amp;nbsp;posizione_max=a;&lt;br /&gt;\r\n&amp;nbsp; }&lt;br /&gt;\r\n&amp;nbsp; else if (array[a]&amp;lt;min) { //se &amp;egrave; minore del minimo lo stostituisce&lt;br /&gt;\r\n&amp;nbsp; &amp;nbsp;min=array[a];&lt;br /&gt;\r\n&amp;nbsp; &amp;nbsp;posizione_min=a;&lt;br /&gt;\r\n&amp;nbsp; }&lt;br /&gt;\r\n&amp;nbsp;}&lt;br /&gt;\r\n&amp;nbsp;&lt;br /&gt;\r\n&amp;nbsp;//risulati a video&lt;br /&gt;\r\n&amp;nbsp;system(&amp;quot;cls&amp;quot;);&lt;br /&gt;\r\n&amp;nbsp;printf(&amp;quot;----------------------------------------------\\nCaratteristiche del vettore di %i posizioni:\\n----------------------------------------------&amp;quot;, i);&lt;br /&gt;\r\n&amp;nbsp;printf(&amp;quot;\\nIl numero maggiore e&amp;#39; presente nel vettore %i con un valore di: %i&amp;quot;, posizione_max, max);&lt;br /&gt;\r\n&amp;nbsp;printf(&amp;quot;\\nIl numero minore e&amp;#39; presente nel vettore %i con un valore di: %i&amp;quot;, posizione_min, min);&lt;br /&gt;\r\n&amp;nbsp;printf(&amp;quot;\\n----------------------------------------------&amp;quot;);&lt;br /&gt;\r\n&amp;nbsp;printf(&amp;quot;\\nVettore\\tContenuto&amp;quot;);&lt;br /&gt;\r\n&amp;nbsp;for (a=0;a&amp;lt;i;a++) {&lt;br /&gt;\r\n&amp;nbsp; printf(&amp;quot;\\n &amp;nbsp;%i\\t &amp;nbsp;%i&amp;quot;, a+1, array[a]);&lt;br /&gt;\r\n&amp;nbsp;}&lt;br /&gt;\r\n&amp;nbsp;printf(&amp;quot;\\n----------------------------------------------&amp;quot;);&lt;br /&gt;\r\n&amp;nbsp;printf(&amp;quot;\\n\\nPremi un tasto per continuare...&amp;quot;);&lt;/code&gt;&lt;/p&gt;\r\n\r\n&lt;p&gt;&lt;code&gt;&amp;nbsp;system(&amp;quot;PAUSE&amp;quot;);&lt;br /&gt;\r\n&amp;nbsp;return 0;&lt;br /&gt;\r\n}&lt;/code&gt;&lt;/p&gt;\r\n', 1, '2021-08-22 21:50:02'),
(76, 8, 15, 'Valori compresi in un vettore', '1629662339_16294883297_img_c.webp', '&lt;p&gt;&lt;strong&gt;PROBLEMA&lt;/strong&gt;&lt;/p&gt;\r\n\r\n&lt;p&gt;Dato un array di N elementi inizializzato casualmente, e due numeri forniti da tastiera restituire a video quanti valori e quali nell&amp;#39;array sono compresi tra questi due numeri. restituire anche a video la stampa dell&amp;#39;array capovolte.&lt;/p&gt;\r\n\r\n&lt;hr /&gt;\r\n&lt;p&gt;&lt;strong&gt;CODICE SORGENTE&lt;/strong&gt;&lt;/p&gt;\r\n\r\n&lt;p&gt;&lt;code&gt;#include &amp;lt;stdio.h&amp;gt;&lt;br /&gt;\r\n#include &amp;lt;stdlib.h&amp;gt;&lt;br /&gt;\r\n#define VALORE_MASSIMO 15&lt;/code&gt;&lt;/p&gt;\r\n\r\n&lt;p&gt;&lt;code&gt;//dichiarazione funzioni&lt;br /&gt;\r\nvoid banner_traccia();&lt;br /&gt;\r\nvoid banner();&lt;/code&gt;&lt;/p&gt;\r\n\r\n&lt;p&gt;&lt;code&gt;//funzione ringraziamento&lt;br /&gt;\r\nvoid banner (void) {&lt;br /&gt;\r\n&amp;nbsp;printf(&amp;quot;Grazie di aver utilizzato il programma\\n&amp;quot;);&lt;br /&gt;\r\n}&lt;/code&gt;&lt;/p&gt;\r\n\r\n&lt;p&gt;&lt;code&gt;//funzione traccia&lt;br /&gt;\r\nvoid banner_traccia(void) {&lt;br /&gt;\r\n&amp;nbsp;&lt;br /&gt;\r\n&amp;nbsp;printf(&amp;quot;Dato un array di N elementi inizializzato casualmente e due numeri forniti da tastiera\\nrestituisce a video quanti valori e quali nell&amp;#39;array sono compresi tra questi due numeri.\\nRestituisce anche a video la stampa dell&amp;#39;array capovolte&amp;quot;);&lt;br /&gt;\r\n&amp;nbsp;sleep(5);&lt;br /&gt;\r\n&amp;nbsp;system(&amp;quot;cls&amp;quot;);&lt;br /&gt;\r\n}&lt;/code&gt;&lt;/p&gt;\r\n\r\n&lt;p&gt;&lt;code&gt;int main(int argc, char *argv[]) {&lt;br /&gt;\r\n&amp;nbsp;&lt;br /&gt;\r\n&amp;nbsp;//sezione dichiarativa&lt;br /&gt;\r\n&amp;nbsp;int range_minimo, range_massimo, i, a=0, c=0, quantita_elementi_compresi=0;&lt;br /&gt;\r\n&amp;nbsp;srand(time(NULL));&lt;br /&gt;\r\n&amp;nbsp;&lt;br /&gt;\r\n&amp;nbsp;//richiamo funzione per visualizzare la traccia&lt;br /&gt;\r\n&amp;nbsp;banner_traccia();&lt;br /&gt;\r\n&amp;nbsp;&lt;br /&gt;\r\n&amp;nbsp;//sezione di input del numero di elementi maggiore di 2&lt;br /&gt;\r\n&amp;nbsp;do {&lt;br /&gt;\r\n&amp;nbsp; system(&amp;quot;cls&amp;quot;);&lt;br /&gt;\r\n&amp;nbsp; printf(&amp;quot;Inserire numero di Elementi:&amp;quot;);&lt;br /&gt;\r\n&amp;nbsp; scanf(&amp;quot;%i&amp;quot;, &amp;amp;a);&lt;br /&gt;\r\n&amp;nbsp;} while(a&amp;lt;2);&lt;br /&gt;\r\n&amp;nbsp;&lt;br /&gt;\r\n&amp;nbsp;//sezione di input del numero minimo&lt;br /&gt;\r\n&amp;nbsp;system(&amp;quot;cls&amp;quot;);&lt;br /&gt;\r\n&amp;nbsp;printf(&amp;quot;Inserire il valore minimo compreso:&amp;quot;);&lt;br /&gt;\r\n&amp;nbsp;scanf(&amp;quot;%i&amp;quot;, &amp;amp;range_minimo);&amp;nbsp;&lt;/code&gt;&lt;/p&gt;\r\n\r\n&lt;p&gt;&lt;code&gt;&amp;nbsp;//sezione di input del numero massimo&lt;br /&gt;\r\n&amp;nbsp;do {&lt;br /&gt;\r\n&amp;nbsp; system(&amp;quot;cls&amp;quot;);&lt;br /&gt;\r\n&amp;nbsp; printf(&amp;quot;Inserire il valore massimo compreso:&amp;quot;);&lt;br /&gt;\r\n&amp;nbsp; scanf(&amp;quot;%i&amp;quot;, &amp;amp;range_massimo);&lt;br /&gt;\r\n&amp;nbsp; if (range_massimo&amp;lt;range_minimo) {&lt;br /&gt;\r\n&amp;nbsp; &amp;nbsp;for (c=5;c&amp;gt;0;c--){&lt;br /&gt;\r\n&amp;nbsp; &amp;nbsp; system(&amp;quot;cls&amp;quot;);&lt;br /&gt;\r\n&amp;nbsp; &amp;nbsp; printf(&amp;quot;[!] Il valore immesso non e&amp;#39; valido potrai reinserire il valore tra %i secondi&amp;quot;, a);&lt;br /&gt;\r\n&amp;nbsp; &amp;nbsp; sleep(1);&lt;br /&gt;\r\n&amp;nbsp; &amp;nbsp;}&lt;br /&gt;\r\n&amp;nbsp; }&lt;br /&gt;\r\n&amp;nbsp;} while(range_massimo&amp;lt;range_minimo);&lt;/code&gt;&lt;/p&gt;\r\n\r\n&lt;p&gt;&lt;code&gt;&amp;nbsp;//dichiarazione array&lt;br /&gt;\r\n&amp;nbsp;int array[a];&lt;br /&gt;\r\n&amp;nbsp;&lt;br /&gt;\r\n&amp;nbsp;//assegnazione numero casuale a ogni elemento dell&amp;#39;array&lt;br /&gt;\r\n&amp;nbsp;for (i=0;i&amp;lt;a;i++) {&lt;br /&gt;\r\n&amp;nbsp; array[i]=rand()%VALORE_MASSIMO+1;&lt;br /&gt;\r\n&amp;nbsp;}&lt;br /&gt;\r\n&amp;nbsp;&lt;br /&gt;\r\n&amp;nbsp;//sezione visualizzazione dati&lt;br /&gt;\r\n&amp;nbsp;system(&amp;quot;cls&amp;quot;);&lt;br /&gt;\r\n&amp;nbsp;printf(&amp;quot;----------------------------------------------\\nCaratteristiche del vettore di %i elementi:\\n----------------------------------------------&amp;quot;, a);&lt;br /&gt;\r\n&amp;nbsp;&lt;br /&gt;\r\n&amp;nbsp;//conteggio elementi compresi nel range indicato&lt;br /&gt;\r\n&amp;nbsp;for (i=0;i&amp;lt;a;i++) {&lt;br /&gt;\r\n&amp;nbsp; if ((array[i]&amp;gt;range_minimo) &amp;amp;&amp;amp; (array[i]&amp;lt;range_massimo)) {&lt;br /&gt;\r\n&amp;nbsp; quantita_elementi_compresi++;&lt;br /&gt;\r\n&amp;nbsp; }&lt;br /&gt;\r\n&amp;nbsp;}&lt;br /&gt;\r\n&amp;nbsp;printf(&amp;quot;\\nElementi compresi tra %i e %i: %i&amp;quot;,range_minimo, range_massimo, quantita_elementi_compresi);&lt;br /&gt;\r\n&amp;nbsp;&lt;br /&gt;\r\n&amp;nbsp;//visualizzazione elementi compresi con posizione e contenuto&lt;br /&gt;\r\n&amp;nbsp;printf(&amp;quot;\\nElemento\\tContenuto&amp;quot;);&lt;br /&gt;\r\n&amp;nbsp;for (i=0;i&amp;lt;a;i++) {&lt;br /&gt;\r\n&amp;nbsp; if ((array[i]&amp;gt;range_minimo) &amp;amp;&amp;amp; (array[i]&amp;lt;range_massimo)) {&lt;br /&gt;\r\n&amp;nbsp; printf(&amp;quot;\\n &amp;nbsp; %i\\t\\t &amp;nbsp; %i&amp;quot;, i, array[i]);&lt;br /&gt;\r\n&amp;nbsp; }&lt;br /&gt;\r\n&amp;nbsp;}&lt;br /&gt;\r\n&amp;nbsp;&lt;br /&gt;\r\n&amp;nbsp;//visualizzazione vettore capovolto&lt;br /&gt;\r\n&amp;nbsp;printf(&amp;quot;\\n----------------------------------------------\\nVettore capovolto:\\nElemento\\tContenuto&amp;quot;);&lt;br /&gt;\r\n&amp;nbsp;for (i=a;i&amp;gt;0;i--) {&lt;br /&gt;\r\n&amp;nbsp; printf(&amp;quot;\\n &amp;nbsp; %i\\t\\t &amp;nbsp; %i&amp;quot;, i, array[i-1]);&lt;br /&gt;\r\n&amp;nbsp;}&lt;br /&gt;\r\n&amp;nbsp;printf(&amp;quot;\\n----------------------------------------------\\n&amp;quot;);&lt;br /&gt;\r\n&amp;nbsp;printf(&amp;quot;Premi un tasto per continuare...&amp;quot;);&lt;br /&gt;\r\n&amp;nbsp;&lt;br /&gt;\r\n&amp;nbsp;//sezione chiusura programma&lt;br /&gt;\r\n&amp;nbsp;getch();&lt;br /&gt;\r\n&amp;nbsp;system(&amp;quot;cls&amp;quot;);&lt;br /&gt;\r\n&amp;nbsp;for (i=0;i&amp;lt;10;i++) {&lt;br /&gt;\r\n&amp;nbsp; banner();&lt;br /&gt;\r\n&amp;nbsp; sleep(1);&lt;br /&gt;\r\n&amp;nbsp;}&lt;br /&gt;\r\n&amp;nbsp;&lt;br /&gt;\r\n&amp;nbsp;system(&amp;quot;PAUSE&amp;quot;);&lt;br /&gt;\r\n&amp;nbsp;return 0;&lt;br /&gt;\r\n}&lt;/code&gt;&lt;/p&gt;\r\n', 1, '2021-08-22 21:58:59');
