(77, 8, 15, 'Somma di elementi casuali', '1629664395_1629488397_img_c.webp', '&lt;p&gt;&lt;strong&gt;PROBLEMA&lt;/strong&gt;&lt;/p&gt;\r\n\r\n&lt;p&gt;Caricare un array numerico di N elementi in modo casuale. Visualizzare a video gli elementi e la loro somma.&lt;/p&gt;\r\n\r\n&lt;hr /&gt;\r\n&lt;p&gt;&lt;strong&gt;CODICE SORGENTE&lt;/strong&gt;&lt;/p&gt;\r\n\r\n&lt;p&gt;&lt;code&gt;//Codificare il seguente esercizio: Caricare un array numerico di N elementi in modo casuale. Visualizzare a video gli elementi e la loro somma.&lt;br /&gt;\r\n//Author: Masone Alessandro&lt;br /&gt;\r\n//Date: 2021/02/18&lt;br /&gt;\r\n#include &amp;lt;stdio.h&amp;gt;&lt;br /&gt;\r\n#include &amp;lt;stdlib.h&amp;gt;&lt;/code&gt;&lt;/p&gt;\r\n\r\n&lt;p&gt;&lt;code&gt;//dichiarazione funzioni&lt;br /&gt;\r\nvoid banner_traccia();&lt;br /&gt;\r\nvoid banner();&lt;/code&gt;&lt;/p&gt;\r\n\r\n&lt;p&gt;&lt;code&gt;//funzione ringraziamento&lt;br /&gt;\r\nvoid banner (void) {&lt;br /&gt;\r\n&amp;nbsp;printf(&amp;quot;Grazie di aver utilizzato il programma\\n&amp;quot;);&lt;br /&gt;\r\n}&lt;/code&gt;&lt;/p&gt;\r\n\r\n&lt;p&gt;&lt;code&gt;//funzione traccia&lt;br /&gt;\r\nvoid banner_traccia(void) {&lt;br /&gt;\r\n&amp;nbsp;int i=0;&lt;br /&gt;\r\n&amp;nbsp;for (i=5;i&amp;gt;0;i--){&lt;br /&gt;\r\n&amp;nbsp; system(&amp;quot;cls&amp;quot;);&lt;br /&gt;\r\n&amp;nbsp; printf(&amp;quot;Codificare il seguente esercizio: Caricare un array numerico di N elementi in modo casuale.\\nVisualizzare a video gli elementi e la loro somma.&amp;quot;);&lt;br /&gt;\r\n&amp;nbsp; printf(&amp;quot;\\n[!] Attendere %i secondi&amp;quot;, i);&lt;br /&gt;\r\n&amp;nbsp; sleep(1);&lt;br /&gt;\r\n&amp;nbsp;}&lt;/code&gt;&lt;/p&gt;\r\n\r\n&lt;p&gt;&lt;code&gt;&amp;nbsp;system(&amp;quot;cls&amp;quot;);&lt;br /&gt;\r\n}&lt;/code&gt;&lt;/p&gt;\r\n\r\n&lt;p&gt;&lt;code&gt;int main(int argc, char *argv[]) {&lt;br /&gt;\r\n&amp;nbsp;&lt;br /&gt;\r\n&amp;nbsp;//sezione dichiarativo&lt;br /&gt;\r\n&amp;nbsp;int a=0, i=0, somma=0;&lt;br /&gt;\r\n&amp;nbsp;srand(time(NULL));&lt;br /&gt;\r\n&amp;nbsp;&lt;br /&gt;\r\n&amp;nbsp;//funzione traccia&lt;br /&gt;\r\n&amp;nbsp;banner_traccia();&lt;br /&gt;\r\n&amp;nbsp;&lt;br /&gt;\r\n&amp;nbsp;//sezione di input del numero di elementi&lt;br /&gt;\r\n&amp;nbsp;do {&lt;br /&gt;\r\n&amp;nbsp; system(&amp;quot;cls&amp;quot;);&lt;br /&gt;\r\n&amp;nbsp; printf(&amp;quot;Inserire numero di Elementi:&amp;quot;);&lt;br /&gt;\r\n&amp;nbsp; scanf(&amp;quot;%i&amp;quot;, &amp;amp;a);&lt;br /&gt;\r\n&amp;nbsp; if (a&amp;lt;1) {&lt;br /&gt;\r\n&amp;nbsp; &amp;nbsp;for (i=5;i&amp;gt;0;i--){&lt;br /&gt;\r\n&amp;nbsp; &amp;nbsp; system(&amp;quot;cls&amp;quot;);&lt;br /&gt;\r\n&amp;nbsp; &amp;nbsp; printf(&amp;quot;[!] Il valore immesso non e&amp;#39; valido potrai reinserire il valore tra %i secondi&amp;quot;, i);&lt;br /&gt;\r\n&amp;nbsp; &amp;nbsp; sleep(1);&lt;br /&gt;\r\n&amp;nbsp; &amp;nbsp;}&lt;br /&gt;\r\n&amp;nbsp; }&lt;br /&gt;\r\n&amp;nbsp;} while(a&amp;lt;1); //deve essere maggiore di 0&lt;br /&gt;\r\n&amp;nbsp;&lt;br /&gt;\r\n&amp;nbsp;//dichiarazione vettore&lt;br /&gt;\r\n&amp;nbsp;int array[a];&lt;br /&gt;\r\n&amp;nbsp;&lt;br /&gt;\r\n&amp;nbsp;//assegnazione numero casuale&lt;br /&gt;\r\n&amp;nbsp;for (i=0;i&amp;lt;a;i++) {&lt;br /&gt;\r\n&amp;nbsp; array[i]=rand()%50+1;&lt;br /&gt;\r\n&amp;nbsp;}&lt;br /&gt;\r\n&amp;nbsp;&lt;br /&gt;\r\n&amp;nbsp;//somma di tutti gli elementi&lt;br /&gt;\r\n&amp;nbsp;for (i=0;i&amp;lt;a;i++) {&lt;br /&gt;\r\n&amp;nbsp; somma=somma+array[i];&lt;br /&gt;\r\n&amp;nbsp;}&lt;br /&gt;\r\n&amp;nbsp;&lt;br /&gt;\r\n&amp;nbsp;//sezione di visualizzazione dei dati&lt;br /&gt;\r\n&amp;nbsp;system(&amp;quot;cls&amp;quot;);&lt;br /&gt;\r\n&amp;nbsp;printf(&amp;quot;----------------------------------------------\\nCaratteristiche del vettore di %i elementi:\\n----------------------------------------------&amp;quot;, a);&lt;br /&gt;\r\n&amp;nbsp;printf(&amp;quot;\\nElemento\\tContenuto&amp;quot;);&lt;br /&gt;\r\n&amp;nbsp; for (i=0;i&amp;lt;a;i++) {&lt;br /&gt;\r\n&amp;nbsp; printf(&amp;quot;\\n &amp;nbsp; %i\\t\\t &amp;nbsp; %i&amp;quot;, i+1, array[i]);&lt;br /&gt;\r\n&amp;nbsp;}&lt;br /&gt;\r\n&amp;nbsp;printf(&amp;quot;\\n----------------------------------------------\\n&amp;quot;);&lt;br /&gt;\r\n&amp;nbsp;printf(&amp;quot;Somma: %i&amp;quot;, somma);&lt;br /&gt;\r\n&amp;nbsp;printf(&amp;quot;\\n----------------------------------------------\\n&amp;quot;);&lt;br /&gt;\r\n&amp;nbsp;printf(&amp;quot;Premi un tasto per continuare...&amp;quot;);&lt;br /&gt;\r\n&amp;nbsp;&lt;br /&gt;\r\n&amp;nbsp;//sezione chiusura programma&lt;br /&gt;\r\n&amp;nbsp;getch();&lt;br /&gt;\r\n&amp;nbsp;system(&amp;quot;cls&amp;quot;);&lt;br /&gt;\r\n&amp;nbsp;for (i=0;i&amp;lt;10;i++) {&lt;br /&gt;\r\n&amp;nbsp; banner();&lt;br /&gt;\r\n&amp;nbsp; sleep(1);&lt;br /&gt;\r\n&amp;nbsp;}&lt;br /&gt;\r\n&amp;nbsp;&lt;br /&gt;\r\n&amp;nbsp;return 0;&lt;br /&gt;\r\n}&lt;/code&gt;&lt;/p&gt;\r\n', 1, '2021-08-22 22:33:15'),
(78, 8, 15, 'Pari e dispari in un vettore', '1629664895_16294883297_img_c.webp', '&lt;p&gt;&lt;strong&gt;PROBLEMA&lt;/strong&gt;&lt;/p&gt;\r\n\r\n&lt;p&gt;Scrivere un programma che acquisito da tastiera un array di 15 restituisce a video il numero dei pari e dei dispari.&amp;nbsp;&lt;/p&gt;\r\n\r\n&lt;hr /&gt;\r\n&lt;p&gt;&lt;strong&gt;CODICE SORGENTE&lt;/strong&gt;&lt;/p&gt;\r\n\r\n&lt;p&gt;&lt;code&gt;//librerie&lt;br /&gt;\r\n#include &amp;lt;stdio.h&amp;gt;&lt;br /&gt;\r\n#include &amp;lt;stdlib.h&amp;gt;&lt;/code&gt;&lt;/p&gt;\r\n\r\n&lt;p&gt;&lt;code&gt;//costante&lt;br /&gt;\r\n#define N 15&lt;/code&gt;&lt;/p&gt;\r\n\r\n&lt;p&gt;&lt;code&gt;//dichiarazione funzione&lt;br /&gt;\r\nvoid banner();&lt;/code&gt;&lt;/p&gt;\r\n\r\n&lt;p&gt;&lt;code&gt;//funzione&lt;br /&gt;\r\nvoid banner (void) {&lt;br /&gt;\r\n&amp;nbsp;printf(&amp;quot;Grazie di aver utilizzato il programma\\n&amp;quot;);&lt;br /&gt;\r\n}&lt;/code&gt;&lt;/p&gt;\r\n\r\n&lt;p&gt;&lt;code&gt;//funzione del programma&lt;br /&gt;\r\nint main(int argc, char *argv[]) {&lt;/code&gt;&lt;/p&gt;\r\n\r\n&lt;p&gt;&lt;code&gt;&amp;nbsp;//dichiarazione variabili&lt;br /&gt;\r\n&amp;nbsp;int array[N];&lt;br /&gt;\r\n&amp;nbsp;int pari=0, dispari=0, i=0;&lt;/code&gt;&lt;/p&gt;\r\n\r\n&lt;p&gt;&lt;code&gt;&amp;nbsp;//sezione di input&lt;br /&gt;\r\n&amp;nbsp;for (i=0;i&amp;lt;N;i++) {&lt;br /&gt;\r\n&amp;nbsp; printf(&amp;quot;Inserisci valore %i:&amp;quot;, i+1);&lt;br /&gt;\r\n&amp;nbsp; scanf(&amp;quot;%i&amp;quot;, &amp;amp;array[i]);&lt;br /&gt;\r\n&amp;nbsp;}&lt;/code&gt;&lt;/p&gt;\r\n\r\n&lt;p&gt;&lt;code&gt;&amp;nbsp;//verifica di2 tutti gli elementi&lt;br /&gt;\r\n&amp;nbsp;for (i=0;i&amp;lt;N;i++) {&lt;br /&gt;\r\n&amp;nbsp; if (array[i]%2==0) { //se &amp;egrave; oari&lt;br /&gt;\r\n&amp;nbsp; &amp;nbsp;pari++;&lt;br /&gt;\r\n&amp;nbsp; }&lt;br /&gt;\r\n&amp;nbsp; else { //se &amp;egrave; dispari&lt;br /&gt;\r\n&amp;nbsp; &amp;nbsp;dispari++;&lt;br /&gt;\r\n&amp;nbsp; }&lt;br /&gt;\r\n&amp;nbsp;}&lt;/code&gt;&lt;/p&gt;\r\n\r\n&lt;p&gt;&lt;code&gt;&amp;nbsp;//output a video&lt;br /&gt;\r\n&amp;nbsp;printf(&amp;quot;Pari: %i\\n&amp;quot;, pari);&lt;br /&gt;\r\n&amp;nbsp;printf(&amp;quot;Dispari: %i&amp;quot;, dispari);&lt;br /&gt;\r\n&amp;nbsp;getch();&lt;/code&gt;&lt;/p&gt;\r\n\r\n&lt;p&gt;&lt;code&gt;&amp;nbsp;//sezione ringranziamenti&lt;br /&gt;\r\n&amp;nbsp;system(&amp;quot;cls&amp;quot;);&lt;br /&gt;\r\n&amp;nbsp;for (i=0;i==5;i++) {&lt;br /&gt;\r\n&amp;nbsp; banner();&lt;br /&gt;\r\n&amp;nbsp;}&lt;/code&gt;&lt;/p&gt;\r\n\r\n&lt;p&gt;&lt;code&gt;&amp;nbsp;//fine programma&lt;br /&gt;\r\n&amp;nbsp;system(&amp;quot;PAUSE&amp;quot;);&lt;br /&gt;\r\n&amp;nbsp;return 0;&lt;br /&gt;\r\n}&amp;nbsp;&lt;/code&gt;&lt;/p&gt;\r\n', 1, '2021-08-22 22:41:35'),
(79, 8, 15, 'Unione di due vettori', '1629665457_1629488397_img_c.webp', '&lt;p&gt;&lt;strong&gt;PROBLEMA&lt;/strong&gt;&lt;/p&gt;\r\n\r\n&lt;p&gt;Input&lt;/p&gt;\r\n\r\n&lt;ul&gt;\r\n	&lt;li&gt;dimensione Array interi (2 ne sono)&lt;/li&gt;\r\n&lt;/ul&gt;\r\n\r\n&lt;p&gt;Work&lt;/p&gt;\r\n\r\n&lt;ul&gt;\r\n	&lt;li&gt;contenuto randomico&lt;/li&gt;\r\n&lt;/ul&gt;\r\n\r\n&lt;p&gt;Output&lt;/p&gt;\r\n\r\n&lt;ul&gt;\r\n	&lt;li&gt;unione dei due array&lt;/li&gt;\r\n&lt;/ul&gt;\r\n\r\n&lt;hr /&gt;\r\n&lt;p&gt;&lt;strong&gt;CODICE SORGENTE&lt;/strong&gt;&lt;/p&gt;\r\n\r\n&lt;p&gt;&lt;code&gt;#include &amp;lt;stdio.h&amp;gt;&lt;br /&gt;\r\n#include &amp;lt;stdlib.h&amp;gt;&lt;br /&gt;\r\n//funzione ringraziamento&lt;br /&gt;\r\nvoid banner (void) {&lt;br /&gt;\r\n&amp;nbsp;printf(&amp;quot;Grazie di aver utilizzato il programma\\n\\n&amp;quot;);&lt;br /&gt;\r\n}&lt;/code&gt;&lt;/p&gt;\r\n\r\n&lt;p&gt;&lt;code&gt;//funzione traccia&lt;br /&gt;\r\nvoid banner_traccia(void) {&lt;br /&gt;\r\n&amp;nbsp;int i=0;&lt;br /&gt;\r\n&amp;nbsp;for (i=5;i&amp;gt;0;i--){&lt;br /&gt;\r\n&amp;nbsp; system(&amp;quot;cls&amp;quot;);&lt;br /&gt;\r\n&amp;nbsp; printf(&amp;quot;Data un numero N creare due vettori della stessa dimensione inizializzarli casulamente.\\nSi otterra a video il concatenamento alternato in un terzo array&amp;quot;);&lt;br /&gt;\r\n&amp;nbsp; printf(&amp;quot;\\n[!] Attendere %i secondi&amp;quot;, i);&lt;br /&gt;\r\n&amp;nbsp; sleep(1);&lt;br /&gt;\r\n&amp;nbsp;}&lt;/code&gt;&lt;/p&gt;\r\n\r\n&lt;p&gt;&lt;code&gt;&amp;nbsp;system(&amp;quot;cls&amp;quot;);&lt;br /&gt;\r\n}&lt;/code&gt;&lt;/p&gt;\r\n\r\n&lt;p&gt;&lt;code&gt;int main(int argc, char *argv[]) {&lt;br /&gt;\r\n&amp;nbsp;&lt;br /&gt;\r\n&amp;nbsp;//sezione dichiarativo&lt;br /&gt;\r\n&amp;nbsp;int a=0, i, j=0;&lt;br /&gt;\r\n&amp;nbsp;srand(time(NULL));&lt;br /&gt;\r\n&amp;nbsp;&lt;br /&gt;\r\n&amp;nbsp;//funzione traccia&lt;br /&gt;\r\n&amp;nbsp;banner_traccia();&lt;br /&gt;\r\n&amp;nbsp;&lt;br /&gt;\r\n&amp;nbsp;//sezione di input del numero di elementi&lt;br /&gt;\r\n&amp;nbsp;do {&lt;br /&gt;\r\n&amp;nbsp; system(&amp;quot;cls&amp;quot;);&lt;br /&gt;\r\n&amp;nbsp; printf(&amp;quot;Inserire numero di Elementi:&amp;quot;);&lt;br /&gt;\r\n&amp;nbsp; scanf(&amp;quot;%i&amp;quot;, &amp;amp;a);&lt;br /&gt;\r\n&amp;nbsp; if (a&amp;lt;1) {&lt;br /&gt;\r\n&amp;nbsp; &amp;nbsp;for (i=5;i&amp;gt;0;i--){&lt;br /&gt;\r\n&amp;nbsp; &amp;nbsp; system(&amp;quot;cls&amp;quot;);&lt;br /&gt;\r\n&amp;nbsp; &amp;nbsp; printf(&amp;quot;[!] Il valore immesso non e&amp;#39; valido potrai reinserire il valore tra %i secondi&amp;quot;, i);&lt;br /&gt;\r\n&amp;nbsp; &amp;nbsp; sleep(1);&lt;br /&gt;\r\n&amp;nbsp; &amp;nbsp;}&lt;br /&gt;\r\n&amp;nbsp; }&lt;br /&gt;\r\n&amp;nbsp;} while(a&amp;lt;1); //deve essere maggiore di 0&lt;br /&gt;\r\n&amp;nbsp;&lt;br /&gt;\r\n&amp;nbsp;//assegnazione numero casuale&lt;br /&gt;\r\n&amp;nbsp;int array_a[a];&lt;br /&gt;\r\n&amp;nbsp;int array_b[a];&lt;br /&gt;\r\n&amp;nbsp;int array_c[a+a];&lt;br /&gt;\r\n&amp;nbsp;&lt;br /&gt;\r\n&amp;nbsp;//assegnazione numero casuale array_a&lt;br /&gt;\r\n&amp;nbsp;for (i=0;i&amp;lt;a;i++) {&lt;br /&gt;\r\n&amp;nbsp; array_a[i]=rand()%50+1;&lt;br /&gt;\r\n&amp;nbsp;}&lt;/code&gt;&lt;/p&gt;\r\n\r\n&lt;p&gt;&lt;code&gt;&amp;nbsp;//assegnazione numero casuale array_b&lt;br /&gt;\r\n&amp;nbsp;for (i=0;i&amp;lt;a;i++) {&lt;br /&gt;\r\n&amp;nbsp; array_b[i]=rand()%50+1;&lt;br /&gt;\r\n&amp;nbsp;}&lt;br /&gt;\r\n&amp;nbsp;&lt;br /&gt;\r\n&amp;nbsp;&lt;br /&gt;\r\n&amp;nbsp;//concatenamento dei due array&lt;br /&gt;\r\n&amp;nbsp;for (i=0;i&amp;lt;a+a;i++) {&lt;br /&gt;\r\n&amp;nbsp; array_c[i]=array_a[j];&lt;br /&gt;\r\n&amp;nbsp; i++;&lt;br /&gt;\r\n&amp;nbsp; array_c[i]=array_b[j];&lt;br /&gt;\r\n&amp;nbsp; j++;&lt;br /&gt;\r\n&amp;nbsp;}&lt;br /&gt;\r\n&amp;nbsp;&lt;br /&gt;\r\n&amp;nbsp;//sezione di visualizzazione dei dati&lt;br /&gt;\r\n&amp;nbsp;//system(&amp;quot;cls&amp;quot;);&lt;br /&gt;\r\n&amp;nbsp;printf(&amp;quot;----------------------------------------------\\nCaratteristiche del vettore C di %i elementi:\\n----------------------------------------------&amp;quot;, a+a);&lt;br /&gt;\r\n&amp;nbsp;printf(&amp;quot;\\nElemento\\tContenuto&amp;quot;);&lt;br /&gt;\r\n&amp;nbsp; for (i=0;i&amp;lt;a+a;i++) {&lt;br /&gt;\r\n&amp;nbsp; printf(&amp;quot;\\n &amp;nbsp; %i\\t\\t &amp;nbsp; %i&amp;quot;, i+1, array_c[i]);&lt;br /&gt;\r\n&amp;nbsp;}&lt;br /&gt;\r\n&amp;nbsp;printf(&amp;quot;\\n----------------------------------------------\\n&amp;quot;);&lt;br /&gt;\r\n&amp;nbsp;printf(&amp;quot;Premi un tasto per continuare...&amp;quot;);&lt;br /&gt;\r\n&amp;nbsp;&lt;br /&gt;\r\n&amp;nbsp;//sezione chiusura programma&lt;br /&gt;\r\n&amp;nbsp;getch();&lt;br /&gt;\r\n&amp;nbsp;system(&amp;quot;cls&amp;quot;);&lt;br /&gt;\r\n&amp;nbsp;for (i=0;i&amp;lt;10;i++) {&lt;br /&gt;\r\n&amp;nbsp; banner();&lt;br /&gt;\r\n&amp;nbsp; sleep(1);&lt;br /&gt;\r\n&amp;nbsp;}&lt;br /&gt;\r\n&amp;nbsp;&lt;br /&gt;\r\n&amp;nbsp;system(&amp;quot;PAUSE&amp;quot;);&lt;br /&gt;\r\n&amp;nbsp;return 0;&lt;br /&gt;\r\n}&lt;/code&gt;&lt;/p&gt;\r\n', 1, '2021-08-22 22:50:57'),
(80, 8, 15, 'Accodamento vettori', '1629666046_16294883297_img_c.webp', '&lt;p&gt;&lt;strong&gt;PROBLEMA&lt;/strong&gt;&lt;/p&gt;\r\n\r\n&lt;p&gt;Scrivere un programma che acquisiti da tastiera 2 array di interi, il primo di dimensione 3 e il secondo di dimensione 5. Restituisce in output un terzo array che accoda al primo il secondo&lt;/p&gt;\r\n\r\n&lt;hr /&gt;\r\n&lt;p&gt;&lt;strong&gt;CODICE SORGENTE&lt;/strong&gt;&lt;/p&gt;\r\n\r\n&lt;p&gt;&lt;code&gt;#include &amp;lt;stdio.h&amp;gt;&lt;br /&gt;\r\n#include &amp;lt;stdlib.h&amp;gt;&lt;/code&gt;&lt;/p&gt;\r\n\r\n&lt;p&gt;&lt;code&gt;//dichiarazione costanti&lt;br /&gt;\r\n#define MAX_A 3&lt;br /&gt;\r\n#define MAX_B 5&lt;br /&gt;\r\n//dichiarazione funzioni&lt;br /&gt;\r\nvoid caricamentoVettoreA();&lt;br /&gt;\r\nvoid caricamentoVettoreB();&lt;br /&gt;\r\nvoid concatenaVettori();&lt;br /&gt;\r\nvoid visualizzaVettore();&lt;br /&gt;\r\nvoid uscitaProgramma ();&lt;br /&gt;\r\n//funzione per il caricamento del primo vettore&lt;br /&gt;\r\nvoid caricamentoVettoreA(int vettore_A[]) {&lt;br /&gt;\r\n&amp;nbsp;//sezione dichiarativa&lt;br /&gt;\r\n&amp;nbsp;int i;&lt;br /&gt;\r\n&amp;nbsp;//ciclo for per caricare il primo array&lt;br /&gt;\r\n&amp;nbsp;for (i=0;i&amp;lt;MAX_A;i++) {&lt;br /&gt;\r\n&amp;nbsp; printf(&amp;quot;inserisci il %i valore del Vettore A: &amp;quot;, i+1);&lt;br /&gt;\r\n&amp;nbsp; scanf(&amp;quot;%i&amp;quot;, &amp;amp;vettore_A[i]);&lt;br /&gt;\r\n&amp;nbsp;}&lt;br /&gt;\r\n}&lt;br /&gt;\r\n//funzione per il caricamento del secondo vettore&lt;br /&gt;\r\nvoid caricamentoVettoreB(int vettore_B[]) {&lt;br /&gt;\r\n&amp;nbsp;//sezione dichiarativa&lt;br /&gt;\r\n&amp;nbsp;int i;&lt;br /&gt;\r\n&amp;nbsp;//ciclo for per caricare il primo array&lt;br /&gt;\r\n&amp;nbsp;for (i=0;i&amp;lt;MAX_B;i++) {&lt;br /&gt;\r\n&amp;nbsp; printf(&amp;quot;inserisci il %i valore del Vettore B: &amp;quot;, i+1);&lt;br /&gt;\r\n&amp;nbsp; scanf(&amp;quot;%i&amp;quot;, &amp;amp;vettore_B[i]);&lt;br /&gt;\r\n&amp;nbsp;}&lt;br /&gt;\r\n}&lt;br /&gt;\r\n//funzione per concatenare il primo al secondo vettore&lt;br /&gt;\r\nvoid concatenaVettori(int vettore_A[], int vettore_B[], int vettore_C[]){&lt;br /&gt;\r\n&amp;nbsp;//sezione dichiarativa&lt;br /&gt;\r\n&amp;nbsp;int j=0, i; //j=variabile d&amp;#39;appoggio per il conteggio; i=indice&lt;br /&gt;\r\n&amp;nbsp;//caricamento prima parte del terzo vettore con il primo vettore&lt;br /&gt;\r\n&amp;nbsp;for(i=0;i&amp;lt;MAX_A;i++){&lt;br /&gt;\r\n&amp;nbsp; vettore_C[j] = vettore_A[i];&lt;br /&gt;\r\n&amp;nbsp; j++;&lt;br /&gt;\r\n&amp;nbsp;}&lt;br /&gt;\r\n&amp;nbsp;//caricamento seconda parte del terzo vettore con il secondo vettore&lt;br /&gt;\r\n&amp;nbsp;for(i=0;i&amp;lt;MAX_B;i++){&lt;br /&gt;\r\n&amp;nbsp; vettore_C[j] = vettore_B[i];&lt;br /&gt;\r\n&amp;nbsp; j++;&lt;br /&gt;\r\n&amp;nbsp;}&lt;br /&gt;\r\n}&lt;br /&gt;\r\n//funzione per visualizzare il terzo vettore&lt;br /&gt;\r\nvoid visualizzaVettore(int vettore_C[]){&lt;br /&gt;\r\n&amp;nbsp;//sezione dichiarativa&lt;br /&gt;\r\n&amp;nbsp;int i;&lt;br /&gt;\r\n&amp;nbsp;printf(&amp;quot;Vettore C\\nElemento\\tContenuto&amp;quot;);&lt;br /&gt;\r\n&amp;nbsp;//ciclo for per visualizzare il terzo vettore&lt;br /&gt;\r\n&amp;nbsp;for(i=0;i&amp;lt;MAX_A+MAX_B;i++){&lt;br /&gt;\r\n&amp;nbsp; printf(&amp;quot;\\n &amp;nbsp; %i\\t\\t &amp;nbsp; %i&amp;quot;, i+1, vettore_C[i]);&lt;br /&gt;\r\n&amp;nbsp;}&lt;br /&gt;\r\n}&lt;br /&gt;\r\n//funzione ringraziamento&lt;br /&gt;\r\nvoid uscitaProgramma (void) {&lt;br /&gt;\r\n&amp;nbsp;//sezione dichiarativa&lt;br /&gt;\r\n&amp;nbsp;int i;&lt;br /&gt;\r\n&amp;nbsp;printf(&amp;quot;\\nPremi un tasto per tornare uscire...&amp;quot;);&lt;br /&gt;\r\n&amp;nbsp;getch();&lt;br /&gt;\r\n&amp;nbsp;system(&amp;quot;cls&amp;quot;);&lt;br /&gt;\r\n&amp;nbsp;for (i=0;i&amp;lt;5;i++) {&lt;br /&gt;\r\n&amp;nbsp; printf(&amp;quot;Grazie di aver utilizzato il programma\\n\\n&amp;quot;);&lt;br /&gt;\r\n&amp;nbsp; sleep(1);&lt;br /&gt;\r\n&amp;nbsp;}&lt;br /&gt;\r\n}&lt;br /&gt;\r\n//funzione principale del programma&lt;br /&gt;\r\nint main(int argc, char *argv[]) {&lt;br /&gt;\r\n&amp;nbsp;//sezione dichiarativa&lt;br /&gt;\r\n&amp;nbsp;int i;&lt;br /&gt;\r\n&amp;nbsp;int vettore_A[MAX_A];&lt;br /&gt;\r\n&amp;nbsp;int vettore_B[MAX_B];&lt;br /&gt;\r\n&amp;nbsp;int vettore_C[MAX_A+MAX_B];&lt;br /&gt;\r\n&amp;nbsp;//richiamo funzioni&lt;br /&gt;\r\n&amp;nbsp;caricamentoVettoreA(vettore_A);&lt;br /&gt;\r\n&amp;nbsp;caricamentoVettoreB(vettore_B);&lt;br /&gt;\r\n&amp;nbsp;concatenaVettori(vettore_A, vettore_B, vettore_C);&lt;br /&gt;\r\n&amp;nbsp;visualizzaVettore(vettore_C);&lt;br /&gt;\r\n&amp;nbsp;uscitaProgramma();&lt;br /&gt;\r\n&amp;nbsp;system(&amp;quot;PAUSE&amp;quot;);&lt;br /&gt;\r\n&amp;nbsp;return 0;&lt;br /&gt;\r\n}&lt;/code&gt;&lt;/p&gt;\r\n', 1, '2021-08-22 23:00:46'),
(81, 8, 15, 'Accodamento vettori v2', '1629666263_1629488397_img_c.webp', '&lt;p&gt;&lt;strong&gt;PROBLEMA&lt;/strong&gt;&lt;/p&gt;\r\n\r\n&lt;p&gt;Scrivere un programma che acquisiti da tastiera due array di interi le cui dimensioni sono fornite da tastiera, restituisce in output un terzo array con accodato al secondo il primo.&lt;/p&gt;\r\n\r\n&lt;hr /&gt;\r\n&lt;p&gt;&lt;strong&gt;CODICE SORGENTE&lt;/strong&gt;&lt;/p&gt;\r\n\r\n&lt;p&gt;&lt;code&gt;#include &amp;lt;stdio.h&amp;gt;&lt;br /&gt;\r\n#include &amp;lt;stdlib.h&amp;gt;&lt;br /&gt;\r\n//Scrivere un programma in C che acquisiti da tastiera due array di interi le cui dimensioni sono fornite da tastiera, restituisce in output un terzo array con accodato al secondo il primo.&lt;br /&gt;\r\n//Author: Masone Alessandro&lt;br /&gt;\r\n//Date: 2021/02/23&lt;/code&gt;&lt;/p&gt;\r\n\r\n&lt;p&gt;&lt;code&gt;//dichiarazione funzioni&lt;br /&gt;\r\nvoid caricamentoVettoreA();&lt;br /&gt;\r\nvoid caricamentoVettoreB();&lt;br /&gt;\r\nvoid concatenaVettori();&lt;br /&gt;\r\nvoid uscitaProgramma();&lt;br /&gt;\r\n//funzione per il caricamento del primo vettore&lt;br /&gt;\r\nvoid caricamentoVettoreA(int vettore_A[], int n_a) {&lt;br /&gt;\r\n&amp;nbsp;//sezione dichiarativa&lt;br /&gt;\r\n&amp;nbsp;int i;&lt;br /&gt;\r\n&amp;nbsp;//ciclo for per caricare il primo array&lt;br /&gt;\r\n&amp;nbsp;for (i=0;i&amp;lt;n_a;i++) {&lt;br /&gt;\r\n&amp;nbsp; printf(&amp;quot;inserisci il %i valore del Vettore A: &amp;quot;, i+1);&lt;br /&gt;\r\n&amp;nbsp; scanf(&amp;quot;%i&amp;quot;, &amp;amp;vettore_A[i]);&lt;br /&gt;\r\n&amp;nbsp;}&lt;br /&gt;\r\n}&lt;br /&gt;\r\n//funzione per il caricamento del secondo vettore&lt;br /&gt;\r\nvoid caricamentoVettoreB(int vettore_B[], int n_b) {&lt;br /&gt;\r\n&amp;nbsp;//sezione dichiarativa&lt;br /&gt;\r\n&amp;nbsp;int i;&lt;br /&gt;\r\n&amp;nbsp;//ciclo for per caricare il primo array&lt;br /&gt;\r\n&amp;nbsp;for (i=0;i&amp;lt;n_b;i++) {&lt;br /&gt;\r\n&amp;nbsp; printf(&amp;quot;inserisci il %i valore del Vettore B: &amp;quot;, i+1);&lt;br /&gt;\r\n&amp;nbsp; scanf(&amp;quot;%i&amp;quot;, &amp;amp;vettore_B[i]);&lt;br /&gt;\r\n&amp;nbsp;}&lt;br /&gt;\r\n}&lt;br /&gt;\r\n//funzione per concatenare il primo al secondo vettore&lt;br /&gt;\r\nvoid concatenaVettori(int vettore_A[], int vettore_B[], int vettore_C[], int n_a, int n_b){&lt;br /&gt;\r\n&amp;nbsp;//sezione dichiarativa&lt;br /&gt;\r\n&amp;nbsp;int j=0, i; //j=variabile d&amp;#39;appoggio per il conteggio; i=indice&lt;br /&gt;\r\n&amp;nbsp;//caricamento seconda parte del terzo vettore con il secondo vettore&lt;br /&gt;\r\n&amp;nbsp;for(i=0;i&amp;lt;n_b;i++){&lt;br /&gt;\r\n&amp;nbsp; vettore_C[j] = vettore_B[i];&lt;br /&gt;\r\n&amp;nbsp; j++;&lt;br /&gt;\r\n&amp;nbsp;}&lt;br /&gt;\r\n&amp;nbsp;//caricamento prima parte del terzo vettore con il primo vettore&lt;br /&gt;\r\n&amp;nbsp;for(i=0;i&amp;lt;n_a;i++){&lt;br /&gt;\r\n&amp;nbsp; vettore_C[j] = vettore_A[i];&lt;br /&gt;\r\n&amp;nbsp; j++;&lt;br /&gt;\r\n&amp;nbsp;}&lt;br /&gt;\r\n}&lt;br /&gt;\r\n//funzione per visualizzare il terzo vettore&lt;br /&gt;\r\nvoid visualizzaVettore(int vettore_C[], int n_a, int n_b){&lt;br /&gt;\r\n&amp;nbsp;//sezione dichiarativa&lt;br /&gt;\r\n&amp;nbsp;int i;&lt;br /&gt;\r\n&amp;nbsp;printf(&amp;quot;Vettore C\\nElemento\\tContenuto&amp;quot;);&lt;br /&gt;\r\n&amp;nbsp;//ciclo for per visualizzare il terzo vettore&lt;br /&gt;\r\n&amp;nbsp;for(i=0;i&amp;lt;n_a+n_b;i++){&lt;br /&gt;\r\n&amp;nbsp; printf(&amp;quot;\\n &amp;nbsp; %i\\t\\t &amp;nbsp; %i&amp;quot;, i+1, vettore_C[i]);&lt;br /&gt;\r\n&amp;nbsp;} &amp;nbsp;&amp;nbsp;&lt;br /&gt;\r\n}&lt;br /&gt;\r\n//funzione ringraziamento&lt;br /&gt;\r\nvoid uscitaProgramma (void) {&lt;br /&gt;\r\n&amp;nbsp;//sezione dichiarativa&lt;br /&gt;\r\n&amp;nbsp;int i;&lt;br /&gt;\r\n&amp;nbsp;printf(&amp;quot;\\nPremi un tasto per tornare uscire...&amp;quot;);&lt;br /&gt;\r\n&amp;nbsp;getch();&lt;br /&gt;\r\n&amp;nbsp;system(&amp;quot;cls&amp;quot;);&lt;br /&gt;\r\n&amp;nbsp;for (i=0;i&amp;lt;5;i++) {&lt;br /&gt;\r\n&amp;nbsp; printf(&amp;quot;Grazie di aver utilizzato il programma\\n\\n&amp;quot;);&lt;br /&gt;\r\n&amp;nbsp; sleep(1);&lt;br /&gt;\r\n&amp;nbsp;}&lt;br /&gt;\r\n}&lt;br /&gt;\r\n//funzione principale del programma&lt;br /&gt;\r\nint main(int argc, char *argv[]) {&lt;br /&gt;\r\n&amp;nbsp;//sezione dichiarativa&lt;br /&gt;\r\n&amp;nbsp;int n_a, n_b; //n_a=numero di elementi del vettore A; n_b=numero di elementi del vettore B&lt;br /&gt;\r\n&amp;nbsp;printf(&amp;quot;Inserisci Elementi del vettore A: &amp;quot;);&lt;br /&gt;\r\n&amp;nbsp;scanf(&amp;quot;%i&amp;quot;, &amp;amp;n_a);&lt;br /&gt;\r\n&amp;nbsp;//dichiarazione vettore&lt;br /&gt;\r\n&amp;nbsp;int vettore_A[n_a];&lt;br /&gt;\r\n&amp;nbsp;//richiamo funzione&lt;br /&gt;\r\n&amp;nbsp;caricamentoVettoreA(vettore_A, n_a);&lt;br /&gt;\r\n&amp;nbsp;printf(&amp;quot;Inserisci Elementi del vettore B: &amp;quot;);&lt;br /&gt;\r\n&amp;nbsp;scanf(&amp;quot;%i&amp;quot;, &amp;amp;n_b);&lt;br /&gt;\r\n&amp;nbsp;//dichiarazione vettore&lt;br /&gt;\r\n&amp;nbsp;int vettore_B[n_b];&lt;br /&gt;\r\n&amp;nbsp;//richiamo funzione&lt;br /&gt;\r\n&amp;nbsp;caricamentoVettoreB(vettore_B, n_b);&lt;br /&gt;\r\n&amp;nbsp;//dichiarazione vettore&lt;br /&gt;\r\n&amp;nbsp;int vettore_C[n_a+n_b];&lt;br /&gt;\r\n&amp;nbsp;//richiamo funzione&lt;br /&gt;\r\n&amp;nbsp;concatenaVettori(vettore_A, vettore_B, vettore_C, n_a, n_b);&lt;br /&gt;\r\n&amp;nbsp;visualizzaVettore(vettore_C, n_a, n_b);&lt;br /&gt;\r\n&amp;nbsp;uscitaProgramma();&lt;br /&gt;\r\n&amp;nbsp;system(&amp;quot;PAUSE&amp;quot;);&lt;br /&gt;\r\n&amp;nbsp;return 0;&lt;br /&gt;\r\n}&lt;/code&gt;&lt;/p&gt;\r\n', 1, '2021-08-22 23:04:23'),
(82, 8, 15, 'Lavoro con tre vettori', '1629666463_16294883297_img_c.webp', '&lt;p&gt;&lt;strong&gt;PROBLEMA&lt;/strong&gt;&lt;/p&gt;\r\n\r\n&lt;p&gt;Scrivere un programma&amp;nbsp;che acquisiti da tastiera due array di interi le cui dimensioni sono fornite da tastiera, restituisce in output un terzo array con accodato del secondo al primo nonch&amp;eacute; il minimo ed il massimo del terzo array.&lt;/p&gt;\r\n\r\n&lt;hr /&gt;\r\n&lt;p&gt;&lt;strong&gt;CODICE SORGENTE&lt;/strong&gt;&lt;/p&gt;\r\n\r\n&lt;p&gt;&lt;code&gt;#include &amp;lt;stdio.h&amp;gt;&lt;br /&gt;\r\n#include &amp;lt;stdlib.h&amp;gt;&lt;br /&gt;\r\n//Scrivere un programma in C che acquisiti da tastiera due array di interi le cui dimensioni sono fornite da tastiera, restituisce in output un terzo array con accodato del secondo al primo nonch&amp;eacute; il minimo ed il massimo del terzo array.&lt;br /&gt;\r\n//Author: Masone Alessandro&lt;br /&gt;\r\n//Date: 2021/02/23&lt;/code&gt;&lt;/p&gt;\r\n\r\n&lt;p&gt;&lt;code&gt;//dichiarazione funzioni&lt;br /&gt;\r\nvoid caricamentoVettoreA();&lt;br /&gt;\r\nvoid caricamentoVettoreB();&lt;br /&gt;\r\nvoid concatenaVettori();&lt;br /&gt;\r\nvoid massimo();&lt;br /&gt;\r\nvoid minimo();&lt;br /&gt;\r\nvoid uscitaProgramma();&lt;br /&gt;\r\n//funzione per il caricamento del primo vettore&lt;br /&gt;\r\nvoid caricamentoVettoreA(int vettore_A[], int n_a) {&lt;br /&gt;\r\n&amp;nbsp;//sezione dichiarativa&lt;br /&gt;\r\n&amp;nbsp;int i;&lt;br /&gt;\r\n&amp;nbsp;//ciclo for per caricare il primo array&lt;br /&gt;\r\n&amp;nbsp;for (i=0;i&amp;lt;n_a;i++) {&lt;br /&gt;\r\n&amp;nbsp; printf(&amp;quot;inserisci il %i valore del Vettore A: &amp;quot;, i+1);&lt;br /&gt;\r\n&amp;nbsp; scanf(&amp;quot;%i&amp;quot;, &amp;amp;vettore_A[i]);&lt;br /&gt;\r\n&amp;nbsp;}&lt;br /&gt;\r\n}&lt;br /&gt;\r\n//funzione per il caricamento del secondo vettore&lt;br /&gt;\r\nvoid caricamentoVettoreB(int vettore_B[], int n_b) {&lt;br /&gt;\r\n&amp;nbsp;//sezione dichiarativa&lt;br /&gt;\r\n&