mp;nbsp;int max;&lt;br /&gt;\r\n&amp;nbsp;int i;&lt;br /&gt;\r\n&amp;nbsp;max=vettore_C[0];&lt;br /&gt;\r\n&amp;nbsp;for (i=0;i&amp;lt;n_a+n_b;i++) {&lt;br /&gt;\r\n&amp;nbsp; if(max&amp;lt;vettore_C[i]) {&lt;br /&gt;\r\n&amp;nbsp; &amp;nbsp;max=vettore_C[i];&lt;br /&gt;\r\n&amp;nbsp; }&lt;br /&gt;\r\n&amp;nbsp;}&lt;br /&gt;\r\n&amp;nbsp;printf(&amp;quot;\\nIl valore massimo e&amp;#39;: %i&amp;quot;, max);&lt;br /&gt;\r\n}&lt;br /&gt;\r\n//funzione per visualizzare il minimo&lt;br /&gt;\r\nvoid minimo(int vettore_C[], int n_a, int n_b) {&lt;br /&gt;\r\n&amp;nbsp;//sezione dichiarativa&lt;br /&gt;\r\n&amp;nbsp;int min;&lt;br /&gt;\r\n&amp;nbsp;int i;&lt;br /&gt;\r\n&amp;nbsp;min=vettore_C[0];&lt;br /&gt;\r\n&amp;nbsp;for (i=0;i&amp;lt;n_a+n_b;i++) {&lt;br /&gt;\r\n&amp;nbsp; if(min&amp;gt;vettore_C[i]) {&lt;br /&gt;\r\n&amp;nbsp; &amp;nbsp;min=vettore_C[i];&lt;br /&gt;\r\n&amp;nbsp; }&lt;br /&gt;\r\n&amp;nbsp;}&lt;br /&gt;\r\n&amp;nbsp;printf(&amp;quot;\\nIl valore minimo e&amp;#39;: %i&amp;quot;, min);&lt;br /&gt;\r\n}&lt;br /&gt;\r\n//funzione ringraziamento&lt;br /&gt;\r\nvoid uscitaProgramma (void) {&lt;br /&gt;\r\n&amp;nbsp;//sezione dichiarativa&lt;br /&gt;\r\n&amp;nbsp;int i;&lt;br /&gt;\r\n&amp;nbsp;printf(&amp;quot;\\nPremi un tasto per tornare uscire...&amp;quot;);&lt;br /&gt;\r\n&amp;nbsp;getch();&lt;br /&gt;\r\n&amp;nbsp;system(&amp;quot;cls&amp;quot;);&lt;br /&gt;\r\n&amp;nbsp;for (i=0;i&amp;lt;5;i++) {&lt;br /&gt;\r\n&amp;nbsp; printf(&amp;quot;Grazie di aver utilizzato il programma\\n\\n&amp;quot;);&lt;br /&gt;\r\n&amp;nbsp; sleep(1);&lt;br /&gt;\r\n&amp;nbsp;}&lt;br /&gt;\r\n}&lt;br /&gt;\r\n//funzione principale del programma&lt;br /&gt;\r\nint main(int argc, char *argv[]) {&lt;br /&gt;\r\n&amp;nbsp;//sezione dichiarativa&lt;br /&gt;\r\n&amp;nbsp;int n_a, n_b; //n_a=numero di elementi del vettore A; n_b=numero di elementi del vettore B&lt;br /&gt;\r\n&amp;nbsp;printf(&amp;quot;Inserisci Elementi del vettore A: &amp;quot;);&lt;br /&gt;\r\n&amp;nbsp;sc