f (vettore_D[i]%2==0) {&lt;br /&gt;\r\n&amp;nbsp; &amp;nbsp;pari++;&lt;br /&gt;\r\n&amp;nbsp; }&lt;br /&gt;\r\n&amp;nbsp;}&lt;br /&gt;\r\n&amp;nbsp;//valore a video&lt;br /&gt;\r\n&amp;nbsp;printf(&amp;quot;Numero di elementi pari:%i&amp;quot;, pari);&lt;br /&gt;\r\n&amp;nbsp;printf(&amp;quot;\\n----------------------------------------------------\\n&amp;quot;);&lt;br /&gt;\r\n}&lt;/code&gt;&lt;/p&gt;\r\n\r\n&lt;p&gt;&lt;code&gt;void dispari (int vettore_D[], int n_a, int n_b, int n_c) {&lt;br /&gt;\r\n&amp;nbsp;//sezione dichiarativa&lt;br /&gt;\r\n&amp;nbsp;int i, dispari=0;&lt;br /&gt;\r\n&amp;nbsp;//for per contare i numeri dispari nel vettore D&lt;br /&gt;\r\n&amp;nbsp;for(i=0;i&amp;lt;n_a+n_b+n_c;i++){&lt;br /&gt;\r\n&amp;nbsp; if (vettore_D[i]%2!=0) {&lt;br /&gt;\r\n&amp;nbsp; &amp;nbsp;dispari++;&lt;br /&gt;\r\n&amp;nbsp; }&lt;br /&gt;\r\n&amp;nbsp;}&lt;br /&gt;\r\n&amp;nbsp;//valore a video&lt;br /&gt;\r\n&amp;nbsp;printf(&amp;quot;Numero di elementi dispari:%i&amp;quot;, dispari);&lt;br /&gt;\r\n&amp;nbsp;printf(&amp;quot;\\n----------------------------------------------------\\n&amp;quot;);&lt;br /&gt;\r\n}&lt;br /&gt;\r\nvoid visualizzaVettore_D(int vettore_D[], int n_a, int n_b, int n_c){&lt;br /&gt;\r\n&amp;nbsp;//sezione dichiarativa&lt;br /&gt;\r\n&amp;nbsp;int i;&lt;br /&gt;\r\n&amp;nbsp;system(&amp;quot;cls&amp;quot;);&lt;br /&gt;\r\n&amp;nbsp;banner_time();&lt;br /&gt;\r\n&amp;nbsp;printf(&amp;quot;Elemento\\tContenuto&amp;quot;);&lt;br /&gt;\r\n&amp;nbsp;//for per mostrare posizione e contenuto&lt;br /&gt;\r\n&amp;nbsp;for(i=0;i&amp;lt;n_a+n_b+n_c;i++){&lt;br /&gt;\r\n&amp;nbsp; printf(&amp;quot;\\n &amp;nbsp; %i\\t\\t &amp;nbsp; %i&amp;quot;, i+1, vettore_D[i]);&lt;br /&gt;\r\n&amp;nbsp;}&lt;br /&gt;\r\n&amp;nbsp;printf(&amp;quot;\\n----------------------------------------------------\\n&amp;quot;);&lt;br /&gt;\r\n&amp;nbsp;pari(vettore_D, n_a, n_b, n_c);&lt;br /&gt;\r\n&amp;nbsp;dispari(vettore_D, n_a, n_b, n_c);&lt;br /&gt;\r\n&amp;nbsp;printf(&amp;quot;Premi un tasto per tornare al menu...&amp;quot;);&lt;br /&gt;\r\n}&lt;br /&gt;\r\nvoid banner_time (void) {&lt;br /&gt;\r\n&amp;nbsp;//sezione dichiarativa&lt;br /&gt;\r\n&amp;nbsp;time_t t;&lt;br /&gt;\r\n&amp;nbsp;time(&amp;amp;t);&lt;br /&gt;\r\n&amp;nbsp;printf(&amp;quot;----------------------------------------------------\\n&amp;quot;);&lt;br /&gt;\r\n&amp;nbsp;printf(&amp;quot;%s&amp;quot;, ctime(&amp;amp;t));&lt;br /&gt;\r\n&amp;nbsp;printf(&amp;quot;----------------------------------------------------\\n&amp;quot;);&lt;br /&gt;\r\n}&lt;br /&gt;\r\nvoid banner_menu (void) {&lt;br /&gt;\r\n&amp;nbsp;banner_time();&lt;br /&gt;\r\n&amp;nbsp;printf(&amp;quot;1. Contenuto Vettore A\\n&amp;quot;);&lt;br /&gt;\r\n&amp;nbsp;printf(&amp;quot;2. Contenuto Vettore B\\n&amp;quot;);&lt;br /&gt;\r\n&amp;nbsp;printf(&amp;quot;3. Contenuto Vettore C\\n&amp;quot;);&lt;br /&gt;\r\n&amp;nbsp;printf(&amp;quot;4. Contenuto Vettore D\\n&amp;quot;);&lt;br /&gt;\r\n&amp;nbsp;printf(&amp;quot;5. Esci\\n&amp;quot;);&lt;br /&gt;\r\n&amp;nbsp;printf(&amp;quot;----------------------------------------------------\\n&amp;quot;);&lt;br /&gt;\r\n&amp;nbsp;printf(&amp;quot;Inserire voce del menu: &amp;quot;);&lt;br /&gt;\r\n}&lt;br /&gt;\r\nvoid banner_grazie (void) {&lt;br /&gt;\r\n&amp;nbsp;int i;&lt;br /&gt;\r\n&amp;nbsp;system(&amp;quot;cls&amp;quot;);&lt;br /&gt;\r\n&amp;nbsp;for (i=0;i&amp;lt;5;i++) {&lt;br /&gt;\r\n&amp;nbsp; printf(&amp;quot;Grazie di aver utilizzato il programma\\n&amp;quot;);&lt;br /&gt;\r\n&amp;nbsp; sleep(1);&lt;br /&gt;\r\n&amp;nbsp;}&lt;br /&gt;\r\n}&lt;/code&gt;&lt;/p&gt;\r\n', 1, '2021-08-22 23:11:15');
