(84, 8, 15, 'Terzo vettore', '1629698028_16294883297_img_c.webp', '&lt;p&gt;&lt;strong&gt;PROBLEMA&lt;/strong&gt;&lt;/p&gt;\r\n\r\n&lt;p&gt;Scrivere un programma&amp;nbsp;che acquisito da tastiera due array monodimensionali le cui dimensioni sono fornite (oppure da 6 elementi) da tastiera restituisce in output un terzo array, che contempli l&amp;#39;accodamento del secondo al primo e nello specifico,&amp;nbsp;&lt;br /&gt;\r\ngli elementi dei primi due array che si trovano in posizione dispari dovranno ritrovarsi in posizioni pari. E quelli in posizioni pari dovranno ritrovarsi in posizioni dispari.&lt;/p&gt;\r\n\r\n&lt;hr /&gt;\r\n&lt;p&gt;&lt;strong&gt;CODICE SORGENTE&lt;/strong&gt;&lt;/p&gt;\r\n\r\n&lt;p&gt;&lt;code&gt;#include &amp;lt;stdio.h&amp;gt;&lt;br /&gt;\r\n#include &amp;lt;stdlib.h&amp;gt;&lt;br /&gt;\r\n#include &amp;lt;time.h&amp;gt;&lt;/code&gt;&lt;/p&gt;\r\n\r\n&lt;p&gt;&lt;code&gt;//dichiarazione funzioni&lt;br /&gt;\r\nvoid caricamentoVettoreA();&lt;br /&gt;\r\nvoid caricamentoVettoreB();&lt;br /&gt;\r\nvoid concatenaVettori();&lt;br /&gt;\r\nvoid invertiVettore();&lt;br /&gt;\r\nvoid uscitaProgramma();&lt;br /&gt;\r\nint dimensioneVettore_A();&lt;br /&gt;\r\nint dimensioneVettore_B();&lt;br /&gt;\r\nvoid visualizzaVettore_C();&lt;br /&gt;\r\nvoid banner_time();&lt;br /&gt;\r\nvoid traccia();&lt;/code&gt;&lt;/p&gt;\r\n\r\n&lt;p&gt;&lt;code&gt;//funzione per visualizzare la traccia&lt;br /&gt;\r\nvoid traccia (void) {&lt;br /&gt;\r\n&amp;nbsp;int i;&lt;br /&gt;\r\n&amp;nbsp;for (i=5;i&amp;gt;0;i--){&lt;br /&gt;\r\n&amp;nbsp; system(&amp;quot;cls&amp;quot;);&lt;br /&gt;\r\n&amp;nbsp; banner_time();&lt;br /&gt;\r\n&amp;nbsp; printf(&amp;quot;Scrivere un programma in C che acquisito da tastiera due array monodimensionali le cui dimensioni sono&amp;quot;);&lt;br /&gt;\r\n&amp;nbsp; printf(&amp;quot;\\nfornite (oppure da 6 elementi) da tastiera restituisce in output un terzo array, che contempli l&amp;#39;accodamento&amp;quot;);&lt;br /&gt;\r\n&amp;nbsp; printf(&amp;quot;\\ndel secondo al primo e nello specifico, gli elementi dei primi due array che si trovano in posizione dispari&amp;quot;);&lt;br /&gt;\r\n&amp;nbsp; printf(&amp;quot;\\ndovranno ritrovarsi in posizioni pari. E quelli in posizioni pari dovranno ritrovarsi in posizioni dispari.&amp;quot;);&lt;br /&gt;\r\n&amp;nbsp; printf(&amp;quot;\\nSarai reinderizzato al programma tra %i&amp;quot;, i);&lt;br /&gt;\r\n&amp;nbsp; sleep(1);&lt;br /&gt;\r\n&amp;nbsp;}&lt;br /&gt;\r\n}&lt;/code&gt;&lt;/p&gt;\r\n\r\n&lt;p&gt;&lt;code&gt;//funzione per richiedere la dimensione del vettore A con controllo&lt;br /&gt;\r\nint dimensioneVettore_A () {&lt;br /&gt;\r\n&amp;nbsp;int i, x;&lt;br /&gt;\r\n&amp;nbsp;//do while per ripetere lo scanf se non &amp;egrave; conforme alla condizione&lt;br /&gt;\r\n&amp;nbsp;do {&lt;br /&gt;\r\n&amp;nbsp; system(&amp;quot;cls&amp;quot;);&lt;br /&gt;\r\n&amp;nbsp; banner_time();&lt;br /&gt;\r\n&amp;nbsp; printf(&amp;quot;Inserire il numero di elementi del vettore A: &amp;quot;);&lt;br /&gt;\r\n&amp;nbsp; scanf(&amp;quot;%i&amp;quot;, &amp;amp;x);&lt;br /&gt;\r\n&amp;nbsp; if (x&amp;lt;=0) {&lt;br /&gt;\r\n&amp;nbsp; &amp;nbsp;//attesa di 5 secondi per poter reinserire l&amp;#39;input&lt;br /&gt;\r\n&amp;nbsp; &amp;nbsp;for (i=5;i&amp;gt;0;i--){&lt;br /&gt;\r\n&amp;nbsp; &amp;nbsp; system(&amp;quot;cls&amp;quot;);&lt;br /&gt;\r\n&amp;nbsp; &amp;nbsp; printf(&amp;quot;[!] Il valore immesso non e&amp;#39; valido potrai reinserire il valore tra %i secondi&amp;quot;, i);&lt;br /&gt;\r\n&amp;nbsp; &amp;nbsp; sleep(1);&lt;br /&gt;\r\n&amp;nbsp; &amp;nbsp;}&lt;br /&gt;\r\n&amp;nbsp; &amp;nbsp;system(&amp;quot;cls&amp;quot;);&lt;br /&gt;\r\n&amp;nbsp; &amp;nbsp;printf(&amp;quot;Inserire nuovamente il valore: &amp;quot;);&lt;br /&gt;\r\n&amp;nbsp; }&lt;br /&gt;\r\n&amp;nbsp;} while(x&amp;lt;=0); //deve essere maggiore di 0 per uscire dal ciclo&lt;br /&gt;\r\n&amp;nbsp;return (x); //richiamare il risultato con input();&lt;br /&gt;\r\n}&lt;br /&gt;\r\n//funzione per richiedere la dimensione del vettore B con controllo&lt;br /&gt;\r\nint dimensioneVettore_B () {&lt;br /&gt;\r\n&amp;nbsp;int i, x;&lt;br /&gt;\r\n&amp;nbsp;//do while per ripetere lo scanf se non &amp;egrave; conforme alla condizione&lt;br /&gt;\r\n&amp;nbsp;do {&lt;br /&gt;\r\n&amp;nbsp; system(&amp;quot;cls&amp;quot;);&lt;br /&gt;\r\n&amp;nbsp; banner_time();&lt;br /&gt;\r\n&amp;nbsp; printf(&amp;quot;Inserire il numero di elementi del vettore B: &amp;quot;);&lt;br /&gt;\r\n&amp;nbsp; scanf(&amp;quot;%i&amp;quot;, &amp;amp;x);&lt;br /&gt;\r\n&amp;nbsp; if (x&amp;lt;=0) {&lt;br /&gt;\r\n&amp;nbsp; &amp;nbsp;//attesa di 5 secondi per poter reinserire l&amp;#39;input&lt;br /&gt;\r\n&amp;nbsp; &amp;nbsp;for (i=5;i&amp;gt;0;i--){&lt;br /&gt;\r\n&amp;nbsp; &amp;nbsp; system(&amp;quot;cls&amp;quot;);&lt;br /&gt;\r\n&amp;nbsp; &amp;nbsp; printf(&amp;quot;[!] Il valore immesso non e&amp;#39; valido potrai reinserire il valore tra %i secondi&amp;quot;, i);&lt;br /&gt;\r\n&amp;nbsp; &amp;nbsp; sleep(1);&lt;br /&gt;\r\n&amp;nbsp; &amp;nbsp;}&lt;br /&gt;\r\n&amp;nbsp; &amp;nbsp;system(&amp;quot;cls&amp;quot;);&lt;br /&gt;\r\n&amp;nbsp; &amp;nbsp;printf(&amp;quot;Inserire nuovamente il valore: &amp;quot;);&lt;br /&gt;\r\n&amp;nbsp; }&lt;br /&gt;\r\n&amp;nbsp;} while(x&amp;lt;=0); //deve essere maggiore di 0 per uscire dal ciclo&lt;br /&gt;\r\n&amp;nbsp;return (x); //richiamare il risultato con input();&lt;br /&gt;\r\n}&lt;br /&gt;\r\n//funzione per il caricamento del primo vettore&lt;br /&gt;\r\nvoid caricamentoVettoreA(int vettore_A[], int n_a) {&lt;br /&gt;\r\n&amp;nbsp;//sezione dichiarativa&lt;br /&gt;\r\n&amp;nbsp;int i;&lt;br /&gt;\r\n&amp;nbsp;//ciclo for per caricare il primo array&lt;br /&gt;\r\n&amp;nbsp;for (i=0;i&amp;lt;n_a;i++) {&lt;br /&gt;\r\n&amp;nbsp; printf(&amp;quot;inserisci il %i valore del Vettore A: &amp;quot;, i+1);&lt;br /&gt;\r\n&amp;nbsp; scanf(&amp;quot;%i&amp;quot;, &amp;amp;vettore_A[i]);&lt;br /&gt;\r\n&amp;nbsp;}&lt;br /&gt;\r\n}&lt;br /&gt;\r\n//funzione per il caricamento del secondo vettore&lt;br /&gt;\r\nvoid caricamentoVettoreB(int vettore_B[], int n_b) {&lt;br /&gt;\r\n&amp;nbsp;//sezione dichiarativa&lt;br /&gt;\r\n&amp;nbsp;int i;&lt;br /&gt;\r\n&amp;nbsp;//ciclo for per caricare il primo array&lt;br /&gt;\r\n&amp;nbsp;for (i=0;i&amp;lt;n_b;i++) {&lt;br /&gt;\r\n&amp;nbsp; printf(&amp;quot;inserisci il %i valore del Vettore B: &amp;quot;, i+1);&lt;br /&gt;\r\n&amp;nbsp; scanf(&amp;quot;%i&amp;quot;, &amp;amp;vettore_B[i]);&lt;br /&gt;\r\n&amp;nbsp;}&lt;br /&gt;\r\n}&lt;br /&gt;\r\n//funzione per concatenare il primo al secondo vettore&lt;br /&gt;\r\nvoid concatenaVettori(int vettore_A[], int vettore_B[], int vettore_C[], int n_a, int n_b){&lt;br /&gt;\r\n&amp;nbsp;//sezione dichiarativa&lt;br /&gt;\r\n&amp;nbsp;int j=0, i; //j=variabile d&amp;#39;appoggio per il conteggio; i=indice&lt;br /&gt;\r\n&amp;nbsp;//caricamento seconda parte del terzo vettore con il secondo vettore&lt;br /&gt;\r\n&amp;nbsp;for(i=0;i&amp;lt;n_a;i++){&lt;br /&gt;\r\n&amp;nbsp; vettore_C[j] = vettore_A[i];&lt;br /&gt;\r\n&amp;nbsp; j++;&lt;br /&gt;\r\n&amp;nbsp;}&lt;br /&gt;\r\n&amp;nbsp;//caricamento prima parte del terzo vettore con il primo vettore&lt;br /&gt;\r\n&amp;nbsp;for(i=0;i&amp;lt;n_b;i++){&lt;br /&gt;\r\n&amp;nbsp; vettore_C[j] = vettore_B[i];&lt;br /&gt;\r\n&amp;nbsp; j++;&lt;br /&gt;\r\n&amp;nbsp;}&lt;br /&gt;\r\n}&lt;br /&gt;\r\n//funzione per invertire pari e dispari&lt;br /&gt;\r\nvoid invertiVettore (int vettore_C[], int n_a, int n_b) {&lt;br /&gt;\r\n&amp;nbsp;int i=0, f=0;&lt;br /&gt;\r\n&amp;nbsp;for (i=0;i&amp;lt;n_a+n_b;i++) {&lt;br /&gt;\r\n&amp;nbsp; if (i&amp;lt;n_a+n_b-1) {&lt;br /&gt;\r\n&amp;nbsp; &amp;nbsp;f=vettore_C[i];&lt;br /&gt;\r\n&amp;nbsp; &amp;nbsp;vettore_C[i]=vettore_C[i+1];&lt;br /&gt;\r\n&amp;nbsp; &amp;nbsp;vettore_C[i+1]=f;&lt;br /&gt;\r\n&amp;nbsp; &amp;nbsp;i++;&lt;br /&gt;\r\n&amp;nbsp; }&lt;br /&gt;\r\n&amp;nbsp;}&lt;br /&gt;\r\n}&lt;br /&gt;\r\n//funzione per visualizzare il terzo vettore&lt;br /&gt;\r\nvoid visualizzaVettore_C (int vettore_C[], int n_a, int n_b){&lt;br /&gt;\r\n&amp;nbsp;//sezione dichiarativa&lt;br /&gt;\r\n&amp;nbsp;int i;&lt;br /&gt;\r\n&amp;nbsp;system(&amp;quot;cls&amp;quot;);&lt;br /&gt;\r\n&amp;nbsp;//richiamo funzione&lt;br /&gt;\r\n&amp;nbsp;banner_time();&lt;br /&gt;\r\n&amp;nbsp;printf(&amp;quot;Vettore C attualmente&amp;quot;);&lt;br /&gt;\r\n&amp;nbsp;printf(&amp;quot;\\n----------------------------------------------------\\n&amp;quot;);&lt;br /&gt;\r\n&amp;nbsp;printf(&amp;quot;Elemento\\tContenuto&amp;quot;);&lt;br /&gt;\r\n&amp;nbsp;//for per mostrare a video posizione e contenuto&lt;br /&gt;\r\n&amp;nbsp;for (i=0;i&amp;lt;n_a+n_b;i++) {&lt;br /&gt;\r\n&amp;nbsp; printf(&amp;quot;\\n &amp;nbsp; %i\\t\\t &amp;nbsp; %i&amp;quot;, i+1, vettore_C[i]);&lt;br /&gt;\r\n&amp;nbsp;}&lt;br /&gt;\r\n&amp;nbsp;printf(&amp;quot;\\n----------------------------------------------------\\n&amp;quot;);&lt;br /&gt;\r\n}&lt;br /&gt;\r\n//funzione ringraziamento&lt;br /&gt;\r\nvoid uscitaProgramma (void) {&lt;br /&gt;\r\n&amp;nbsp;//sezione dichiarativa&lt;br /&gt;\r\n&amp;nbsp;int i;&lt;br /&gt;\r\n&amp;nbsp;printf(&amp;quot;Premi un tasto per chiudere il programma...&amp;quot;);&lt;br /&gt;\r\n&amp;nbsp;getch();&lt;br /&gt;\r\n&amp;nbsp;system(&amp;quot;cls&amp;quot;);&lt;br /&gt;\r\n&amp;nbsp;for (i=0;i&amp;lt;5;i++) {&lt;br /&gt;\r\n&amp;nbsp; printf(&amp;quot;Grazie di aver utilizzato il programma\\n&amp;quot;);&lt;br /&gt;\r\n&amp;nbsp; sleep(1);&lt;br /&gt;\r\n&amp;nbsp;}&lt;br /&gt;\r\n}&lt;br /&gt;\r\n//funzione per visualizzare l&amp;#39;orario&lt;br /&gt;\r\nvoid banner_time (void) {&lt;br /&gt;\r\n&amp;nbsp;//sezione dichiarativa&lt;br /&gt;\r\n&amp;nbsp;time_t t;&lt;br /&gt;\r\n&amp;nbsp;time(&amp;amp;t);&lt;br /&gt;\r\n&amp;nbsp;printf(&amp;quot;----------------------------------------------------\\n&amp;quot;);&lt;br /&gt;\r\n&amp;nbsp;printf(&amp;quot;%s&amp;quot;, ctime(&amp;amp;t));&lt;br /&gt;\r\n&amp;nbsp;printf(&amp;quot;----------------------------------------------------\\n&amp;quot;);&lt;br /&gt;\r\n}&lt;br /&gt;\r\n//funzione principale del programma&lt;br /&gt;\r\nint main(int argc, char *argv[]) {&lt;br /&gt;\r\n&amp;nbsp;//sezione dichiarativa&lt;br /&gt;\r\n&amp;nbsp;int n_a, n_b; //n_a=numero di elementi del vettore A; n_b=numero di elementi del vettore B&lt;br /&gt;\r\n&amp;nbsp;traccia();&lt;br /&gt;\r\n&amp;nbsp;n_a=dimensioneVettore_A();&lt;br /&gt;\r\n&amp;nbsp;//dichiarazione vettore&lt;br /&gt;\r\n&amp;nbsp;int vettore_A[n_a];&lt;br /&gt;\r\n&amp;nbsp;//richiamo funzione&lt;br /&gt;\r\n&amp;nbsp;caricamentoVettoreA(vettore_A, n_a);&lt;br /&gt;\r\n&amp;nbsp;n_b=dimensioneVettore_B();&lt;br /&gt;\r\n&amp;nbsp;//dichiarazione vettore&lt;br /&gt;\r\n&amp;nbsp;int vettore_B[n_b];&lt;br /&gt;\r\n&amp;nbsp;//richiamo funzione&lt;br /&gt;\r\n&amp;nbsp;caricamentoVettoreB(vettore_B, n_b);&lt;br /&gt;\r\n&amp;nbsp;//dichiarazione vettore&lt;br /&gt;\r\n&amp;nbsp;int vettore_C[n_a+n_b];&lt;br /&gt;\r\n&amp;nbsp;//richiamo funzione&lt;br /&gt;\r\n&amp;nbsp;concatenaVettori(vettore_A, vettore_B, vettore_C, n_a, n_b);&lt;br /&gt;\r\n&amp;nbsp;visualizzaVettore_C(vettore_C, n_a, n_b);&lt;br /&gt;\r\n&amp;nbsp;printf(&amp;quot;Premi un tasto per effettura il cambiamento. . .&amp;quot;);&lt;br /&gt;\r\n&amp;nbsp;getch();&lt;br /&gt;\r\n&amp;nbsp;invertiVettore(vettore_C, n_a, n_b);&lt;br /&gt;\r\n&amp;nbsp;visualizzaVettore_C(vettore_C, n_a, n_b);&lt;br /&gt;\r\n&amp;nbsp;uscitaProgramma();&lt;br /&gt;\r\n&amp;nbsp;system(&amp;quot;PAUSE&amp;quot;);&lt;br /&gt;\r\n&amp;nbsp;return 0;&lt;br /&gt;\r\n}&lt;/code&gt;&lt;/p&gt;\r\n', 1, '2021-08-23 07:53:48'),
(85, 8, 15, 'Nuovo elemento in testa e in coda al vettore', '1629698288_1629488397_img_c.webp', '&lt;p&gt;&lt;strong&gt;PROBLEMA&lt;/strong&gt;&lt;/p&gt;\r\n\r\n&lt;p&gt;Dopo aver caricato in memoria un vettore di interi con dimensione d (con d inserito da tastiera), inserire &amp;ldquo;in coda&amp;rdquo; ed in &amp;ldquo;testa&amp;rdquo;al vettore un nuovo elemento. Scrivere il vettore iniziale e il vettore modificato&lt;/p&gt;\r\n\r\n&lt;hr /&gt;\r\n&lt;p&gt;&lt;strong&gt;DIAGRAMMA A BLOCCHI&lt;/strong&gt;&lt;/p&gt;\r\n\r\n&lt;p&gt;Clicca qui per visualizzare il&amp;nbsp;&lt;a href=&quot;../assets/images/posts/body/1883950940.svg&quot; target=&quot;_blank&quot;&gt;Diagramma a blocchi&lt;/a&gt;.&lt;/p&gt;\r\n\r\n&lt;hr /&gt;\r\n&lt;p&gt;&lt;strong&gt;CODICE SORGENTE&lt;/strong&gt;&lt;/p&gt;\r\n\r\n&lt;p&gt;&lt;code&gt;#include &amp;lt;stdio.h&amp;gt;&lt;br /&gt;\r\n#include &amp;lt;stdlib.h&amp;gt;&lt;br /&gt;\r\n#include &amp;lt;time.h&amp;gt;&lt;/code&gt;&lt;/p&gt;\r\n\r\n&lt;p&gt;&lt;code&gt;void traccia();&lt;br /&gt;\r\nint input();&lt;br /&gt;\r\nvoid caricamentoVettoreA();&lt;br /&gt;\r\nvoid caricamentoVettoreB();&lt;br /&gt;\r\nint valore();&lt;br /&gt;\r\nint valore_2();&lt;br /&gt;\r\nvoid visualizzaVettore_A();&lt;br /&gt;\r\nvoid visualizzaVettore_B();&lt;br /&gt;\r\nvoid banner_time();&lt;br /&gt;\r\nvoid uscitaProgramma();&lt;br /&gt;\r\nint main(int argc, char *argv[]) {&lt;br /&gt;\r\n&amp;nbsp;//sezione dichiarativa&lt;br /&gt;\r\n&amp;nbsp;int d, i, nuovo_elemento, nuovo_elemento_2; //d=numero elementi; i=indice; nuovo_elemento=elemento in testa; nuovo_elemento_2=elemento in coda&lt;br /&gt;\r\n&amp;nbsp;traccia();&lt;br /&gt;\r\n&amp;nbsp;d=input();&lt;br /&gt;\r\n&amp;nbsp;int array_a[d];&lt;br /&gt;\r\n&amp;nbsp;int array_b[d+1];&lt;br /&gt;\r\n&amp;nbsp;caricamentoVettoreA(array_a, d);&lt;br /&gt;\r\n&amp;nbsp;nuovo_elemento=valore();&lt;br /&gt;\r\n&amp;nbsp;nuovo_elemento_2=valore_2();&amp;nbsp;&lt;br /&gt;\r\n&amp;nbsp;caricamentoVettoreB(array_a, array_b, d, nuovo_elemento, nuovo_elemento_2);&lt;br /&gt;\r\n&amp;nbsp;visualizzaVettore_A(array_a, d);&lt;br /&gt;\r\n&amp;nbsp;visualizzaVettore_B(array_b, d);&lt;br /&gt;\r\n&amp;nbsp;uscitaProgramma();&lt;br /&gt;\r\n&amp;nbsp;system(&amp;quot;PAUSE&amp;quot;);&lt;br /&gt;\r\n&amp;nbsp;return 0;&lt;br /&gt;\r\n}&lt;br /&gt;\r\n//funzione per visualizzare la traccia&lt;br /&gt;\r\nvoid traccia (void) {&lt;br /&gt;\r\n&amp;nbsp;int i;&lt;br /&gt;\r\n&amp;nbsp;for (i=10;i&amp;gt;0;i--){&lt;br /&gt;\r\n&amp;nbsp; system(&amp;quot;cls&amp;quot;);&lt;br /&gt;\r\n&amp;nbsp; banner_time();&lt;br /&gt;\r\n&amp;nbsp; printf(&amp;quot;Dopo aver caricato in memoria un vettore di interi con dimensione d (con d inserito da tastiera),&amp;quot;);&lt;br /&gt;\r\n&amp;nbsp; printf(&amp;quot;\\ninserire &amp;ldquo;in coda&amp;rdquo; ed in &amp;ldquo;testa&amp;rdquo;al vettore un nuovo elemento. Scrivere il vettore iniziale e il vettore modificato.&amp;quot;);&lt;br /&gt;\r\n&amp;nbsp; printf(&amp;quot;\\n\\nSarai reinderizzato al programma tra %i&amp;quot;, i);&lt;br /&gt;\r\n&amp;nbsp; sleep(1);&lt;br /&gt;\r\n&amp;nbsp;}&lt;br /&gt;\r\n}&lt;br /&gt;\r\n//sezione controllo dell&amp;#39;input per la creazione del vettore&lt;br /&gt;\r\nint input () {&lt;br /&gt;\r\n&amp;nbsp;//sezione dichiarativa&lt;br /&gt;\r\n&amp;nbsp;int i, x; //i=contatore; x=numero di elementi&lt;br /&gt;\r\n&amp;nbsp;//do while per ripetere lo scanf se non &amp;egrave; conforme alla condizione&lt;br /&gt;\r\n&amp;nbsp;do {&lt;br /&gt;\r\n&amp;nbsp; system(&amp;quot;cls&amp;quot;);&lt;br /&gt;\r\n&amp;nbsp; banner_time();&lt;br /&gt;\r\n&amp;nbsp; printf(&amp;quot;Inserire numero di elementi del vettore:&amp;quot;);&lt;br /&gt;\r\n&amp;nbsp; scanf(&amp;quot;%i&amp;quot;, &amp;amp;x);&lt;br /&gt;\r\n&amp;nbsp; if (x&amp;lt;=0) {&lt;br /&gt;\r\n&amp;nbsp; &amp;nbsp;//attesa di 5 secondi per poter reinserire l&amp;#39;input&lt;br /&gt;\r\n&amp;nbsp; &amp;nbsp;for (i=5;i&amp;gt;0;i--){&lt;br /&gt;\r\n&amp;nbsp; &amp;nbsp; system(&amp;quot;cls&amp;quot;);&lt;br /&gt;\r\n&amp;nbsp; &amp;nbsp; printf(&amp;quot;[!] Il valore immesso non e&amp;#39; valido potrai reinserire il valore tra %i secondi&amp;quot;, i);&lt;br /&gt;\r\n&amp;nbsp; &amp;nbsp; sleep(1);&lt;br /&gt;\r\n&amp;nbsp; &amp;nbsp;}&lt;br /&gt;\r\n&amp;nbsp; }&lt;br /&gt;\r\n&amp;nbsp;} while(x&amp;lt;=0); //deve essere maggiore di 0 per uscire dal ciclo&lt;br /&gt;\r\n&amp;nbsp;return (x); //richiamare il risultato con input();&lt;br /&gt;\r\n}&lt;br /&gt;\r\n//caricamento del vettore numero 1 da parte dell&amp;#39;utente&lt;br /&gt;\r\nvoid caricamentoVettoreA(int array_a[], int d) {&lt;br /&gt;\r\n&amp;nbsp;//sezione dichiarativa&lt;br /&gt;\r\n&amp;nbsp;int i;&lt;br /&gt;\r\n&amp;nbsp;system(&amp;quot;cls&amp;quot;);&lt;br /&gt;\r\n&amp;nbsp;banner_time();&lt;br /&gt;\r\n&amp;nbsp;//ciclo for per richiedere all&amp;#39;utente il contenuto degli elementi&lt;br /&gt;\r\n&amp;nbsp;for (i=0;i&amp;lt;d;i++) {&lt;br /&gt;\r\n&amp;nbsp; printf(&amp;quot;inserisci il %i valore del Vettore A: &amp;quot;, i+1);&lt;br /&gt;\r\n&amp;nbsp; scanf(&amp;quot;%i&amp;quot;, &amp;amp;array_a[i]);&lt;br /&gt;\r\n&amp;nbsp;}&lt;br /&gt;\r\n}&lt;br /&gt;\r\n//richiesta del nuovo valore da mettere in testa&lt;br /&gt;\r\nint valore () {&lt;br /&gt;\r\n&amp;nbsp;//sezione dichirativa&lt;br /&gt;\r\n&amp;nbsp;int x;&lt;br /&gt;\r\n&amp;nbsp;system(&amp;quot;cls&amp;quot;);&lt;br /&gt;\r\n&amp;nbsp;banner_time();&lt;br /&gt;\r\n&amp;nbsp;printf(&amp;quot;Inserire nuovo valore in testa: &amp;quot;);&lt;br /&gt;\r\n&amp;nbsp;scanf(&amp;quot;%i&amp;quot;, &amp;amp;x);&amp;nbsp;&lt;br /&gt;\r\n&amp;nbsp;return (x);&lt;br /&gt;\r\n}&lt;br /&gt;\r\nint valore_2 () {&lt;br /&gt;\r\n&amp;nbsp;//sezione dichirativa&lt;br /&gt;\r\n&amp;nbsp;int x;&lt;br /&gt;\r\n&amp;nbsp;system(&amp;quot;cls&amp;quot;);&lt;br /&gt;\r\n&amp;nbsp;banner_time();&lt;br /&gt;\r\n&amp;nbsp;printf(&amp;quot;Inserire nuovo valore in coda: &amp;quot;);&lt;br /&gt;\r\n&amp;nbsp;scanf(&amp;quot;%i&amp;quot;, &amp;amp;x);&amp;nbsp;&lt;br /&gt;\r\n&amp;nbsp;return (x);&lt;br /&gt;\r\n}&lt;br /&gt;\r\n//caricamento del vettore numero 2 con in testa il nuovo elemento&lt;br /&gt;\r\nvoid caricamentoVettoreB(int array_a[], int array_b[], int d, int nuovo_elemento, int nuovo_elemento_2) {&lt;br /&gt;\r\n&amp;nbsp;int i;&lt;br /&gt;\r\n&amp;nbsp;array_b[0]=nuovo_elemento;&lt;br /&gt;\r\n&amp;nbsp;for (i=1;i&amp;lt;d+1;i++) {&lt;br /&gt;\r\n&amp;nbsp; array_b[i]=array_a[i-1];&lt;br /&gt;\r\n&amp;nbsp;}&lt;br /&gt;\r\n&amp;nbsp;array_b[d+1]=nuovo_elemento_2;&lt;br /&gt;\r\n}&lt;br /&gt;\r\n//funzione per visualizzare il primo vettore&lt;br /&gt;\r\nvoid visualizzaVettore_A (int array_a[], int d){&lt;br /&gt;\r\n&amp;nbsp;//sezione dichiarativa&lt;br /&gt;\r\n&amp;nbsp;int i;&lt;br /&gt;\r\n&amp;nbsp;system(&amp;quot;cls&amp;quot;);&lt;br /&gt;\r\n&amp;nbsp;//richiamo funzione&lt;br /&gt;\r\n&amp;nbsp;banner_time();&lt;br /&gt;\r\n&amp;nbsp;printf(&amp;quot;\\t\\tVettore A&amp;quot;);&lt;br /&gt;\r\n&amp;nbsp;printf(&amp;quot;\\n----------------------------------------------------\\n&amp;quot;);&lt;br /&gt;\r\n&amp;nbsp;printf(&amp;quot;\\tElemento\\tContenuto&amp;quot;);&lt;br /&gt;\r\n&amp;nbsp;//for per mostrare a video posizione e contenuto&lt;br /&gt;\r\n&amp;nbsp;for (i=0;i&amp;lt;d;i++) {&lt;br /&gt;\r\n&amp;nbsp; printf(&amp;quot;\\n\\t &amp;nbsp; %i\\t\\t &amp;nbsp; %i&amp;quot;, i+1, array_a[i]);&lt;br /&gt;\r\n&amp;nbsp;}&lt;br /&gt;\r\n&amp;nbsp;printf(&amp;quot;\\n----------------------------------------------------\\n&amp;quot;);&lt;br /&gt;\r\n}&lt;br /&gt;\r\n//funzione per visualizzare il secondo vettore&lt;br /&gt;\r\nvoid visualizzaVettore_B (int array_b[], int d){&lt;br /&gt;\r\n&amp;nbsp;//sezione dichiarativa&lt;br /&gt;\r\n&amp;nbsp;int i;&lt;br /&gt;\r\n&amp;nbsp;//system(&amp;quot;cls&amp;quot;);&lt;br /&gt;\r\n&amp;nbsp;//richiamo funzione&lt;br /&gt;\r\n&amp;nbsp;//banner_time();&lt;br /&gt;\r\n&amp;nbsp;p