(86, 8, 15, 'Somma di due vettori', '1629698411_16294883297_img_c.webp', '&lt;p&gt;&lt;strong&gt;PROBLEMA&lt;/strong&gt;&lt;/p&gt;\r\n\r\n&lt;p&gt;Caricare in modo randomico due Vettori A e B con N elementi (uguale lunghezza) sommare gli elementi di A e B in un vettore 3.&lt;/p&gt;\r\n\r\n&lt;hr /&gt;\r\n&lt;p&gt;&lt;strong&gt;CODICE SORGENTE&lt;/strong&gt;&lt;/p&gt;\r\n\r\n&lt;p&gt;&lt;code&gt;#include &amp;lt;stdio.h&amp;gt;&lt;br /&gt;\r\n#include &amp;lt;stdlib.h&amp;gt;&lt;br /&gt;\r\n#include &amp;lt;time.h&amp;gt;&lt;br /&gt;\r\n#include &amp;lt;math.h&amp;gt;&lt;/code&gt;&lt;/p&gt;\r\n\r\n&lt;p&gt;&lt;code&gt;int input();&lt;br /&gt;\r\nvoid casuale();&lt;br /&gt;\r\nvoid banner_menu();&lt;br /&gt;\r\nvoid visualizzaVettore_A();&lt;br /&gt;\r\nvoid visualizzaVettore_B();&lt;br /&gt;\r\nvoid visualizzaVettore_C_divisione();&lt;br /&gt;\r\nvoid visualizzaVettore_C_sottrazione();&lt;br /&gt;\r\nvoid visualizzaVettore_C_addizione();&lt;br /&gt;\r\nvoid visualizzaVettore_C_moltiplicazione();&lt;br /&gt;\r\nvoid somma_vettori();&lt;br /&gt;\r\nvoid differenza_vettori();&lt;br /&gt;\r\nvoid moltiplicazione_vettori();&lt;br /&gt;\r\nvoid divisione_vettori();&lt;br /&gt;\r\nvoid banner_grazie();&lt;br /&gt;\r\nvoid banner_time();&lt;br /&gt;\r\n//funzione principale&lt;br /&gt;\r\nint main(int argc, char *argv[]) {&lt;br /&gt;\r\n&amp;nbsp;//enum per il menu&amp;nbsp;&lt;br /&gt;\r\n&amp;nbsp;enum scelta {&lt;br /&gt;\r\n&amp;nbsp; visualizza_vettore_a_menu=1,&lt;br /&gt;\r\n&amp;nbsp; visualizza_vettore_b_menu,&lt;br /&gt;\r\n&amp;nbsp; visualizza_somma_menu,&lt;br /&gt;\r\n&amp;nbsp; visualizza_differenza_menu,&lt;br /&gt;\r\n&amp;nbsp; visualizza_moltiplicazione_menu,&lt;br /&gt;\r\n&amp;nbsp; visualizza_divisione_menu,&lt;br /&gt;\r\n&amp;nbsp; uscita_menu,&lt;br /&gt;\r\n&amp;nbsp;};&lt;br /&gt;\r\n&amp;nbsp;//sezione dichiarativa&lt;br /&gt;\r\n&amp;nbsp;int a, scelta; //a=numero di elementi per array; scelta=scelta per il menu&lt;br /&gt;\r\n&amp;nbsp;a=input();&lt;br /&gt;\r\n&amp;nbsp;//dichiarazione array&lt;br /&gt;\r\n&amp;nbsp;int array_a[a];&lt;br /&gt;\r\n&amp;nbsp;int array_b[a];&lt;br /&gt;\r\n&amp;nbsp;float array_c[a];&lt;br /&gt;\r\n&amp;nbsp;//randomizzazione dei numeri&lt;br /&gt;\r\n&amp;nbsp;casuale(array_a, array_b, a);&lt;br /&gt;\r\n&amp;nbsp;//ciclo do while per il reinserimento delle opzione del menu&lt;br /&gt;\r\n&amp;nbsp;do {&lt;br /&gt;\r\n&amp;nbsp; banner_menu();&lt;br /&gt;\r\n&amp;nbsp; scanf(&amp;quot;%i&amp;quot;, &amp;amp;scelta);&lt;br /&gt;\r\n&amp;nbsp; switch (scelta) {&lt;br /&gt;\r\n&amp;nbsp; &amp;nbsp;case visualizza_vettore_a_menu:&lt;br /&gt;\r\n&amp;nbsp; &amp;nbsp; visualizzaVettore_A(array_a, a);&lt;br /&gt;\r\n&amp;nbsp; &amp;nbsp; getch();&lt;br /&gt;\r\n&amp;nbsp; &amp;nbsp; break;&lt;br /&gt;\r\n&amp;nbsp; &amp;nbsp;case visualizza_vettore_b_menu:&lt;br /&gt;\r\n&amp;nbsp; &amp;nbsp; visualizzaVettore_B(array_b, a);&lt;br /&gt;\r\n&amp;nbsp; &amp;nbsp; getch();&lt;br /&gt;\r\n&amp;nbsp; &amp;nbsp; break;&lt;br /&gt;\r\n&amp;nbsp; &amp;nbsp;case visualizza_somma_menu:&lt;br /&gt;\r\n&amp;nbsp; &amp;nbsp; somma_vettori(array_a, array_b, array_c, a);&lt;br /&gt;\r\n&amp;nbsp; &amp;nbsp; visualizzaVettore_C_addizione(array_a, array_b, array_c, a);&lt;br /&gt;\r\n&amp;nbsp; &amp;nbsp; getch();&lt;br /&gt;\r\n&amp;nbsp; &amp;nbsp; break;&lt;br /&gt;\r\n&amp;nbsp; &amp;nbsp;case visualizza_differenza_menu:&lt;br /&gt;\r\n&amp;nbsp; &amp;nbsp; differenza_vettori(array_a, array_b, array_c, a);&lt;br /&gt;\r\n&amp;nbsp; &amp;nbsp; visualizzaVettore_C_sottrazione(array_a, array_b, array_c, a);&lt;br /&gt;\r\n&amp;nbsp; &amp;nbsp; getch();&lt;br /&gt;\r\n&amp;nbsp; &amp;nbsp; break;&lt;br /&gt;\r\n&amp;nbsp; &amp;nbsp;case visualizza_moltiplicazione_menu:&lt;br /&gt;\r\n&amp;nbsp; &amp;nbsp; moltiplicazione_vettori(array_a, array_b, array_c, a);&lt;br /&gt;\r\n&amp;nbsp; &amp;nbsp; visualizzaVettore_C_moltiplicazione(array_a, array_b, array_c, a);&lt;br /&gt;\r\n&amp;nbsp; &amp;nbsp; getch();&lt;br /&gt;\r\n&amp;nbsp; &amp;nbsp; break;&lt;br /&gt;\r\n&amp;nbsp; &amp;nbsp;case visualizza_divisione_menu:&lt;br /&gt;\r\n&amp;nbsp; &amp;nbsp; divisione_vettori(array_a, array_b, array_c, a);&lt;br /&gt;\r\n&amp;nbsp; &amp;nbsp; visualizzaVettore_C_divisione(array_a, array_b, array_c, a);&lt;br /&gt;\r\n&amp;nbsp; &amp;nbsp; getch();&lt;br /&gt;\r\n&amp;nbsp; &amp;nbsp; break;&lt;br /&gt;\r\n&amp;nbsp; &amp;nbsp;case uscita_menu:&lt;br /&gt;\r\n&amp;nbsp; &amp;nbsp; banner_grazie();&lt;br /&gt;\r\n&amp;nbsp; &amp;nbsp; break;&lt;br /&gt;\r\n&amp;nbsp; &amp;nbsp;default:&lt;br /&gt;\r\n&amp;nbsp; &amp;nbsp; break;&lt;br /&gt;\r\n&amp;nbsp; }&lt;br /&gt;\r\n&amp;nbsp;}while((scelta&amp;gt;=0) &amp;amp;&amp;amp; (scelta&amp;lt;7));&lt;br /&gt;\r\n&amp;nbsp;system(&amp;quot;PAUSE&amp;quot;);&lt;br /&gt;\r\n&amp;nbsp;return 0;&lt;br /&gt;\r\n}&lt;br /&gt;\r\nint input () {&lt;br /&gt;\r\n&amp;nbsp;//sezione dichiarativa&lt;br /&gt;\r\n&amp;nbsp;int i, x; //i=contatore; x=numero di elementi&lt;br /&gt;\r\n&amp;nbsp;//do while per ripetere lo scanf se non &amp;egrave; conforme alla condizione&lt;br /&gt;\r\n&amp;nbsp;do {&lt;br /&gt;\r\n&amp;nbsp; system(&amp;quot;cls&amp;quot;);&lt;br /&gt;\r\n&amp;nbsp; printf(&amp;quot;Inserire numero di elementi per vettore:&amp;quot;);&lt;br /&gt;\r\n&amp;nbsp; scanf(&amp;quot;%i&amp;quot;, &amp;amp;x);&lt;br /&gt;\r\n&amp;nbsp; if (x&amp;lt;=0) {&lt;br /&gt;\r\n&amp;nbsp; &amp;nbsp;//attesa di 5 secondi per poter reinserire l&amp;#39;input&lt;br /&gt;\r\n&amp;nbsp; &amp;nbsp;for (i=5;i&amp;gt;0;i--){&lt;br /&gt;\r\n&amp;nbsp; &amp;nbsp; system(&amp;quot;cls&amp;quot;);&lt;br /&gt;\r\n&amp;nbsp; &amp;nbsp; printf(&amp;quot;[!] Il valore immesso non e&amp;#39; valido potrai reinserire il valore tra %i secondi&amp;quot;, i);&lt;br /&gt;\r\n&amp;nbsp; &amp;nbsp; sleep(1);&lt;br /&gt;\r\n&amp;nbsp; &amp;nbsp;}&lt;br /&gt;\r\n&amp;nbsp; }&lt;br /&gt;\r\n&amp;nbsp;} while(x&amp;lt;=0); //deve essere maggiore di 0 per uscire