 /&gt;\r\n&amp;nbsp;printf(&amp;quot;\\n------------------------------------------------------------------------------\\n&amp;quot;);&lt;br /&gt;\r\n&amp;nbsp;printf(&amp;quot;Premi un tasto per tornare al menu...&amp;quot;);&lt;br /&gt;\r\n}&lt;br /&gt;\r\nvoid visualizzaVettore_C_addizione (int array_a[], int array_b[], float array_c[], int a){&lt;br /&gt;\r\n&amp;nbsp;//sezione dichiarativa&lt;br /&gt;\r\n&amp;nbsp;int i;&lt;br /&gt;\r\n&amp;nbsp;system(&amp;quot;cls&amp;quot;);&lt;br /&gt;\r\n&amp;nbsp;banner_time();&lt;br /&gt;\r\n&amp;nbsp;printf(&amp;quot;\\t\\t\\t\\tAddizione&amp;quot;);&lt;br /&gt;\r\n&amp;nbsp;printf(&amp;quot;\\n------------------------------------------------------------------------------\\n&amp;quot;);&lt;br /&gt;\r\n&amp;nbsp;//richiaamo funzione&lt;br /&gt;\r\n&amp;nbsp;printf(&amp;quot;\\tElemento\\tVettore A\\tVettore B\\tVettore C&amp;quot;);&lt;br /&gt;\r\n&amp;nbsp;//for per mostrare a video posizione e contenuto&lt;br /&gt;\r\n&amp;nbsp;for (i=0;i&amp;lt;a;i++) {&lt;br /&gt;\r\n&amp;nbsp; printf(&amp;quot;\\n\\t &amp;nbsp; %i\\t\\t &amp;nbsp; %i\\t\\t &amp;nbsp; %i\\t\\t &amp;nbsp; %.0f&amp;quot;, i+1, array_a[i], array_b[i], array_c[i]);&lt;br /&gt;\r\n&amp;nbsp;}&lt;br /&gt;\r\n&amp;nbsp;printf(&amp;quot;\\n------------------------------------------------------------------------------\\n&amp;quot;);&lt;br /&gt;\r\n&amp;nbsp;printf(&amp;quot;Premi un tasto per tornare al menu...&amp;quot;);&lt;br /&gt;\r\n}&lt;br /&gt;\r\nvoid visualizzaVettore_C_moltiplicazione (int array_a[], int array_b[], float array_c[], int a){&lt;br /&gt;\r\n&amp;nbsp;//sezione dichiarativa&lt;br /&gt;\r\n&amp;nbsp;int i;&lt;br /&gt;\r\n&amp;nbsp;system(&amp;quot;cls&amp;quot;);&lt;br /&gt;\r\n&amp;nbsp;banner_time();&lt;br /&gt;\r\n&amp;nbsp;printf(&amp;quot;\\t\\t\\t\\tMoltiplicazione&amp;quot;);&lt;br /&gt;\r\n&amp;nbsp;printf(&amp;quot;\\n------------------------------------------------------------------------------\\n&amp;quot;);&lt;br /&gt;\r\n&amp;nbsp;//richiaamo funzione&lt;br /&gt;\r\n&amp;nbsp;printf(&amp;quot;\\tElemento\\tVettore A\\tVettore B\\tVettore C&amp;quot;);&lt;br /&gt;\r\n&amp;nbsp;//for per mostrare a video posizione e contenuto&lt;br /&gt;\r\n&amp;nbsp;for (i=0;i&amp;lt;a;i++) {&lt;br /&gt;\r\n&amp;nbsp; printf(&amp;quot;\\n\\t &amp;nbsp; %i\\t\\t &amp;nbsp; %i\\t\\t &amp;nbsp; %i\\t\\t &amp;nbsp; %.0f&amp;quot;, i+1, array_a[i], array_b[i], array_c[i]);&lt;br /&gt;\r\n&amp;nbsp;}&lt;br /&gt;\r\n&amp;nbsp;printf(&amp;quot;\\n------------------------------------------------------------------------------\\n&amp;quot;);&lt;br /&gt;\r\n&amp;nbsp;printf(&amp;quot;Premi un tasto per tornare al menu...&amp;quot;);&lt;br /&gt;\r\n}&lt;br /&gt;\r\nvoid visualizzaVettore_C_divisione (int array_a[], int array_b[], float array_c[], int a){&lt;br /&gt;\r\n&amp;nbsp;//sezione dichiarativa&lt;br /&gt;\r\n&amp;nbsp;int i;&lt;br /&gt;\r\n&amp;nbsp;system(&amp;quot;cls&amp;quot;);&lt;br /&gt;\r\n&amp;nbsp;banner_time();&lt;br /&gt;\r\n&amp;nbsp;printf(&amp;quot;\\t\\t\\t\\tDivisione&amp;quot;);&lt;br /&gt;\r\n&amp;nbsp;printf(&amp;quot;\\n------------------------------------------------------------------------------\\n&amp;quot;);&lt;br /&gt;\r\n&amp;nbsp;//richiaamo funzione&lt;br /&gt;\r\n&amp;nbsp;printf(&amp;quot;\\tElemento\\tVettore A\\tVettore B\\tVettore C&amp;quot;);&lt;br /&gt;\r\n&amp;nbsp;//for per mostrare a video posizione e contenuto&lt;br /&gt;\r\n&amp;nbsp;for (i=0;i&amp;lt;a;i++) {&lt;br /&gt;\r\n&amp;nbsp; printf(&amp;quot;\\n\\t &amp;nbsp; %i\\t\\t &amp;nbsp; %i\\t\\t &amp;nbsp; %i\\t\\t &amp;nbsp; %.0f&amp;quot;, i+1, array_a[i], array_b[i], array_c[i]);&lt;br /&gt;\r\n&amp;nbsp;}&lt;br /&gt;\r\n&amp;nbsp;printf(&amp;quot;\\n------------------------------------------------------------------------------\\n&amp;quot;);&lt;br /&gt;\r\n&amp;nbsp;printf(&amp;quot;Premi un tasto per tornare al menu...&amp;quot;);&lt;br /&gt;\r\n}&lt;br /&gt;\r\nvoid somma_vettori (int array_a[], int array_b[], float array_c[], int a) {&lt;br /&gt;\r\n&amp;nbsp;//sezione dichiarativa&lt;br /&gt;\r\n&amp;nbsp;int i;&lt;br /&gt;\r\n&amp;nbsp;//ciclo for per ripetere l&amp;#39;operazione per tutti gli elementi&lt;br /&gt;\r\n&amp;nbsp;for (i=0;i&amp;lt;a;i++) {&lt;br /&gt;\r\n&amp;nbsp; if (array_a[i]&amp;gt;=0 &amp;amp;&amp;amp; array_b[i]&amp;gt;=0) { //segno concordo &amp;quot;+&amp;quot;&lt;br /&gt;\r\n&amp;nbsp; &amp;nbsp;array_c[i]=(float)array_a[i]+array_b[i];&lt;br /&gt;\r\n&amp;nbsp; }&lt;br /&gt;\r\n&amp;nbsp; else if (array_a[i]&amp;lt;0 &amp;amp;&amp;amp; array_b[i]&amp;lt;0){ //segno concordo &amp;quot;-&amp;quot;&lt;br /&gt;\r\n&amp;nbsp; &amp;nbsp;array_c[i]=(float)(abs(array_a[i])+(abs(array_b[i])));&lt;br /&gt;\r\n&amp;nbsp; &amp;nbsp;array_c[i]=array_c[i]-(array_c[i]*2);&lt;br /&gt;\r\n&amp;nbsp; }&lt;br /&gt;\r\n&amp;nbsp; else { //segno discordo&lt;br /&gt;\r\n&amp;nbsp; &amp;nbsp;if ((abs(array_a[i]))&amp;gt;(abs(array_b[i]))) { //se valore A &amp;egrave; maggiore B&lt;br /&gt;\r\n&amp;nbsp; &amp;nbsp; array_c[i]=(float)(abs(array_a[i])-(abs(array_b[i])));&lt;br /&gt;\r\n&amp;nbsp; &amp;nbsp; if (array_a[i]&amp;gt;=0) { //se &amp;egrave; il valore A ha il segno &amp;quot;+&amp;quot;&lt;br /&gt;\r\n&amp;nbsp; &amp;nbsp; &amp;nbsp;if (array_c[i]&amp;lt;0) { //se C &amp;egrave; minore di 0 renderlo positivo&lt;br /&gt;\r\n&amp;nbsp; &amp;nbsp; &amp;nbsp; array_c[i]=array_c[i]+(array_c[i]*2);&lt;br /&gt;\r\n&amp;nbsp; &amp;nbsp; &amp;nbsp;}&lt;br /&gt;\r\n&amp;nbsp; &amp;nbsp; }&lt;br /&gt;\r\n&amp;nbsp; &amp;nbsp; else { //se &amp;egrave; il valore A ha il segno &amp;quot;-&amp;quot;&lt;br /&gt;\r\n&amp;nbsp; &amp;nbsp; &amp;nbsp;if (array_c[i]&amp;gt;=0) { //se C &amp;egrave; maggiore di 0 renderlo positivo&lt;br /&gt;\r\n&amp;nbsp; &amp;nbsp; &amp;nbsp; array_c[i]=array_c[i]-(array_c[i]*2);&lt;br /&gt;\r\n&amp;nbsp; &amp;nbsp; &amp;nbsp;}&lt;br /&gt;\r\n&amp;nbsp; &amp;nbsp; }&lt;br /&gt;\r\n&amp;nbsp; &amp;nbsp;}&lt;br /&gt;\r\n&amp;nbsp; &amp;nbsp;else { //se valore B &amp;egrave; maggiore A&lt;br /&gt;\r\n&amp;nbsp; &amp;nbsp; array_c[i]=(float)(abs(array_b[i])-(abs(array_a[i]))); //sottrazione tra valori assoluti&lt;br /&gt;\r\n&amp;nbsp; &amp;nbsp; if (array_b[i]&amp;gt;=0) { //se B ha il segno &amp;quot;+&amp;quot;&lt;br /&gt;\r\n&amp;nbsp; &amp;nbsp; &amp;nbsp;if (array_c[i]&amp;lt;0) { //se C &amp;egrave; nagativo renderlo positivo&lt;br /&gt;\r\n&amp;nbsp; &amp;nbsp; &amp;nbsp; array_c[i]=array_c[i]+(array_c[i]*2);&lt;br /&gt;\r\n&amp;nbsp; &amp;nbsp; &amp;nbsp;}&lt;br /&gt;\r\n&amp;nbsp; &amp;nbsp; }&lt;br /&gt;\r\n&amp;nbsp; &amp;nbsp; else { //se B ha il segno &amp;quot;-&amp;quot;&lt;br /&gt;\r\n&amp;nbsp; &amp;nbsp; &amp;nbsp;if (array_c[i]&amp;gt;=0) { //se C &amp;egrave; positivo renderlo negativo&lt;br /&gt;\r\n&amp;nbsp; &amp;nbsp; &amp;nbsp; array_c[i]=array_c[i]-(array_c[i]*2);&lt;br /&gt;\r\n&amp;nbsp; &amp;nbsp; &amp;nbsp;}&lt;br /&gt;\r\n&amp;nbsp; &amp;nbsp; }&lt;br /&gt;\r\n&amp;nbsp; &amp;nbsp;}&lt;br /&gt;\r\n&amp;nbsp; }&lt;br /&gt;\r\n&amp;nbsp;}&lt;br /&gt;\r\n}&lt;br /&gt;\r\n//funzione per la sottrazione&lt;br /&gt;\r\nvoid differenza_vettori (int array_a[], int array_b[], float array_c[], int a) {&lt;br /&gt;\r\n&amp;nbsp;//sezione dichiarativa&lt;br /&gt;\r\n&amp;nbsp;int i;&lt;br /&gt;\r\n&amp;nbsp;//sottrarre A e B per ricavarci C&lt;br /&gt;\r\n&amp;nbsp;for (i=0;i&amp;lt;a;i++) {&lt;br /&gt;\r\n&amp;nbsp; array_c[i]=(float)(array_a[i])-(array_b[i]);&lt;br /&gt;\r\n&amp;nbsp;}&lt;br /&gt;\r\n}&lt;br /&gt;\r\n//funzione per la moltiplicazione&lt;br /&gt;\r\nvoid moltiplicazione_vettori (int array_a[], int array_b[], float array_c[], int a) {&lt;br /&gt;\r\n&amp;nbsp;//sezione dichiarativa&lt;br /&gt;\r\n&amp;nbsp;int i;&lt;br /&gt;\r\n&amp;nbsp;//moltiplicare A e B per ricavarci C&lt;br /&gt;\r\n&amp;nbsp;for (i=0;i&amp;lt;a;i++) {&lt;br /&gt;\r\n&amp;nbsp; array_c[i]=(float)array_a[i]*array_b[i];&lt;br /&gt;\r\n&amp;nbsp;}&lt;br /&gt;\r\n}&lt;br /&gt;\r\n//funzione per dividere i