(117, 8, 15, 'Gioco dell\'impiccato', '1629705693_1629488397_img_c.webp', '&lt;p&gt;&lt;strong&gt;DESCRIZIONE&lt;/strong&gt;&lt;/p&gt;\r\n\r\n&lt;p&gt;L&amp;#39;impiccato&amp;nbsp;&amp;egrave; un&amp;nbsp;&lt;a href=&quot;https://it.wikipedia.org/wiki/Gioco_di_carta_e_matita&quot; title=&quot;Gioco di carta e matita&quot;&gt;gioco di carta e matita&lt;/a&gt;&amp;nbsp;per due giocatori. Uno dei giocatori sceglie segretamente una parola; l&amp;#39;altro deve indovinarla.&lt;/p&gt;\r\n\r\n&lt;hr /&gt;\r\n&lt;p&gt;&lt;strong&gt;CODICE SORGENTE&lt;/strong&gt;&lt;/p&gt;\r\n\r\n&lt;p&gt;&lt;code&gt;#include &amp;lt;stdio.h&amp;gt;&lt;br /&gt;\r\n#include &amp;lt;stdlib.h&amp;gt;&lt;br /&gt;\r\n#include &amp;lt;string.h&amp;gt;&lt;br /&gt;\r\n#include &amp;lt;time.h&amp;gt;&lt;br /&gt;\r\n#include &amp;lt;unistd.h&amp;gt;&lt;br /&gt;\r\n#include &amp;lt;conio.h&amp;gt;&lt;br /&gt;\r\n#define PAROLE_NEL_VOCABOLARIO 10&lt;br /&gt;\r\n//Author: Masone Alessandro&lt;br /&gt;\r\nvoid banner_menu_home();&lt;br /&gt;\r\nvoid banner_menu_play();&lt;br /&gt;\r\nvoid errore_0 ();&lt;br /&gt;\r\nvoid errore_1 ();&lt;br /&gt;\r\nvoid errore_2 ();&lt;br /&gt;\r\nvoid errore_3 ();&lt;br /&gt;\r\nvoid errore_4 ();&lt;br /&gt;\r\nvoid errore_5 ();&lt;br /&gt;\r\nvoid errore_6 ();&lt;br /&gt;\r\nvoid errore_7 ();&lt;br /&gt;\r\nvoid banner_time();&lt;br /&gt;\r\nvoid banner_regole();&lt;br /&gt;\r\nvoid banner_fireworks_1 ();&lt;br /&gt;\r\nvoid banner_fireworks_2 ();&lt;br /&gt;\r\nint main(int argc, char *argv[]){&lt;br /&gt;\r\n&amp;nbsp;//sezione dichiarativa&lt;br /&gt;\r\n&amp;nbsp;char dizionario[PAROLE_NEL_VOCABOLARIO][256]={{&amp;quot;bruco&amp;quot;}, {&amp;quot;fotocopia&amp;quot;}, {&amp;quot;virus&amp;quot;}, {&amp;quot;yogurt&amp;quot;}, {&amp;quot;diretta&amp;quot;}, {&amp;quot;bot&amp;quot;}, {&amp;quot;pagliaio&amp;quot;}, {&amp;quot;agosto&amp;quot;}, {&amp;quot;claustrofobia&amp;quot;}, {&amp;quot;gru&amp;quot;}};&lt;br /&gt;\r\n&amp;nbsp;char suggerimento_dizionario[10][256]={{&amp;quot;Lo e&amp;#39; prima di diventare una farfalla&amp;quot;}, {&amp;quot;Una copia attraverso la scannerizzazione dell&amp;#39;immagine&amp;quot;}, {&amp;quot;un&amp;#39;entita&amp;#39; biologica con caratteristiche di parassita obbligato&amp;quot;}, {&amp;quot;E&amp;#39; un alimento frutto della fermentazione del latte operata da batteri&amp;quot;}, {&amp;quot;Una risposta mirata&amp;quot;}, {&amp;quot;Unione di python e telegram&amp;quot;}, {&amp;quot;l&amp;#39;ago finisce sempre al suo interno&amp;quot;}, {&amp;quot;L&amp;#39;ottavo mese dell&amp;#39;anno&amp;quot;}, {&amp;quot;Fobia dei piccoli spazio&amp;quot;}, {&amp;quot;E&amp;#39; sia un animale che uno stumento per edilizia&amp;quot;}};&lt;br /&gt;\r\n&amp;nbsp;char parola_da_indovinare[256];&lt;br /&gt;\r\n&amp;nbsp;char parola_o_lettera_inserita[256];&lt;br /&gt;\r\n&amp;nbsp;char suggerimento[2560];&lt;br /&gt;\r\n&amp;nbsp;char temp[256];&lt;br /&gt;\r\n&amp;nbsp;int i;&lt;br /&gt;\r\n&amp;nbsp;int lettera_presente;&lt;br /&gt;\r\n&amp;nbsp;int errore=0;&lt;br /&gt;\r\n&amp;nbsp;int parola_indovinata;&lt;br /&gt;\r\n&amp;nbsp;char menu;&lt;br /&gt;\r\n&amp;nbsp;int numero_casuale;&lt;br /&gt;\r\n&amp;nbsp;int start=0;&lt;br /&gt;\r\n&amp;nbsp;//primo menu&lt;br /&gt;\r\n&amp;nbsp;do{&lt;br /&gt;\r\n&amp;nbsp; //visualizza primo menu con scelta&lt;br /&gt;\r\n&amp;nbsp; banner_menu_home();&lt;br /&gt;\r\n&amp;nbsp; menu=getch();&lt;br /&gt;\r\n&amp;nbsp; switch(menu){&lt;br /&gt;\r\n&amp;nbsp; &amp;nbsp;case &amp;#39;1&amp;#39;: //scelta avvio gioco&lt;br /&gt;\r\n&amp;nbsp; &amp;nbsp; //scelta del secondo menu&lt;br /&gt;\r\n&amp;nbsp; &amp;nbsp; do{&lt;br /&gt;\r\n&amp;nbsp; &amp;nbsp; &amp;nbsp;//visualizza menu di modalit&amp;agrave; di gioco con scelta&lt;br /&gt;\r\n&amp;nbsp; &amp;nbsp; &amp;nbsp;start=0;&lt;br /&gt;\r\n&amp;nbsp; &amp;nbsp; &amp;nbsp;banner_menu_play();&lt;br /&gt;\r\n&amp;nbsp; &amp;nbsp; &amp;nbsp;menu=getch();&lt;br /&gt;\r\n&amp;nbsp; &amp;nbsp; &amp;nbsp;switch(menu){&lt;br /&gt;\r\n&amp;nbsp; &amp;nbsp; &amp;nbsp; case &amp;#39;1&amp;#39;: //gioca con un amico&lt;br /&gt;\r\n&amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp;//sezione di input della parola da indovinare e del suggerimento&lt;br /&gt;\r\n&amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp;do{&lt;br /&gt;\r\n&amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; system(&amp;quot;cls&amp;quot;);&lt;br /&gt;\r\n&amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; printf(&amp;quot;Inserire la parola da indovinare\\n[!]Minimo 3 lettere\\n&amp;gt;&amp;quot;);&lt;br /&gt;\r\n&amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; fflush(stdin);&lt;br /&gt;\r\n&amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; gets(parola_da_indovinare);&lt;br /&gt;\r\n&amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp;}while(strlen(parola_da_indovinare)&amp;lt;3);&lt;br /&gt;\r\n&amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp;printf(&amp;quot;Inserire il suggerimento per indovinare la parola &amp;quot;);&lt;br /&gt;\r\n&amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp;puts(parola_da_indovinare);&lt;br /&gt;\r\n&amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp;printf(&amp;quot;&amp;gt;&amp;quot;);&lt;br /&gt;\r\n&amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp;fflush(stdin);&lt;br /&gt;\r\n&amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp;gets(suggerimento);&lt;br /&gt;\r\n&amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp;start=1;&lt;br /&gt;\r\n&amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp;menu=&amp;#39;1&amp;#39;;&lt;br /&gt;\r\n&amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp;break;&lt;br /&gt;\r\n&amp;nbsp; &amp;nbsp; &amp;nbsp; case &amp;#39;2&amp;#39;: //gioca da solo&lt;br /&gt;\r\n&amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp;//assegnanzioni parole casuali da un vacabolario&lt;br /&gt;\r\n&amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp;numero_casuale=rand()%PAROLE_NEL_VOCABOLARIO;&lt;br /&gt;\r\n&amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp;strcpy(parola_da_indovinare, dizionario[numero_casuale]);&lt;br /&gt;\r\n&amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp;strcpy(suggerimento, suggerimento_dizio